css:
@-moz-document domain("forum.treeofsavior.com") {
body, .wrapper, .content, .topic-map, .category-dropdown-menu,.menu-panel, .header-body,.sweet-alert, .loginContent, .notifications li, .user-content, .item, #share-link, .select2-drop, .d-editor-preview {
    background: #002a3a !important;
} 

html {
	color: #fefefe !important
}

.header-wrapper{
	background:transparent;
}

#reply-control{
	background-color: #1a1b25 !important;
}

.badge {
    color: #efe !important;
    background:none !important
}

.user-badge {
	background: none !important;
    border: 0 !important;
    color: #fefefe !important
}

.user-main .about .details, #user-card .card-content {
	background-color: rgba(33,35,49,0.9) !important;
}

a.title:visited, a.search-link:visited .topic-title {
	color: #bbb !important;
}

textarea, .wmd-preview {
	background: #1a1b25 !important;
    box-shadow: 0px 0px 1px 0px  #626784 !important;
    color: #fefefe !important;
}
#reply-control .contents .wmd-preview {
	border:none;
}
.grippie{
	background:none !important;
    border-top: 1px solid #626784 !important;
}

.header-wrapper {
	top: 0px !important;
    position: absolute !important;
}

.docked .d-header{
    position: absolute !important;
    background: #1a1b25;
}

.d-header{
	margin-top: 0px !important;
}

input, select, .ac-wrap, .select2-container, a.select2-choice {
	background-color: #fefefe !important;
  	color: #333 !important;
    border-color: #626784 !important;
}

blockquote, aside.quote .title, aside.onebox {
	background-color: #212331 !important;
    color: #634a54 !important;
    border-left: 5px solid #272939 !important;
}

.homeNewsContent, .boardViewInfo, .guideSubTitle, .inquiryFAQTitlem, .viewing-self .user-main .about.collapsed-info .secondary, .user-main .about .secondary {
	background-color: #212331 !important;
}
.homeStatus, .homeNews1, .homeNews1, .homeNews2, .homeNews3, .nav-stacked .active, .item.notification, .composer-popup {
	background-color: #272939 !important;
	border-color: #626784 !important;
}

a, button, h1, h2, h3, dt, label, .error-page, .draft-tex, .homeNewsTitle, .boardItem, .boardViewTitle, .loggedNick, .guideTitle, .font18, #reply-control, div.poll .poll-info .btn {
	color: #fefefe !important;
}

p, td, li, b, dd, .homeNewsContent div, .boardEtc, .font13, .font17, .guideContents, .inquiryNoticeL, .inquiryNoticeR, .cooked, .category-desc, .blurb, .topic-error{
	color: #fefefe !important;
}

.btn-primary, .btn, #btnBetaKey, #logoutButton, .sweet-alert button, .loginButton, .boardViewButtonfl1, .boardViewButtonfr1, .boardViewButtonfr2, .pageList, .pageCurrent, .inquiryTopItem , .inquiryButtonL, .inquiryButtonR, .nav-stacked {
    box-shadow: 0px 0px 1px 0px  #626784 !important;
    background: none !important;
    color: #fefefe !important;
    border-color: #ccc !important;
}

.topic-map .buttons .btn {
	border:none !important;
   	padding: 1px 5px;
	margin-top: 1px !important;
}

button:hover, .inquiryTopItem:hover, .inquiryButtonOverL, .inquiryButtonOverR, .boardViewButtonOverfr2, .boardViewButtonOverfr1, .select2-highlighted, .btn:hover {
    color: #333 !important;
	background-color:#ccc !important;
}

.select2-results-dept-0.discourse-tag a {
	color: #fff
}

#reply-control .d-editor-button-bar {
	background:none
}

.discourse-tag-select2 a {
	color: #333 !important;
}

.select2-highlighted {
	background: none !important
}

.topic-body, .topic-avatar {
	border-top: 1px inset #626784 !important;
}

section, hr, .topic-map, tr, #topic-closing-info, .small-action, #reply-control.draft, .menu-panel, .sa-icon.sa-info, .boardItem, .topline, .bottomline, .boardView, .guideTitle, .guideTd, .inquiryNoticeTop, .inquiryNotice, .inquiryBorder, .nav-stacked li, .item {
	border-color: #626784 !important;
}

.headerWhiteBG {
	display:none;
}

.mention {
    background-color: #1f212d !important;
    color: #626784  !important;
}

.topic-body.highlighted,.topic-list tr.highlighted {
    background-color: #1f212d !important;
}

#topic-progress {
	background-color: #1a1b25;
    border-color: #626784;
    color: #626784;
}

#topic-progress .bg, .badge-category {
	background-color: #262636 !important;
	border-color: #626784 !important;
}

.alert.alert-info, #user-card {
    background-color: #212331 !important;
  	color: #fefefe !important;
}

.card-content {
	background: none !important
}

.active a, .navigatorItemSelected {
	box-shadow: 0px 0px 5px 1px rgba(110, 83, 94, 0.5) !important;
    background:none !important;
}

.nav-pills a:hover, th.category:hover, th.posts:hover, th.views:hover, th.activity:hover, .menu-panel a:hover, .icon:hover {
	background-color: rgba(110, 83, 94, 0.2) !important;
	box-shadow: 0px 0px 5px 0px #634a54 !important;
   	border-radius: 2px !important;
}

span.badge-category.clear-badge, .stats-section li, .badge-card, .badge-card, .badge-contents, .badge-icon {
	background: #212331 !important;
    color: #efe !important;
}

.badge-category-parent-bg, a.badge-category {
    background:none !important;
}

.topic-body, .topic-avatar {
    border-color: #bbb !important
}

.header-dropdown-toggle a {
    border: none !important
}

.header-dropdown-toggle a:hover, .header-dropdown-toggle.active a {
    background-color:#ccc !important;
    color:#333 !important;
	border: none !important;
}

.category-list th, .topic-list th, .list-controls .category-breadcrumb a.badge-category {
    color: #fefefe !important
}

.list-controls .category-dropdown-menu {
	-webkit-box-shadow: none;
    box-shadow: none;
    border: 2px solid;
}

.d-editor-preview {
	border: none !important
}

.d-editor-preview-wrapper {
	padding: 0 !important;
    border-left: 1px solid
}

th.category:hover {
	border: none !important;
    background: none !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

footer {
	background-color:#002a3a !important
}

#sns a:hover > .fa {
	color: #fff
}
}
