css:
@-moz-document url-prefix("http://www.animeultima.io/") {
#right-content-hp {
display:none;
}

p.tagline {
display:none;
}

#top-menu ul {
margin:0;
}

.generic-container {
width:824px;
}

form#search {
margin:0;
margin-top:5px;
}

.centered iframe, .comment-user {
display: none;
}

form#search .button-search {
margin:0 0 0 10px;
}

input[type="text"] {
border:1px solid #EEE;
height:22px;
padding-left:5px;
}

input[type="text"]:focus {
outline-color:#E75219;
}

ol#searchresult {
margin:0;
padding:0;
}

.error {
margin-bottom:0;
}

#header-container {
width:995px;
padding:3px 0;
height:61px;
}

ul#hot-shows li.li-label {
display:none;
}

ul#hot-shows {
list-style-type:none;
font-size:11px;
margin:0;
padding:5px 5px 5px 0;
background:none;
}

div.hot-picks {
margin:0;
width:100%;
position:absolute;
top:43px;
left:0;
text-align:center;
}

#header-left img {
border:0;
margin-top:8px;
}

#watch-list {
display:none;
}

h1 {
margin:0;
text-align:center;
padding:6px 0;
}

div#pembed {
margin:-2px auto;
width:854px;
height:510px;
text-align:center;
position:inherit;
background:#02354F;
}

#embed_holder {
width:854px;
margin-left:-15px;
background:#02354F;
overflow:hidden;
height:480px;
}

.centered.fornoobs {
display:none;
}

#main-content-hp .centered {
display:none;
}

#embed_holder_loading {
visibility:hidden;
}

iframe {
width:854px;
height:484px;
}

div.uploader-info {
margin: 15px 3px 0px 15px;
width: 292px;
float: left;
height: 82px;
position: relative;
}

div#related-videos {
float:none;
width:auto;
margin-top:10px;
margin-right:-3px;
margin-left: 12px;
}

.report-video {
display:none;
}

.generic-video-item div.thumb img.bg-image {
position: absolute;
top: 0px;
left: 0px;
width: 152px;
height: 102px;
}

li {
list-style-type:none;
padding:2px 0;
}

ul {
margin: 0;
padding:0;
}

#main-content {
width:824px;
}

.generic-padd {
padding:9px 15px 15px;
}

iframe#fb-like {
display:none;
}

.video-meta-1 {
margin:0 -15px;
}

.nextepisode {
padding:7px;
}

.section {
margin:5px 0;
float:left;
width:100%;
}

.generic-video-item div.thumb span.border {
display:none;
}

.generic-video-item span.video-subbed,.generic-video-item span.video-dubbed {
bottom: 0px;
right: 0px;
width: 148px;
}

.generic-video-item div.thumb span.now-playing {
top: 0px;
left: 0px;
width: 152px;
height: 102px;
}

.generic-video-item div.thumb {
margin:0 auto;
}

.generic-video-item img.trusted {
bottom: 1px;
left: 1px;
height: 13px;
}

.theintro-dubbed {
display:none;
}

.generic-video-item div.title {
width:auto;
overflow:hidden;
text-overflow:ellipsis;
white-space:pre-wrap;
}

.nr-top,.inline-top {
border-bottom:2px solid #86BA2C;
padding:7px 10px;
margin:0 -15px;
}

.generic-video-item div.thumb span.play {
left:0;
width:100%;
height:100%;
background:url(http://i.imgur.com/ZngGzkg.png) no-repeat center 25px;
}

#comment-container {
float:left;
width:100%;
}

#new-anime-div {
float:right;
width:384px;
}

#hp-ads {
float:left;
width:384px;
}

#popular-genres {
float:left;
padding:0;
}

#popular-genres li {
padding:2px 0;
float:left;
width:128px;
}

h3 {
border-bottom:2px solid #86BA2C;
padding:7px 10px;
margin:0 -15px 5px;
}

.comment-user br {
display:none;
}

div.comment-user {
float:left;
width:56px;
height:auto;
margin:0 10px 10px 0;
padding:0;
background-color:#FFF;
}

div.comment-content {
float:left;
width:738px;
}

#mini-announcement {
width:844px;
padding:5px;
color:#32798D;
-moz-border-radius:8px;
-webkit-border-radius:8px;
}

#footer dl {
float:left;
width:271px;
margin:0;
}

div#footer {
background:#FFF;
width:834px;
padding:10px 10px 5px;
}

p {
margin:5px 0;
}

div#footer-copy {
text-align:center;
margin:0;
padding:5px;
}

img.video-comments-avatar {
width:56px;
height:auto;
border:none;
padding:0;
background:none;
margin-bottom:-3px;
}

ul#left-nav li:hover ul {
z-index:1;
}

.anime-desc p {
width: 48%;
float: left;
padding: 0px 7px;
}

.anime-desc p:nth-child(3) {
display: none;
}

#anime-table-info {
width: 49%;
float: right;
}

.nr-content .section p:last-child, #latest-episode-ongoing {
display: none;
}

img[src*="http://www.animeultima.tv/uploads/anime_photo/"], img[src^="http://www.mp4upload.com/api/thumbnail.php?id="], img[src*="videos.myspacecdn.com"] {
display: none;
}

img[src="//static.cdn.animeultima.tv/images/logo.png"] {
display:block !important;
}

.comment-content p {
margin:0 0 10px;
float:none;
}

h1.tagline {
display:none;
}

#main-content-hp {
width:100%;
}

h2 {
padding:7px;
margin:0;
}

.comment-icons {
float:right;
height:17px;
margin:0;
}

div.comment-item {
padding:10px 10px 0;
border-top:none;
color:#444;
width:804px;
float:left;
background-color:#E1F2FA;
margin:0 0 10px;
}
}
