css:
@-moz-document domain("www.digi24.ro") {
body,
html,
.site-header,
.container-fluid,
.bg-faded,
.bg-primary,
.site-footer,
.bg-inverse,
.dropdown-menu,
.box-header,
.box-title,
.pagination,
.list-exchange,
.box-weather-day,
.box-block,
.card-block,
.bg-black {
    background-color: hsl(0, 0%, 30%)!important;
    color: hsl(0, 0%, 80%)!important;
    border: none !important;
}


.page-item,
.page-link,
.btn {
    background-color: hsl(0, 0%, 50%)!important;
}

.page-link {
 	border: none;
    color: hsl(0, 0%, 80%)!important;
}

.page-item.active .page-link,
.page-item.active,
.page-link:hover {
 	color: hsl(0, 0%, 60%)!important;
}

.box-header {
 	border-top: 2px solid hsl(0, 0%, 50%)!important;  
}

.box-title a,
.h6,
.entry-meta a,
.site-header .navbar-nav .nav-item.active .nav-link,
.card-title,
.card-articles .card-title a {
 	color: hsl(0, 0%, 90%)!important;  
}

.box-title a:hover,
.entry-meta a:hover,
.card-title:hover,
.card-articles .card-title a:hover {
 	color: hsl(0, 0%, 70%)!important;  
}

.site-header .dropdown-full.open>.dropdown-toggle {
 	color: hsl(0, 0%, 70%)!important;     
}

.btn-primary {
    background-color: hsl(0, 0%, 50%)!important;
    border: none;
}

.btn-primary:hover {
    background-color: hsl(0, 0%, 20%)!important;
}

}
