css:
@-moz-document domain("www.youtube.com") {
/* progress bar */
.html5-play-progress, .ytp-play-progress {
   background: #ff00dd !important; /* Old browsers */
   background: -moz-linear-gradient(left, #ff00dd 0%, #ff00dd 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#ff00dd), color-stop(100%,#ff00dd)) !important;
   background: -webkit-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
   background: -o-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
   background: -ms-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
   background: linear-gradient(to right, #0099ff 0%,#000000 50%,#ff00dd 100%) !important;
   border-color: #000000 !important;
   box-shadow: 0px 0px 5px #FFFFFF, 0px 0px 6px #FFFFFF, 0px 0px 7px #FFFFFF !important;
}
/* Video load bar */
.html5-load-progress, .ytp-load-progress {
	background: #000000;
	background: -moz-linear-gradient(left, #000000  100%, #000000  100%, #000000 100%, #000000  100%) !important;
	background: -webkit-linear-gradient(left, #000000  100%,#000000 100%,#000000 100%,#000000  100%) !important;
	background: linear-gradient(to right, #000000 100%,#000000 100%,#000000 100%,#000000 100%) !important;
	border-color: #000000 !important;
	box-shadow: 0px 0px 5px #FFFFFF, 0px 0px 6px #FFFFFF, 0px 0px 7px #FFFFFF !important;
}
/* Scrubber button */
.html5-scrubber-button, .ytp-scrubber-button {
    background: #0099ff !important;
	background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #0099ff 100%) !important;
	background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#0099ff 100%) !important;
	background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#0099ff 100%) !important;
	border-color: #000000 !important;
	box-shadow: 0px 0px 5px #0099ff, 0px 0px 6px #0099ff, 0px 0px 7px #0099ff !important; 
}
/* Scrubber button â€” hover */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover {
   
    background: #ff00dd !important;
	background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #ff00dd 100%) !important;
	background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#ff00dd 100%) !important;
	background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#ff00dd 100%) !important;
	border-color: #000000 !important;
	box-shadow: 0px 0px 5px #ff00dd, 0px 0px 6px #ff00dd, 0px 0px 7px #ff00dd !important; 
}  
/* Volume control bar */
.ytp-volume-slider-handle {
	position:absolute;
	top:50%;
	width:3px;
	height:14px;
	margin-top:-7px;
	margin-left:0px;
	background: #ff00dd !important; /* Old browsers */
   background: -moz-linear-gradient(left, #ff00dd 0%, #ff00dd 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#ff00dd), color-stop(100%,#ff00dd)) !important;
   background: -webkit-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
   background: -o-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
   background: -ms-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
   background: linear-gradient(to right, #0099ff 0%,#000000 50%,#ff00dd 100%) !important;
   border-color: #000000 !important;
   box-shadow: 0px 0px 5px #FFFFFF, 0px 0px 6px #FFFFFF, 0px 0px 7px #FFFFFF !important;
}
/* Volume control bar - hover*/
.html5-volume-slider-handle:hover, .ytp-volume-slider-handle:hover {
	position:absolute;
	top:50%;
	width:3px;
	height:14px;
	margin-top:-7px;
	margin-left:0px;
    background: #0099ff !important; /* Old browsers */
   background: -moz-linear-gradient(left, #0099ff 0%, #0099ff 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#0099ff), color-stop(100%,#0099ff)) !important;
   background: -webkit-linear-gradient(left, #ff00dd 0%,#0099ff 100%) !important;
   background: -o-linear-gradient(left, #ff00dd 0%,#0099ff 100%) !important;
   background: -ms-linear-gradient(left, #ff00dd 0%,#0099ff 100%) !important;
   background: linear-gradient(to right, #ff00dd 0%,#000000 50%,#0099ff 100%) !important;
   border-color: #000000 !important;
   box-shadow: 0px 0px 5px #FFFFFF, 0px 0px 6px #FFFFFF, 0px 0px 7px #FFFFFF !important;
}
.ytp-big-mode .ytp-volume-slider-handle {
	width:4px;
	height:22px;
	margin-top:-11px
}
.ytp-volume-slider-handle:before,.ytp-volume-slider-handle:after {
	background: #ff00dd !important; /* Old browsers */
    background: -moz-linear-gradient(left, #ff00dd 0%, #ff00dd 100%) !important;
    background: -webkit-gradient(linear, left top, right top, color-stop(0%,#ff00dd), color-stop(100%,#ff00dd)) !important;
    background: -webkit-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
    background: -o-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
    background: -ms-linear-gradient(left, #0099ff 0%,#ff00dd 100%) !important;
    background: linear-gradient(to right, #0099ff 0%,#000000 50%,#ff00dd 100%) !important;
    border-color: #000000 !important;
    box-shadow: 0px 0px 5px #FFFFFF, 0px 0px 6px #FFFFFF, 0px 0px 7px #FFFFFF !important;
	width:50px;
	margin-left:-50px;
}
/*Button Colors*/
.ytp-playlist-menu-button-icon .ytp-svg-fill,.ytp-chrome-top .ytp-button:not(.ytp-playlist-menu-button) .ytp-svg-fill,.ytp-chrome-controls .ytp-button .ytp-svg-fill,.ytp-settings-button-icon{
	fill:#ffffff !important;
}
/* Time*/
.ytp-time-display, .ytp-time-duration{
color: #FFFFFF !important;
}
.ytp-volume-slider-handle:after {
	left:0px;
	background:#00ffaa
}
.ytp-big-mode .ytp-volume-slider-handle:before,.ytp-volume-slider-handle:after {
	width:50px;
	height:2.5px;
}
.ytp-big-mode .ytp-volume-slider-handle:after {
	left:-46px;
	background:#00ffaa
}
/* Badge HD-4k-5k-8k-3D */
.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after {
   width:3px;
   height:40px;
 	 top:8px;
 	 right:17px;
   background: #00ffaa !important;
   background: -moz-linear-gradient(left, #00ffaa 0%, #00ffaa 35%, #000000 65%, #00ffaa 100%) !important;
   background: -webkit-linear-gradient(left, #00ffaa 0%,#000000 35%,#000000 65%,#00ffaa 100%) !important;
   background: linear-gradient(to right, #00ffaa 0%,#000000 24%,#000000 65%,#00ffaa 100%) !important;
   box-shadow: 0px 0px 10px #00ffaa, 0px 0px 12px #00ffaa, 0px 0px 14px #00ffaa !important;
   content:''
}
.ytp-big-mode .ytp-settings-button.ytp-hd-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-4k-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-5k-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-8k-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-3d-badge-grey:after,.ytp-big-mode .ytp-settings-button.ytp-3d-badge:after {
   width:3px;
   height: calc(40px*1.4) ;
 	 top:10px;
 	 right:23px;
   content:'';
   background-image:none;
}
.ytp-settings-button.ytp-3d-badge::after {
   width:4px;
   height:40px;
 	 top:8px;
 	 right:16px;
   background: #00ffaa !important;
   background: -moz-linear-gradient(left, #00ffaa 0%, #00ffaa 35%, #000000 65%, #00ffaa 100%) !important;
   background: -webkit-linear-gradient(left, #00ffaa 0%,#000000 35%,#000000 65%,#00ffaa 100%) !important;
   background: linear-gradient(to right, #00ffaa 0%,#000000 24%,#000000 65%,#00ffaa 100%) !important;
   box-shadow: 0px 0px 10px #00ffaa, 0px 0px 12px #00ffaa, 0px 0px 14px #00ffaa !important;
   content:''
}
.ytp-big-mode .ytp-settings-button.ytp-3d-badge::after {
   width:3px;
   height: calc(40px*1.4) ;
 	 top:10px;
 	 right:23px;
   content:''
}
/* HD letters in menu */
.ytp-swatch-color {
   color: #ffffff !important;
}  
/* Checked item */
.ytp-menuitem[aria-checked="true"] 
.ytp-menuitem-toggle-checkbox {
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #ff00dd 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   border-color: #000000 !important;
   box-shadow: 0px 0px 10px #ff00dd, 0px 0px 12px #ff00dd, 0px 0px 14px #ff00dd !important;
   content:''
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
	-moz-transform:translateX(-50px);
	-ms-transform:translateX(-50px);
	-webkit-transform:translateX(-50px);
	transform:translateX(-50px)
}
.ytp-big-mode .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
	-moz-transform:translateX(-50px);
	-ms-transform:translateX(-50px);
	-webkit-transform:translateX(-50px);
	transform:translateX(-50px)
}
/* Unchecked item */
.ytp-menuitem[aria-checked="false"] .ytp-menuitem-toggle-checkbox {
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #0099ff 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#0099ff 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#0099ff 100%) !important;
   border-color: #000000 !important;
   box-shadow: 0px 0px 10px #0099ff, 0px 0px 12px #0099ff, 0px 0px 14px #0099ff !important;
   content:''
}
/* 'Watch on YouTube' logo for other websites */
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
   fill: #000000 !important;
}
/* Menu Buttons */
.ytp-button:not(.ytp-playlist-menu-button) svg {
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #0099ff 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#0099ff 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#0099ff 100%) !important;
   box-shadow: 0px 0px 5px #0099ff, 0px 0px 6px #0099ff, 0px 0px 7px #0099ff !important;
   border-color: #000000 !important;
   content:'';
   background-image:none;
}
.ytp-button:not(.ytp-playlist-menu-button):hover svg {
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #ff00dd 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   box-shadow: 0px 0px 5px #ff00dd, 0px 0px 6px #ff00dd, 0px 0px 7px #ff00dd !important;
   border-color: #000000 !important;
   content:'';
   background-image:none;
}
.ytp-settings-button svg {
	-moz-transition:-moz-transform .1s cubic-bezier(.58,.24,.47,2.3);
	-webkit-transition:-webkit-transform .1s cubic-bezier(.58,.24,.47,1);
	-ms-transition:-ms-transform .1s cubic-bezier(.58,.24,.47,2.3);
   transition:transform .1s cubic-bezier(.58,.24,.47,2.3)
}
.ytp-settings-button[aria-expanded=true] svg {
	-moz-transform:rotateY(720deg) translateY(0px);
	-ms-transform:rotateY(720deg) translateY(0px);
	-webkit-transform:rotateY(720deg) translateY(0px);
	transform:rotateY(720deg) translateY(0px);
}
.ytp-big-mode .ytp-settings-button[aria-expanded=true] svg {
	-moz-transform:rotateY(720deg) translateY(0px);
	-ms-transform:rotateY(720deg) translateY(0px);
	-webkit-transform:rotateY(720deg) translateY(0px);
	transform:rotateY(720deg) translateY(0px);
}
/* Inside Menu Buttons */
.ytp-menuitem[aria-checked="true"], .ytp-menuitem-toggle-checkbox-button {
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #0099ff 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#0099ff 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#0099ff 100%) !important;
   box-shadow: 0px 0px 5px #0099ff, 0px 0px 6px #0099ff, 0px 0px 7px #0099ff !important;
   border-color: #000000 !important; 
}   
.ytp-menuitem[aria-checked="false"], .ytp-menuitem-toggle-checkbox-button{
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #ff00dd 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   box-shadow: 0px 0px 5px #ff00dd, 0px 0px 6px #ff00dd, 0px 0px 7px #ff00dd !important;
   border-color: #000000 !important;
}   
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
	-moz-transform:translateX(-50px);
	-ms-transform:translateX(-50px);
	-webkit-transform:translateX(-50px);
	transform:translateX(-50px)
}
.ytp-big-mode .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
	-moz-transform:translateX(-50px);
	-ms-transform:translateX(-50px);
	-webkit-transform:translateX(-50px);
	transform:translateX(-50px)
}
.ytp-menuitem, .ytp-menuitem-speed-button {
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #0099ff 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#0099ff 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#0099ff 100%) !important;
   box-shadow: 0px 0px 5px #0099ff, 0px 0px 6px #0099ff, 0px 0px 7px #0099ff !important;
   border-color: #000000 !important; 
}
.ytp-menuitem:hover, .ytp-menuitem-speed-button:hover {
   background: #000000 !important;
   background: -moz-radial-gradient(center, ellipse cover, #000000 0%, #000000 20%, #ff00dd 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   background: radial-gradient(ellipse at center, #000000 0%,#000000 20%,#ff00dd 100%) !important;
   box-shadow: 0px 0px 5px #ff00dd, 0px 0px 6px #ff00dd, 0px 0px 7px #ff00dd !important;
   border-color: #000000 !important;
}
}
