css:
@-moz-document regexp("chrome-extension://.*") {
html,
body,
body>div, #confirm > div, .menu-items-wrapper {
    background: #1A1A1A !important;
}
body:not(.CodeMirror-cursors):not(.CodeMirror-cursor),
button,
select {
    border-color: #252525 !important;
}
body:not([background]):not(div):not(.works-info),
button,
select,
.CodeMirror,
.CodeMirror-gutters {
    background-color: #1A1118 !important;
}
body,
button,
select {
    text-shadow: none !important;
    color: #FFFFEE !important;
}
body a,
body a:link,
body a *:not(img),
body a:link *:not(img) {
    background-color: rgba(60, 60, 60, 0) !important;
    color: #30C5FF !important;
}
body a:hover,
body a:active,
body a:hover *:not(img),
body a:active *:not(img) {
    background-color: rgba(60, 60, 60, 0) !important;
    color: #40e0ff !important;
}
body a:visited,
body a:visited *:not(img) {
    background-color: rgba(60, 60, 60, 0) !important;
    color: #9182ff !important;
}
body input,
body textarea {
    color: #f0f0f0 !important;
    background: #222 !important;
    border-color: #333 !important;
}
*::selection {
    background: #B0B0B0;
}
.close-icon {
    border-top-color: #333 !important;
    border-left-color: #333 !important;
    border-bottom-color: black !important;
    border-right-color: black !important;
    border-width: 3px !important;
    height: 12px !important;
    width: 12px !important;
    background: linear-gradient(-45deg, transparent 8px, lightgray 8px, lightgray 8.5px, transparent 9.5px) no-repeat, linear-gradient(45deg, transparent 8px, lightgray 8px, lightgray 8.5px, transparent 9.5px) no-repeat !important;
}
body#stylish-popup div#sitename {
    color: #CDE !important;
}
div.tabs-container header .tab-header {
    background-color: #32262E !important;
}
div.tabs-container header .tab-header.active,
div.tabs-container div#disable-all-container,
div.tabs-container div#installed {
    background-color: #1D141B !important;
}
div.tabs-container div#disable-all-container span.disable-all-lable {
    color: #FFFFEE !important;
}
div.tabs-container div#installed div.entry {
    background-color: #2A1E20 !important;
}
div.tabs-container div#installed div.entry {
    color: #DDE0D0 !important;
}
div.style-name, label.style-name {
    color: #CDE !important;
}
.CodeMirror,
.CodeMirror-lint-tooltip {
    text-shadow: none !important;
    color: #D5D5C6 !important;
}
.CodeMirror-activeline-background {
    background-color: #332C30 !important;
}
.CodeMirror-selected {
    background: #3E343B !important;
}
.CodeMirror-cursors {
    color: #FFAABB !important;
}
.CodeMirror-cursor {
    border-left-color: #FFAABB !important;
}
.cm-atom {
    color: #30C5FF !important;
}
.cm-keyword {
    color: #AF5AD0 !important;
}
.cm-qualifier {
    color: #FFFFF0 !important;
}
.cm-tag {
    color: #77BB44 !important;
}
.cm-number {
    color: #44AA20 !important;
}
.cm-variable-3 {
    color: #119966 !important;
}
.cm-builtin,
.cm-def {
    color: #7788EE !important;
}
.cm-string {
    color: #C33 !important;
}
}
