css:
@-moz-document url("about:newtab") {

  body{
    background: #feefff;
  }
  
  #newtab-search-text {
  /*Text Bar Background*/
    background-color: rgba(255, 255, 255, 0.76);

  /*Text Color*/
    font-weight: lighter;
    width: 100%;
    height: 24px;  
     border: 1px transparent;
    box-shadow: none !important;  
    font-size: 1.2em;  
    color: rgb(255, 153, 179, 1) !important;
    background: rgba(255, 224, 246, 1) !important;
  }
  
  #newtab-search-submit {
    /*Search button*/
    background: url("http://i387.photobucket.com/albums/oo314/paravidatoda/mini-172.gif") center center no-repeat, rgba(255, 224, 246, 1);
    padding: 0;border: 0px solid;
    border-color: transparent;
  } 
  
#newtab-search-icon {
  border: 1px transparent;
  padding: 0;
  margin: 0;
  width: 36px;
  height: 36px;
  background: url("https://68.media.tumblr.com/tumblr_lmt0fw8JPN1qfel73.gif") center center no-repeat;
  position: absolute;
  }
  
#newtab-search-text + #newtab-search-submit:hover {
    background-image: url("http://i387.photobucket.com/albums/oo314/paravidatoda/mini-172.gif"), linear-gradient(#ffd1f2, #ffa8e6);
  }
  
.newtab-cell:empty {
    outline: 2px dashed #FF99B3;
    outline-offset: -2px;
    -moz-outline-radius: var(--cell-corner-radius);  }
  
  .newtab-title {
    background-color: #FFE0F6;
    font-size: 16px;
    line-height: 30px;
    border: #FF99B3;
  }
  
  .newtab-title, .newtab-suggested {
    color: #FF99B3;
  }
  
  body:not(.compact) .newtab-site:hover .newtab-title {
    color: #ff7c9e;
    background-color: #ffc9f0;
  }
}
