css:
@-moz-document domain("trello.com")
{
/*----------------------------------------------------------------------
Digital Dark Style & Multiple Rows by André Weber 'Web3r' <info@seo-os.de>.
inspired by 
https://userstyles.org/styles/118288/trello-multiple-rows-responsive
and
https://userstyles.org/styles/126027/trello-digital-dark
----------------------------------------------------------------------*/


/* -- Layer 0 -- */

Layer-0,

/* Background */

body,
div.tabbed-pane-header,
div.tabbed-pane-nav {
    background: hsl(0, 0%, 5%) !important;
    color: hsl(0, 0%, 50%) !important;
    border-color: hsl(0, 0%, 0%) !important;
}

/* -- Layer 1 -- */

Layer-1,

/* Header */

div#header,

/* List */

div.list,

/* List > Card Header */

div.list div.list-header,
a.open-card-composer
/* Button: "Add a card" */

,
a.board-tile.mod-add
/* "Create new board." */

,
a.boards-page-board-section-header-options-item
/* Board buttons */

,
a.boards-page-board-section-header-options-item:hover span,
div.boards-page-board-section a.quiet-button
/* Create a new team... */

,
div.tabbed-pane-nav li.tabbed-pane-nav-item a.tabbed-pane-nav-item-button,

/* Profile - Tab Area */

div.tabbed-pane-main-col,
.tabbed-pane-header a.button-link,
form.js-profile-form input,
form.js-profile-form textarea,

/* Board Menu */

div.board-menu,

/* Board Menu > Board Navigation Buttons */

div.board-menu a.board-menu-navigation-item-link,

/* Board Menu > Power-Ups > Counter */

div.board-menu a.board-menu-navigation-item-link span.js-enabled-power-up-count,

/* Board Menu > Activity */

div.board-menu a.js-open-activity,

/* Board Page > Tile */

.board-tile {
    background: hsl(0, 0%, 10%) !important;
    color: hsl(0, 0%, 60%) !important;
    border-color: hsl(0, 0%, 5%) !important;
}

/* -- Layer 2 -- */

Layer-2,
a.header-btn
/* Header Button */

,
div.list div.list-card
/* List Card */

,
a.list-header-menu-icon
/* Button: "..." */

,
a.open-card-composer:hover
/* Button: "Add a card"  */

,
div.window-overlay div.window
/* Window Overlay */

,
input.header-search-input
/* Header Search */

,
.boards-drawer
/* Boards */

,
a.board-tile.mod-add:hover
/* "Create new board." */

,
a.boards-page-board-section-header-options-item:hover
/* Board buttons */

,
a.boards-page-board-section-header-options-item:hover span,
div.boards-page-board-section a.quiet-button:hover
/* Create a new team... */

,
div.tabbed-pane-nav li.tabbed-pane-nav-item a.tabbed-pane-nav-item-button:hover,
a.board-tile.mod-add,
.window-module .js-search,
.setting-item .helper,
div.tabbed-pane-main-col div.action-comment,
div.tabbed-pane-main-col p.gutter,
div.tabbed-pane-main-col div.member-login,
div.tabbed-pane-main-col input.js-add-email,
div.tabbed-pane-main-col input.js-log-out-other,
div.tabbed-pane-main-col input.js-revoke,
div.tabbed-pane-main-col a.big-link,

/* Profile - Cards - Card */

.tabbed-pane-main-col .js-cards-content .list-card,

/* Organization - Members - Find Members */

.tabbed-pane-main-col .org-members-page-layout .org-members-page-layout-sidebar input,

/* Organization - Members - Add Members Button */

.tabbed-pane-main-col .org-members-page-layout .button-link,

/* Organization - Settings - Business Class */

.tabbed-pane-main-col .org-bc-settings,

/* Organization - Business Class - Input */

.tabbed-pane-main-col .credit-card-form input,
.tabbed-pane-main-col .credit-card-form .iframe-input,
form.js-profile-form input:hover,
form.js-profile-form textarea:hover,

/* Board Menu > Member */

div.board-menu div.member,

/* Board Menu > Add Member Button */

div.board-menu a.button-link,

/* Board Menu > Board Navigation Buttons */

div.board-menu a.board-menu-navigation-item-link:hover,

/* Board Menu > Power-Ups > Counter */

div.board-menu a.board-menu-navigation-item-link:hover span.js-enabled-power-up-count,

/* Board Menu > Activity */

div.board-menu a.js-open-activity:hover,

/* Board Menu > Activity Entry */

div.board-menu div.js-menu-action-list div.phenom,

/* Window - Title */

.window .window-title textarea,

/* Window - Description */

.window .description-content,

/* Board Page > Tile */

.board-tile:hover {
    background: hsl(0, 0%, 15%) !important;
    color: hsl(0, 0%, 70%) !important;
    border-color: hsl(0, 0%, 10%) !important;
}

/* -- Layer 3 -- */

Layer-3,
a.header-btn:hover
/* Header Button */

,
input.header-search-input:hover
/* Header Search */

,
.list .list-card:hover
/* List Card */

,

/* List Card Details (Sticker) */

.list-card.is-stickered .list-card-stickers-area,
a.list-header-menu-icon:hover
/* Button: "..." */

,
span.list-card-operation
/* Button: (pencil) */

,
div.pop-over
/* Pop Overs */

,
div.window a.button-link
/* Window Overlay: Button Links */

,
.boards-drawer input.js-search-boards
/* Board Search */

,
.boards-drawer .quiet-button,
a.board-tile.mod-add:hover,
div.tabbed-pane-main-col input.js-add-email:hover,
div.tabbed-pane-main-col input.js-log-out-other:hover,
div.tabbed-pane-main-col input.js-revoke:hover,
div.tabbed-pane-main-col a.big-link:hover,

/* Organization - Members - Find Members */

.tabbed-pane-main-col .org-members-page-layout .org-members-page-layout-sidebar input:hover,

/* Organization - Members - Add Members Button */

.tabbed-pane-main-col .org-members-page-layout .button-link:hover,

/* Organization - Business Class - Input */

.tabbed-pane-main-col .credit-card-form input:hover,
.tabbed-pane-main-col .credit-card-form .iframe-input:hover,
.checklist-item:hover,
.window .action-comment,
.window .button-link,
.window .card-detail-badge,
.window .card-detail-item-add-button,
.window .comment-box,
.window .confirm,
.window .checklist-new-item-text,
.window .checklist-item-checkbox,
.window .checklist-progress-bar,
.window .js-format-help,
.window .js-search:hover,
.window .quiet-button:hover,
.phenom,
.list .list-card .badge.is-voted,

/* Board Menu > Add Member Button */

div.board-menu a.button-link:hover,

/* Phenom Entry > Comment */

div.board-menu div.js-menu-action-list div.phenom div.current-comment,

/* Window - Title */

.window .window-title textarea.editing,

/* Window - Description */

.window .description-content .edit textarea {
    background: hsl(0, 0%, 20%) !important;
    color: hsl(0, 0%, 80%) !important;
    border-color: hsl(0, 0%, 15%) !important;
}

/* -- Layer 4 -- */

Layer-4,
span.list-card-operation:hover
/* Button: (pencil) */

,
div.window .button-link:hover
/* Window Overlay: Button Links */

,
.boards-drawer input.js-search-boards:hover
/* Board Search */

,

/* List Card Details (Sticker) */

.list-card.active-card.is-stickered .list-card-stickers-area,
.boards-drawer .sidebar-section-header-toggle-icon:hover,
.boards-drawer a.quiet-button:hover,
.boards-drawer-header,
a.js-new-tip:hover,
a.js-tip:hover,
.meta-link-list-item-link:hover,
.mod-notifications div.action-comment,
.window .button-link:hover,
.window .comment-box a:hover,
.window .confirm:hover,
.window .card-detail-item-add-button:hover,
.window .dark-hover:hover .icon-sm,
.window .js-format-help:hover,
.phenom.mod-unread,

/* Card - Comment - Card Link */

.phenom .comment-container a.known-service-link,

/* Card - Description - Card Link */

.window .card-detail-item a.known-service-link,
.list .list-card:hover .badge.is-voted {
    background: hsl(0, 0%, 25%) !important;
    color: hsl(0, 0%, 90%) !important;
    border-color: hsl(0, 0%, 15%) !important;
}

/* -- Layer 5 -- */

Layer-5,
.phenom.mod-unread:hover,

/* Card - Comment - Card Link */

.phenom .comment-container a.known-service-link:hover,

/* Card - Description - Card Link */

.window .card-detail-item a.known-service-link:hover {
    background: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 90%) !important;
    border-color: hsl(0, 0%, 20%) !important;
}

/* -- Unlayered Elements -- */

a
/* Links */

,
.header-search-icon {
    color: hsl(0, 0%, 75%) !important;
}
a:hover
/* Links */

,
.header-search-icon:hover,
.header-search-icon-dark.header-search-icon:hover,
.card-composer .list-card-composer-textarea,
.window .card-detail-edit .field,
.window .comment-box .comment-box-input {
    color: hsl(0, 0%, 95%) !important;
}
.compact-board-tile-fade {
    background: rgba(0, 0, 0, .88) !important;
}
div.tabbed-pane-nav {
    border-color: hsl(0, 0%, 5%) !important;
}
div.tabbed-pane-main-col *,
div.mod-notifications .phenom {
    border-color: hsl(0, 0%, 25%) !important;
}
div.tabbed-pane-main-col input.js-add-email,
div.tabbed-pane-main-col input.js-log-out-other,
div.tabbed-pane-main-col input.js-revoke,
form.js-profile-form input.js-submit-profile,
form.js-profile-form input.js-cancel-edit-profile,
.window .confirm {
    box-shadow: none !important;
    border-bottom-width: 1px !important;
    border-bottom-style: solid !important;
    border-color: transparent;
    color: inherit;
}
.window .comment-box .comment-box-input:focus {
    background: transparent !important;
}

/* Horizontal Bars */

hr {
    background: hsl(0, 0%, 5%) !important;
}

/* Phenom Entry */

div.phenom {
    margin-left: 0px;
    margin-bottom: 2px;
    padding: 10px 5px 8px 38px;
}

/* Phenom Entry > Member */

div.phenom div.phenom-creator {
    left: 5px;
}

/* Phenom Entry > Member */

div.phenom.mod-other-type div.phenom-creator {
    left: 12px;
}

/* List Card Details (Sticker) */

.list-card .list-card-details,

/* List Card Details (Sticker) */

.list-card.is-stickered .list-card-details,

/* List Card Details (Sticker) */

.list-card.active-card.is-stickered .list-card-details {
    background: none;
}

/* Card Labels */

.card-label-red {
    background-color: hsla(0, 100%, 50%, 0.25) !important;
}
.card-label-orange {
    background-color: hsla(30, 100%, 50%, 0.25) !important;
}
.card-label-yellow {
    background-color: hsla(60, 100%, 50%, 0.25) !important;
}
.card-label-lime {
    background-color: hsla(90, 100%, 50%, 0.25) !important;
}
.card-label-green {
    background-color: hsla(120, 100%, 50%, 0.25) !important;
}

/*.card-label-x {
	background-color: hsla(150, 100%, 50%, 0.25) !important;
}*/

.card-label-sky {
    background-color: hsla(180, 100%, 50%, 0.25) !important;
}

/*.card-label-x {
	background-color: hsla(210, 100%, 50%, 0.25) !important;
}*/

.card-label-blue {
    background-color: hsla(240, 100%, 50%, 0.25) !important;
}
.card-label-purple {
    background-color: hsla(270, 100%, 50%, 0.25) !important;
}
.card-label-purple {
    background-color: hsla(300, 100%, 50%, 0.25) !important;
}
.card-label-pink {
    background-color: hsla(330, 100%, 50%, 0.25) !important;
}
.card-label-black {
    background-color: hsla(0, 0%, 0%, 0.25) !important;
}
.card-label-null {
    background-color: hsla(0, 0%, 100%, 0.25) !important;
}

/* ----- ----- ----- ----- ----- ----- ----- ----- ----- -----
   Webkit-only here (Chrome, Opera, Steam)
   ----- ----- ----- ----- ----- ----- ----- ----- ----- ----- */

::-webkit-scrollbar {
    background: transparent !important;
    height: 12px !important;
    width: 12px !important;
}
::-webkit-scrollbar-button {
    display: none !important;
}
::-webkit-scrollbar-track {
    background: transparent !important;
}
::-webkit-scrollbar-track:horizontal {
    margin-left: 4px !important;
    margin-right: 4px !important;
}
::-webkit-scrollbar-track:vertical {
    margin-top: 4px !important;
    margin-bottom: 4px !important;
}
::-webkit-scrollbar-track-piece {
    background: #050505 !important;
    border-radius: 0px !important;
}
::-webkit-scrollbar-thumb {
    background: #222222 !important;
    border-radius: 16px;
}
::-webkit-scrollbar-thumb:hover {
    box-shadow: inset 0 0 6px #777;
}
#board {
    display: block !important;
    text-align: left;
    position: relative !important;
    white-space: initial;
    padding-bottom: 24px;
}
.board-canvas {
    height: auto !important;
}
.body-board-view {
    overflow-y: visible;
}
.js-list,
.js-add-list {
    display: inline-block !important;
    vertical-align: top;
    margin: 0 0 10px 10px;
}
.u-gutter {
    margin-left: 0;
}
}
