css:
@-moz-document domain(radio.yandex.ru) {

body,
.track__cover, .nb-user-avatar,
.station-icon, .station__title, .background,
.nav__banner-content, .footer__static-text, .track__menu-wrapper {
    filter: invert(100%);
}

.logo__radio {
    filter: hue-rotate(180deg) saturate(3.7);
}

}
