css:
@-moz-document domain("csuchico.edu") {
:root {
    
    
    --bg-color-background   : #2b2c2f;      /* Main Background */
    --bg-color-footer		: #222426;      /* Grey highlight */
    --bg-color-first		: #35383b;      /* Grey highlight */
    --bg-color-light-grey	: #6b6b6b;      /* Light Grey */
    --bg-color-hover		: #3b3b4b;      /* */
    --bg-color-button		: #222426;
    --bg-color-button-hover	: #2f3235;
    --bg-color-dropdown		: #0e1114;
    
    
    --table-color-header	: #17191b;
    --table-color-body		: #242627;
    --table-color-even		: #242627;
    --table-color-odd		: #2b2c32;

    --border-color-top		: #2c2c36;      /* */
    --border-color-right	: #2c2c36;      /* */
    --border-color-bottom	: #2c2c36;      /* */
    --border-color-left		: #2c2c36;      /* */

    --text-color-links		: #33B1D7;      
    --text-color-hover		: #10C4FB;      
    --text-color-visited	: #9A6EFF;     
    
    --text-color-first		: #ffffff;      /* */
    --text-color-second		: #eeeeee;      /* */
    --text-color-third		: #b2b2b2;      /* */
    --text-color-forth		: #5c5c5c;      /* */
    
    --input-color-background: #333333;
    
    
    /* -------------- User Changable Values ----------------- */
    
}


/* ----------------------------- Background Colors ----------------------- */
/* Main BG */
body, html, .audience-nav__inner-wrap, .connect, .event-listing .month, #content-col1 .highlight-item, .homebox, #content-col2-right .highlight-item, #masthead, body.front .pane-mlib-mlib-featured .pane-content, .list-group-item, body.front #page-header #block-block-13 #ask-wrapper, .btn-default, body.front .pane-events .pull-left span:first-child, #content, .up, .up #portalPageBodyInner, .up #portalPageInner, .locationPane
{ background: var(--bg-color-background); }
html
{ background: var(--bg-color-background) !important;}

.main-content-wrapper, .site-header-wrapper, div#header div, div#header, #header, #top-nav, #page-banner, #footer, ul#left-nav, .content-inner, .global-header-inner, .site-header > .row, [role="main"] > .row, header .container, .container, .btn-default:hover, .yui3-g, #yui3-main, .up #portalPageHeader, .up #portalNavigation, .up .up-portlet-wrapper .up-portlet-content-wrapper, .banner .panel.bannert .content, #moduleTitle, .portlet h2
{ background: var(--bg-color-first); }

.site-footer > .row, #home #content-col2-right, .site-footer, .site-footer-inner, .global-footer, .global-footer-inner, .accordion .accordion-navigation > a, .accordion dd > a, .accordion .accordion-navigation > .content.active, .accordion dd > .content.active, .tiny-font, div[style*="background-color:#960000;"], .small-font, #s-lg-az-nav, #s-lg-er-nav, #s-lg-hp-nav, #s-lg-profile-nav, #s-lg-srch-nav, #s-lg-er-nav-bottom, #s-lg-hp-nav-bottom, #s-lg-srch-nav-bottom, .panel, .panel-default > .panel-heading, .sidebar-nav__section, .sidebar-nav .has-dropdown:hover li, .up #portalPageFooter, .panel.numbered.disabled .panelnumberheader, .panel.disabled .panelheader, .up #portalPageHeader #portalPageBar, .portlet, .actionBar, .paneTabs
{ background: var(--bg-color-footer) !important; }

/* --- Tabs --- */
/*Active Tab*/
.maintab-active
{ background-color: #57636a; }

/* -- Tables -- */
.top-bar, .campus-menu-wrap > .row, .top-bar-section li:not(.has-form) a:not(.button), .audience-nav a, #topnav, .breadcrumbs, table thead, table tfoot, .navbar-default, tr.dr-table-header *, .dr-table-footer *, .disabledGrayish
{ background: var(--table-color-header); }
table, .rich-table-cell
{ background: var(--table-color-body); }
table tr.even, .dr-table:nth-child(even)
{ background: var(--table-color-even); }
table tr.odd, .dr-table:nth-child(odd), .custom-header
{ background: var(--table-color-odd); }
/* hover */
.top-bar-section li:not(.has-form) a:hover:not(.button), ul#left-nav li a:hover, ul#left-nav li a:visited:hover, #topnav ul#main-nav li:hover, tr.hover-highlight:hover, tr.hover-highlight:hover td
{  background: var(--bg-color-button-hover); }

/* -- Buttons/Dropdowns -- */
.button, button, ul.pagination li:hover a, ul.pagination li a:focus, .genericButton, .genericButtonImg, input[type="submit"].genericButton, .browse, .browseIcon, .rumble h3+ul>li>a, .rumble h3+ul>li>a, .rumble_top h3+ul>li>a, .rumble_top h3+ul>li>a, .paging a, .itemHeaderControl>a, .actionMenuButton, .button-3, .button-3-img, .button-4, .button-4-img, #toolsSearchBox+.button-4, .treeContainer .tree li a.button-4-img, .rumble a, .rumble_top a, .rumble .abutton, .rumble_top .abutton, .cmdiv
{ background: var(--bg-color-button); }
/* Hover */
.button:hover, button:hover, .top-bar:not(.expanded) .top-bar-section li.quicklinks .dropdown li:hover:not(.has-form) > a:not(.button), ul#left-nav li:hover ul li a:hover, ul#left-nav li:hover ul li a:visited:hover, ul#left-nav li:hover ul li:hover ul li a:hover, ul#left-nav li:hover ul li:hover ul li a:visited:hover, ul#left-nav li:hover ul li:hover a, ul#left-nav li:hover>a, ul#quicklinks li ul li a:hover, ul#quicklinks li a:hover, #left-nav li:hover, .accordion .accordion-navigation > a:hover, .accordion dd > a:hover, .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover, .nav .open > a, .nav .open > a:focus, .nav .open > a:hover, .sidebar-nav .has-dropdown:hover > span, .sidebar-nav__section a:hover, .genericButton:hover, .genericButton:focus, .genericButtonImg:hover, .genericButtonImg:focus, .browse:hover, .browse:focus, .browseIcon:hover, .browseIcon:focus, input[type="submit"].genericButton:hover, input[type="submit"].genericButton:focus, .rumble h3+ul>li>a:hover, .rumble h3+ul>li>a:focus, .rumble_top h3+ul>li>a:hover, .rumble_top h3+ul>li>a:focus, .paging a:hover, .paging a:focus, .itemHeaderControl a:hover, .itemHeaderControl a:focus, .actionMenuButton:hover, .actionMenuButton:focus, .button-3:hover, .button-3:focus, .button-4:hover, .button-4:focus, .button-3-img:hover, .button-3-img:focus, .button-4-img:hover, .button-4-img:focus, .navPaletteContent ul li:first-child .paletteSearch .button-4:hover, .navPaletteContent ul li:first-child .paletteSearch .button-4:focus, h3#cs_search+ul div.none:first-child .button-4:hover, h3#cs_search+ul div.none:first-child .button-4:focus, #toolsSearchBox+.button-4:hover, #toolsSearchBox+.button-4:focus, .treeContainer .tree li a.button-4-img:hover, .treeContainer .tree li a.button-4-img:focus, .rumble a:hover, .rumble_top a:hover, .rumble a:focus, .rumble_top a:focus, .cmdiv a:hover, .cmdiv a:focus, .menumini a:hover, .menumini a:focus
{  background: var(--bg-color-button-hover) !important; }

.top-bar:not(.expanded) .top-bar-section li.quicklinks .dropdown a:not(.button), .top-bar:not(.expanded) .top-bar-section li.quicklinks a:not(.button), .top-bar:not(.expanded) .top-bar-section li.quicklinks:hover a:not(.button), .top-bar:not(.expanded) .top-bar-section li.quicklinks .dropdown a:not(.button), .top-bar:not(.expanded) .top-bar-section li.quicklinks a:not(.button), .top-bar:not(.expanded) .top-bar-section li.quicklinks:hover a:not(.button), .task-nav .has-dropdown > .dropdown, ul#left-nav li:hover ul, ul#left-nav li:hover ul li:hover ul, ul#quicklinks li h2, ul#quicklinks li ul, select, ul.pagination li.current a, .dropdown-menu, .up #portalNavigation .portal-flyout-container li, .reorderableModule:hover
{ background: var(--bg-color-dropdown) !important; }

/*Portal Yellow Border background*/
.up .up-portlet-wrapper {
 background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgb(130, 129, 129)), color-stop(0.1, rgb(115, 118, 118)), to(rgb(78, 77, 77))) rgb(71, 70, 69);    
}

/*Note at bottom of BBL*/
div.vtbegenerated>div {
 	background-color: #1c1500 !important;   
}

/*Remove Background Color */
input.bannerHelp
{ background: none !important; }


/* ----------------------------- Text Colors ----------------------------- */
/* Links */
a
{ color: var(--text-color-links) !important; }
a:hover
{ color: var(--text-color-hover) !important; }
a:visited
{ color: var(--text-color-visited) !important; }

/*Red Links on Portal Page*/
.fl-theme-mist .fl-widget-content a {
 	color: #e27a7a !important;   
}
.fl-theme-mist .fl-widget-content a:hover {
 	color: #ff5d5d !important;   
}

.audience-nav--home a, .task-nav a, #page-banner h1 a, .up .up-portlet-wrapper .up-portlet-titlebar h2 a
{ color: var(--text-color-first) !important; }

body, #global-nav a, .event-listing .month, .button:hover, button:hover, .audience-nav a, .audience-nav a:focus, .audience-nav a:hover, .audience-nav a:visited, #header ul li a:hover, #header ul li a:visited:hover, #content-col2-right .highlight-item h3, div[style*="color:black;"], .audience-nav--home a, .carousel__header, .main-content p, .main-content dl, .main-content ol, .main-content ul, table thead tr th, table thead tr td, table tfoot tr th, table tfoot tr td, .navbar-default #block-menu-block-2 .nav > li > a, .btn-info, div#task-nav a:hover, .panel h2, table.ss_section_csu h3, .subtab-text, .custom-header, .padded-cell, .up #portalNavigation .portal-navigation .portal-navigation-link, .fl-theme-mist .fl-tabs li.fl-tabs-active a, .fl-theme-mist .fl-tabs li a
{ color: var(--text-color-second) !important; }

#footer p, .event-listing .day, .top-bar-section .dropdown li:not(.has-form):not(.active) > a:not(.button), .top-bar-section ul li > a, .button, button, .audience-nav a, .breadcrumbs > .current, .breadcrumbs > .current a, #content ul, #content ol, #content blockquote, #header ul li a, #header ul li a:visited, ul#left-nav li a, ul#left-nav li a:visited, ul#left-nav li span, ul#left-nav li a, ul#left-nav li a:visited, select, body.front #page-header #block-mlib-mlib-hours p, body.front .pane-events .pull-left span:first-child, .sidebar-nav__section span:not([class^="ext-label"]), div#task-nav a, #sidebar, #login-form input[type="submit"], .up #portalPageFooter, .dr-mpnl-pnl-b, .rich-table-cell, .dr-tbpnl-cntnt, .rich-table-cell, .defaultInstruction, .rich-subtable-cell, .up #portalPageHeader #portalPageBar, .up #portalPageHeader #portalWelcome
{ color: var(--text-color-third) !important;}

/* RED Text */
.up #portalPageHeader #portalWelcome a, .up #portalPageHeader #portalWelcome .user-name
{ color: red; }

/* -- Input field Background -- */
textarea, pre, input, code, input[type="search"]
{ background: #171717 !important }

/* -- Input Field inactive color -- */
input, .up #portalNavigation .active .portal-navigation-link, .fl-theme-mist a
{ color: var(--text-color-forth) !important; }

input:active, input:focus, input.button, .portlet
{ color: var(--text-color-third) !important; }

/* Red Text */
div[style*="color:red;"], h1, h2, h3, h4, h5, h6, #security-notice
{  color: #f33535 !important; }


/* ======================= Borders ========================== */
/* Darken White Borders */
/*Box Border*/
.connect, .button, button, .searchbox input[type="text"], #header #searchbox input#site-search-query, #content-col1 .highlight-item, .main-nav-wrap, .genericButton, .genericButtonImg, input[type="submit"].genericButton, .browse, .browseIcon, .rumble h3+ul>li>a, .rumble h3+ul>li>a, .rumble_top h3+ul>li>a, .rumble_top h3+ul>li>a, .paging a, .itemHeaderControl>a, .actionMenuButton, .button-3, .button-3-img, .button-4, .button-4-img, #toolsSearchBox+.button-4, .treeContainer .tree li a.button-4-img, .rumble a, .rumble_top a, .rumble .abutton, .rumble_top .abutton
{
    border: 1px solid #555 !important;
}

ul#left-nav li:hover ul li a:hover, ul#left-nav li:hover ul li a:visited:hover, ul#left-nav li:hover ul li:hover ul li a:hover, ul#left-nav li:hover ul li:hover ul li a:visited:hover, #topnav ul#main-nav li, ul#quicklinks li ul, ul#quicklinks li ul li a, .homebox, .breadcrumbs, select, input[type="search"], table, input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], textarea, .list-group-item, .footer, body.front .pane-mlib-mlib-featured .pane-content, .navbar-default, .s-lg-az-result, .s-lg-az-trial, .s-lg-az-result, .s-lg-az-trial, .s-lg-db-panel-title, .s-lib-public-side-header h2, .panel-default > .panel-heading, .panel, hr, .dept-contact--alt .dept-contact__content, .carousel--bp-thumbs img, #yui3-main, #login-form input[type="text"], #login-form input[type="password"]
{
 	border-color		:  #555; 
 	border-top-color	:  #555; 
 	border-bottom-color	:  #555; 
 	border-right-color	:  #555; 
 	border-left-color	:  #555; 
}

/*Top Border*/
asdf {
    border-top: 1px solid #555;
}
asdf {
    border-top-color: #555 !important;
}
/*Bottom Border*/
asdf {
    border-bottom: 1px solid #555;
}
.up #portalNavigation { 
    border-bottom-color: #555 !important;
}
asdf {
    border-bottom-color: #3e3e3e;   
}
/*Right Border*/
.top-bar-section > ul > .divider, .top-bar-section > ul > [role="separator"] {
    border-right: 1px solid #555;
}
asdf { /* Color only */
    border-right-color: #555 !important; 
}
asdf { /* Color only */
    border-right-color: #3e3e3e;   
}
/*Left Border*/
asdf {
    border-left: 1px solid #555;
}
asdf { /* Color only */
    border-left-color: #555 !important;
}
/* Borders using "Background-color" rather than "border-color" */
asdf {
    background: #555;
}

/* Remove Border completely */
.up #portalPageBody
{
 	border: none !important;   
}


/* -------------------- Image replacement ----------------------- */
.csuc-signature, #header h1 a, #header-signature
{ 
 	background: url(https://www.csuchico.edu/_common/2.3/images/csuc-signature.svg) 0 0/contain no-repeat,0 0/contain no-repeat !important;   
}

/* ----------------- Filters --------------------------- */
.site-footer .icon
{ filter: invert(1); }
#interior #banner, .main-nav-wrap
{ mix-blend-mode: luminosity; }
div#portalLogo a[title="Go to Home"]
{ mix-blend-mode: multiply;   }
.portal-page-column-inner
{ mix-blend-mode: hard-light; }

.carousel__caption
{ background: rgba(0,0,0,.8); }

body.front #page-header #block-mlib-mlib-hours, body.front #page-header #block-mlib-mlib-onesearch .tab-content
{ background: rgba(50,50,50,.75) !important; }

/* ------------------------ Sizing ----------------------- */
.global-footer
{ max-width: 100% !important; }
/* ----------------- Shadows ---------------------- */
#search-box input[type="submit"], #login-form input[type="submit"]
{
 	text-shadow: none !important;   
}
.genericButton, .genericButtonImg, input[type="submit"].genericButton, .browse, .browseIcon, .rumble h3+ul>li>a, .rumble h3+ul>li>a, .rumble_top h3+ul>li>a, .rumble_top h3+ul>li>a, .paging a, .itemHeaderControl>a, .actionMenuButton, .button-3, .button-3-img, .button-4, .button-4-img, #toolsSearchBox+.button-4, .treeContainer .tree li a.button-4-img, .rumble a, .rumble_top a, .rumble .abutton, .rumble_top .abutton {
 	box-shadow: none;   
}

.carousel--bp-thumbs img
{ opacity: 0.75; }
.carousel--bp-thumbs img[src*="thumb-color"] {
    border-color: #cecece;
    opacity: 0.90;
}
 img[alt="CSU, Chico"], img[alt="CSU, Chico signature"]
{ opacity: 0; }
}
