css:
@-moz-document domain("9gag.com") {
.background-white {
    background-color: #000;
}

.CS3 .comment-box .action {
    border: 1px solid #777;
    border-top-color: #888;
}

.CS3 input[type=url], .CS3 textarea {
    border: 1px solid #777;
}

input, textarea {
	background-color: #000;
    color: #ccc;
}

textarea {
	box-shadow: 0px 0px 1px #ccc;
}

section.modal.upload .source:hover {
    background-color: #666;
}

section.modal.upload .source.file:after, section.modal.upload .source.pic-url:after {
	filter: invert(.8);
}

.section-nav {
	background-color: #222;
    /*background: linear-gradient(to bottom, #222, #333);*/
}

p {
	color: #ccc;
}

h3 {
	color: #fff;
}

.ticker li a:hover {
    background-color: #444;
    color: #ccc;
}

.CS3 a.cmnt-btn.grey {
    background-color: #000;
    border-color: #999;
    color: #ccc;
}

section.section-header .social-love span.instagram-follow {
    border: 1px solid #000;
    background-color: #222;
}

section.section-header .social-love span.instagram-follow a {
    border: 1px solid #333333;
    color: #ccc;
}

.CS3 .comment-box.reaction .action .cmnt-btn.grey {
    padding: 3px 2px 3px 2px;
}

.CS3 a.cmnt-btn.grey:hover {
    background-color: #333;
}

.CS3 .comment-entry .action .vote-buttons.up .icn-up, .CS3 .comment-entry .action .vote-buttons.up .up:hover .icn-up {
    animation: upvote .8s;
}

.CS3 .comment-entry .action .vote-buttons.down .down:hover .icn-down, .CS3 .comment-entry .action .vote-buttons.down .icn-down {
    animation: downvote .8s;
}

.CS3 .comment-entry .action .vote-buttons .down {
    background: none;
}

.CS3 .comment-entry .action .vote-buttons .down .icn-down {
    background: url(http://assets-comment-fun.9cache.com/s/9a6b6452/34e3af4a9a43f636de49dbcbaf95ca112326e256/static/dist/img/comment-sprite.png) no-repeat -20px 0px !important;
}

.CS3 .comment-entry .action .vote-buttons.down .down .icn-down {
    background: url(http://assets-comment-fun.9cache.com/s/9a6b6452/34e3af4a9a43f636de49dbcbaf95ca112326e256/static/dist/img/comment-sprite.png) no-repeat -20px -40px !important;
    display: block;
    filter: hue-rotate(145deg);
}

.CS3 .tab-bar ul.tab li.active a {
    color: #fff;
}

.CS3 .tab-bar ul.tab a:hover {
    color: #fff;
}

.post-afterbar-meta a:hover {
    color: #fff;
}

.popup-menu a {
    background-color: #000;
}

.popup-menu ul {
	background-color: #000;
}

.post-text-container.badge-item-description {
	color: white;
}

.badge-scroll-to-top.back-to-top.show {
	background-color: #222;
    color: #ddd;
    border-color: #444;
}

.section-nav ul li a {
	color: rgba(255, 255, 255, .6) !important;
    transition: all .2s;
}

.section-nav ul li a:hover {
	color: rgba(255, 255, 255, 1) !important;
}

.section-nav a.selected {
	color: white !important;
}

.section-header {
	box-shadow: none !important;
}

.section-header:after {
	content: '';
	box-shadow: inset 0 0 0 1px rgba(255,255,255,.2) !important;
    height: 1px;
    display: block;
}

section.modal.upload .source-file {
    background-color: #000;
}

section.modal.upload .source-file:hover {
    background-color: #444;
}

section.modal.upload .source-meme:hover, section.modal.upload .source-url:hover {
    background-color: #444;
}

a.btn.grey {
    background-color: #000;
    color: #999;
    border: solid 1px #333;
}

a.btn.grey:hover {
    background-color: #444;
    border: solid 1px #333;
}

.badge-item-vote-down.down.active {
    animation: downvote-large .8s;
    filter: hue-rotate(145deg);
}

.badge-item-vote-up.up.active {
    animation: upvote-large .8s;
}

@keyframes upvote {
    0% {
        transform: translateY(0px);
        opacity: 1;
    }
    70% {
        transform: translateY(-12px);
        opacity: 0;
    }
    71% {
        transform: translateY(12px);
        opacity: 0;
    }
    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}

@keyframes upvote-large {
    0% {
        transform: translateY(0px);
        opacity: 1;
    }
    70% {
        opacity: 1;
        transform: translateY(-40px);
    }
    71% {
    	transform: translateY(40px);
        opacity: 0;
    }
    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}

@keyframes downvote {
    0% {
        transform: translateY(0px);
        opacity: 1;
    }
    70% {
        transform: translateY(12px);
        opacity: 0;
    }
    71% {
        transform: translateY(-12px);
        opacity: 0;
    }
    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}

@keyframes downvote-large {
    0% {
        transform: translateY(0px);
        opacity: 1;
    }
    70% {
   		transform: translateY(40px);
    	opacity: 1;
    }
    71% {
        transform: translateY(-40px);
        opacity: 0;
    }
    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}

section#list-view-2 h2 a {
    font-size: 20px;
    color: #fff;
}

.post-afterbar-a.in-post-top {
    background-color: #000;
}

.horizontal-vote a.up span {
    background-color: #000;
}

.CS3 {
    background-color: #000;
    color: #fff;
}

.username, .username.badge-author-name.badge-normal {
    color: #fff !important;
}

.badge-evt {
    color: #fff !important;
    transition: all .2s;
}

.badge-evt:hover {
    color: #ccc !important;
}

.footer {
    background-color: #000 !important;
}

.CS3 .comment-box.indent-1 .payload .textarea-container textarea, .CS3 .comment-box .payload .textarea-container textarea.focus {
    background-color: #000;
    color: #fff;
}

section#list-view-2 article {
    margin-bottom: 30px;
    border-top: 1px solid #444;
    padding: 20px 0 0;
}

.btn-vote a {
    border: 1px solid #444;
    transition: all .2s;
}

.btn-vote a:active {
    background-color: #fff;
}

header#top-nav {
    background: linear-gradient(to bottom, #222, #111) !important;
}

img, video {
    transition: all .2s;
}

.app-store, .google-play {
    border: 1px solid #ccc !important;
}

.nav-menu>ul li a {
    transition: all .2s;
}

.nav-menu>ul li a:hover {
    color: #fff;
}

.popup-menu-wide {
    color: #fff;
    background-color: #000;
}

.popup-menu-wide > ul > li > a {
    color: #fff;
    background-color: #000;
    transition: all .2s;
}

.popup-menu-wide > ul > li > a:hover {
    background-color: #000;
}

section.modal {
    background-color: #000;
}

h2 {
    color: #fff;
}

section.modal a.btn-close {
    filter: invert(1);
}

label {
    color: #fff;
}

input[type="text"], input[type="password"] {
    background-color: #000;
    color: #fff;
}

a.post-read-more {
    border-left: 1px solid #222;
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
    box-shadow: 0 1px 0 rgba(0,0,0,.1);
}

.post-container:hover .post-read-more .shadow {
    filter: invert(1);
    background-position: 0 -15px;
}

.post-container:hover .post-read-more {
    border-left: 1px solid #333;
    border-right: 1px solid #333;
    border-bottom: 1px solid #333;
}
}
