css:
@-moz-document domain("02ch.net") {
html,body {
  background-color: #6E7C87;
  color: #FFF;
  font-family: Calibri, sans-serif;
}

a:hover {
  color: #343855;
}

.adminbar {
  clear: both;
  float: left;
  text-align: left;
  margin: 5px;
  font-size: .8em;
}

.adminbar a {
  font-weight: 700;
}

.postblock {
  background: transparent;
  color: #D1D7DD;
  font-weight: 700;
}

.footer {
  text-align: center;
  font-size: 12px;
  font-family: arial;
  margin: 2em 0 0;
}

.unkfunc {
  background: inherit;
  color: #81B1FF;
}

.reflink {
  font-size: .8em;
  font-weight: 700;
}

.filesize {
  text-decoration: none;
  color: #FFF;
  font-size: .8em;
  margin-left: 20px;
}

.filetitle {
  background: inherit;
  font-size: 1.2em;
  color: #28344A;
  font-weight: 700;
}

.postername {
  color: #FFF;
  font-weight: 700;
}

.omittedposts {
  color: #D0D0D0;
}

.reply {
  background: #546471;
  border: solid 2px #546471;
  -moz-border-radius: 5px;
  margin: 0;
  padding: 0;
}

blockquote {
  margin: .5em .5em .5em 1em;
}

blockquote p {
  margin: 0;
  padding: 0;
}

.theader, .passvalid {
    background: #768998;
    text-align: center;
    color: #BDD2E7;
    clear: both;
    font-weight: 700;
    margin-bottom: .5em;
    border: solid 1px #1B204D;
    -moz-border-radius: 5px;
    padding: 2px;
}

.reply blockquote {
  margin: .5em;
}

.doubledash {
  display: none;
  vertical-align: top;
  clear: both;
  float: left;
}

.replytitle {
  font-size: 1.2em;
  color: #FFF;
  font-weight: 700;
}

.commentpostername {
  color: #FFF;
  font-weight: 800;
}

.thumbnailmsg {
  font-size: .8em;
  color: #FFF;
}

hr {
  height: 3px;
  background: url('data:image/png;base64,') center no-repeat;
  border-width: 0;
}

.highlight {
  background: #212D57;
  color: #A19CBC;
  border: 2px dashed #585858;
}

textarea,input {
  -moz-box-sizing: content-box;
  -webkit-box-sizing: content-box;
  box-sizing: content-box;
  /*-moz-appearance:none;-webkit-appearance:none;appearance:none;*/
  background-color: #37414a;
  color: #fff;
  border: 2px solid #545454!important;
  -moz-border-radius: 5px;
  -webkit-border-radius: 5px;
  border-radius: 5px;
}

a {
  color: #FFF;
}

table,table td {
  border-style: none;
}

.rules {
  font-size: .7em;
}

span.spoiler,span[onmouseout*="color='black'"] {
  background: #212121;
  color: #212121;
}

span.spoiler:hover,span[onmouseover*="color='white'"]:hover {
  background: #212121;
  color: #48B0FD;
}

span.spoiler a {
  color: #212121!important;
  background: #212121!important;
}

span.spoiler a:hover {
  background: #212121!important;
  color: white!important;
}

.logo,.logo a {
  clear: both;
  text-align: center;
  font-size: 24pt;
  font-weight: 700;
  color: #C8CDD5;
  text-decoration: none;
}

.nothumb {
  color: #37414A;
  background-color: #FFF;
  border-style: dotted;
  margin: .3em .5em;
}

#bottom {
  left: 143px;
  /*position:absolute;*/
}

html,body {
  margin: 0;
  padding: 0;
}

table td.reply blockquote {
  margin-left: 20px;
  margin-right: 20px;
  margin-top: 10px;
}

.unkfunc {
  margin: 0px !important;
}

::-webkit-input-placeholder {
  color: #37414A;
}

::-moz-placeholder {
  color: #37414A;
}

::-moz-placeholder {
  color: #37414A;
}

:-ms-input-placeholder {
  color: #37414A;
}

#imgcaptcha {
  filter: invert(100%);
}

label nobr {
  margin-left: 5px;
}

.abbrev {
  color: #FFF;
  font-size: 0.9em;
}

small br {
  display: none !important;
}

div[id^="pstprev_"], div[id*=" pstprev_"] {
  background-color: #37414a !important;
  border: 2px solid #545454 !important;
}

h1 a {
  color: #C8CDD5;
  padding-left: 10px;
  padding-top: 5px;
}

#dis {
  background-color: #37414a;
  border: 1px solid #545454;
}
dl.menu dd {
  border-left: 1px solid #545454;
}
@media only screen and (max-width: 480px) {
  #delform table {
    width: 100%;
    table-layout: fixed;
  }

  #delform table td.reply {
    width: 100%;
  }

  .doubledash {
    display: none;
  }

  ::-webkit-input-placeholder {
    color: gray;
  }

  ::-moz-placeholder {
    color: gray;
  }

  ::-moz-placeholder {
    color: gray;
  }

  :-ms-input-placeholder {
    color: gray;
  }
}
}
