css:
@-moz-document domain("www.shareville.no"), domain("www.shareville.se"), domain("www.shareville.dk"), domain("www.shareville.fi") {
/*			/* Shareville Flat Dark & Clean v.0.1 */
                    /* by MarUlberg */
/* Inspired by kirnbauer's "Youtube Dark Style Material [BETA]" */

/*	Detail Color    	*/
.circle-indicator.bought{
    background-color: #99DD99 !important;
}
.circle-indicator.sold{
    background-color: #99DD99 !important;
}
/*	Text Color    	*/
.plus{
    color: #99DD99 !important;
}
.minus{
    color: #DD7777 !important;
}
body, button, div, dt, dl, dd, a, b, h1, h2, h3, h4, h5, h6, h7, input, textarea, label, li, p, s, select, option, small, span:not(.value), table, th, td, blockquote, legend, .panel-body.panel-body-banner-tip ul .sidebar .nav.nav-pills li:hover a li:before, .sidebar .nav.nav-pills li:hover a .icon, .sidebar .nav.nav-pills li:hover a .panel-body.panel-body-banner-tip ul li:before, .navbar a .icon, .navbar a .panel-body.panel-body-banner-tip ul li:before, .panel-body.panel-body-banner-tip ul .navbar a li:before{
    color: #cccccc !important;
}
body, button, div, dt, dl, dd, a, b, h1, h2, h3, h4, h5, h6, h7, input, textarea, label, li, p, s, select, option, small, span, table, th, td, blockquote, legend{
    text-shadow: 0px 0px #000000 !important;
}
/*	Header Color   	*/
.navbar-default, .alert-info, .navbar, footer, #footer, #footerContainerDepa, #navWrapper{
    background-color: #424242 !important;
}
/*	Transparent 	*/

.panel, .panel.panel-profile-card .profile-meta-container, .dividend-calendar-panel .widget-nav, .panel .panel-heading, .instrument-item, .instrument-header, .position-list ng-include .position-item, .top-panel, .nav.nav-tabs.nav-justified li a, #search.input-group.search button, #search.input-group.search input, .dividend-calendar-panel .list-container table .month-header, .form-control, .bg-odd, .bg-even, .rating-wrapper .rating-inner, .sub-panel, .panel-footer, .panel-body-stock-development, .panel-body-kpi.panel-body-kpi, .panel-footer-kpi.panel-body-kpi, .table-striped>tbody>tr:nth-of-type(odd), .message-create, .dropdown-menu-notifications .panel-notifications-list .panel-notifications-list-item.is-read, .dropdown-menu-notifications .panel-notifications-list li.is-read, .dropdown-menu-notifications .scrollable-list .panel-notifications-list-item.is-read, .dropdown-menu-notifications .scrollable-list li.is-read, .panel-notifications .panel-notifications-list .panel-notifications-list-item.is-read, .panel-notifications .panel-notifications-list li.is-read, .panel-notifications .scrollable-list .panel-notifications-list-item.is-read, .panel-notifications .scrollable-list li.is-read, .list-group-item, get-started-button > a.btn.text-normal, .horizontal-divider, hr, #content, div.order-bottom-panel, .trade-panel table th, .order-table-body table th, .order-table-body table tr.fundSwitch td, #iframe-tunnel, #topnavigation li a, #shareville-ticker, #topSearch input.searchField, .mcContent .navigation, .mcContent .navigation li, .mcContent .navigation li a, .bodyArea, .half, .mcContent table th, .mcContent .headBar, div.svara, .sv-layout.sv-skip-spacer{
    background-color: transparent !important;
}
#topnavigation li a:hover{
    background: transparent !important;
}
/*	Background Color  	*/
body, .btn:not(.btn-default), .sidebar .nav.nav-pills li.active a, .sidebar .nav.nav-pills li:active a, .sidebar .nav.nav-pills li:hover a, .startPage .multiPlatform, .startPage .insights, .startPage .joinnow, select, optgroup, option, .btn-primary, #cookies-warning-container span.submit, #cookies-denied-container span.submit, span.deny-cookies, .pagecontent:nth-child(even){
    background-color: #212121 !important;
}
body.bgi, #content div.background, .firstpage .usp, #content.firstpage .topBar, #content.firstpage .contentbox{
    background: #212121 !important;
}
comment-text .comment-content .comment-text-expander{
    background: -moz-linear-gradient(top,rgba(0,0,0,0) 0, #000000 65%);
    background: -webkit-gradient(left top,left bottom,color-stop(0,rgba(0,0,0,0)),color-stop(75%,#000000));
    background: -webkit-linear-gradient(top,rgba(0,0,0,0) 0, #000000 65%);
    background: -o-linear-gradient(top,rgba(0,0,0,0) 0, #000000 65%);
    background: -ms-linear-gradient(top,rgba(0,0,0,0) 0, #000000 65%);
    background: linear-gradient(to bottom,rgba(0,0,0,0) 0, #000000 65%);
}
/*	Window Color   	*/
.col-md-4.sidebar, .nav.nav-pills.nav-justified li:hover a, .nav.nav-pills.nav-justified li.active a, .dropdown-menu, .rating-wrapper .rating-inner .rating-popover, .message-item:hover, p.intent-description:first-child, .trade-panel table tr:hover, .order-table-body table tr:hover, .top-border, .trade-heading, div#content div.twocol.box, .mcContent table tr:hover{
    background-color: #424242 !important;
}
.ui-select-bootstrap .ui-select-choices-row.active>span, .ui-select-bootstrap .ui-select-choices-row>span:focus, .ui-select-bootstrap .ui-select-choices-row>span:hover, .dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover, .mcContent, .mpContent{
    background-color: #212121 !important;
}
/*	Theater Color  	*/
.well, #cookies-warning-container, .pagecontent:nth-child(odd){
    background-color: #000000 !important;
}
/*	Borders & Shadows	*/
.btn.btn-bookmark, .navbar-default, .sidebar .nav.nav-pills li a, .panel.panel-profile-card .profile-meta-container, .panel.panel-profile-card .profile-meta-container .kpi:not(:last-child), .btn.btn-primary, .dividend-calendar-panel .widget-nav, .panel .panel-heading, .sidebar .panel-body, .btn.btn-buy, .btn.btn-sell, .nav.nav-tabs.underlined, .nav.nav-tabs.nav-justified li a, .form-control, .sidebar > div, .navbar-search .input-group-btn .btn, .nav.nav-pills.nav-justified li a, .nav.nav-pills.nav-justified li.active a, .dropdown-menu, .rating-wrapper .rating-inner .rating-popover, .well, .message-create, .dropdown-menu-notifications .panel-notifications-list .panel-notifications-list-item, .dropdown-menu-notifications .panel-notifications-list li, .dropdown-menu-notifications .scrollable-list .panel-notifications-list-item, .dropdown-menu-notifications .scrollable-list li, .panel-notifications .panel-notifications-list .panel-notifications-list-item, .panel-notifications .panel-notifications-list li, .panel-notifications .scrollable-list .panel-notifications-list-item, .panel-notifications .scrollable-list li, .list-group-item, .startPage .btn-nordnet, .alert-info, .trade-heading, div#content div.twocol.box:first-child, select, optgroup, option, #cookies-warning-container, .btn-primary, #shareville-ticker, #center input, .fastSeekResultContainer input, #topSearch input.searchField, .bodyArea, .mcContent .headBar, div.svara{
    border-color: transparent !important;
}
body, button, div, dt, dl, dd, a, b, h1, h2, h3, h4, h5, h6, h7, input, textarea, label, li, p, s, select, option, small, span, table, th, td, blockquote, legend{
    box-shadow: none !important;
}
.position-list .position-header, .position-list ng-include .position-item, .nav.nav-tabs.nav-tabs-box.nav-justified li a, .top-panel, .stream-group .clean .panel, stream-group .clean .panel, .border-bottom, .panel-transaction-item, .rating-wrapper .rating-inner, .sub-panel, .panel-footer, .instrument-header, .instrument-info.sub-panel, .table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th, .message-item, .dropdown.list-filter.positions-filter, .order-body, div.fourcol.order-body, div#content div.twocol.box, div#content div.twocol.box, div.right-border, .half{
    border-color: #424242;
}
.stock-table .instrument-item, .dropdown-menu li, .dropdown.list-filter .dropdown-toggle:before{
    border-color: #212121;
}
ul.nav.nav-pills.nav-justified{
    border: 1px solid #424242;
}
/*	Round Edges	*/
.well, .navbar-nav>li>.dropdown-menu, .dropdown.list-filter .dropdown-menu{
    border-radius: 10px !important;
}
.col-md-4.sidebar, .highcharts-container > svg, ul.nav.nav-pills.nav-justified, .startPage .btn-nordnet, .btn-primary, .mcContent table tr:hover, .bttn a, input.large[type="reset"], input.large[type="button"], input.large[type="submit"]{
    border-radius: 5px !important;
}
.nav.nav-pills.nav-justified li:last-child a, .nav.nav-pills.nav-justified li:first-child a{
    border-radius: 0px !important;
}
.sidebar .nav.nav-pills li.active a, .sidebar .nav.nav-pills li:active a, .sidebar .nav.nav-pills li:hover a{
    border-radius: 5px 0px 0px 5px !important;
}
comment-text .comment-content .comment-text-expander{
    border-radius: 0px 0px 10px 10px !important;
}
/*	Filter       	*/
.profile-content .main-content .highcharts-container, path.highcharts-tracker, .graph-container > .highcharts-container{
    -webkit-filter: contrast(200%) invert(87%) grayscale(0) !important;
    filter: contrast(200%) invert(87%) grayscale(0) !important;
}
.sidebar .highcharts-container, i.icon.icon-dot.small, #center input, .fastSeekResultContainer input{
    -webkit-filter: contrast(1000%) invert(74%) grayscale(0) !important;
    filter: contrast(1000%) invert(74%) grayscale(0) !important;
}
/*	Hidden           	*/
.info-box, .banner-tip{
    display: none !important;
}
/*	Other           	*/
.rating-wrapper .rating-inner .rating-popover:after, .rating-wrapper .rating-inner .rating-popover:before{
    border-top-color: #424242 !important;
}
.dropdown-menu .arrow:after, .dropdown.list-filter .dropdown-toggle:after{
    border-bottom-color: #424242 !important;
}
.navbar-nav>li>.dropdown-menu, .dropdown.list-filter .dropdown-menu{
    border: 2px solid #212121 !important;
}
.stars-container .text-success{
    color: #CCCCCC !important;
}
ul.nav.nav-pills.nav-justified li:last-child{
    padding-right: 3px
}
ul.nav.nav-pills.nav-justified li:first-child{
    padding-left: 3px
}
.stars-container .text-muted{
    color: #383838 !important;
}
ul.nav.nav-pills.nav-stacked{
    margin-right: -16px
}
ul.nav.nav-pills.nav-stacked .btn-container{
    margin-left: -16px
}
.position-list .position-header{
    border-top: transparent;
    padding-top: 25px
}
.btn-primary{
    padding: 5px
}
/*	Scroll Bar        	*/
*::-webkit-scrollbar{
    width:14px !important;
}
*::-webkit-scrollbar-thumb{
    min-height:35px !important;
    background: #424242 !important;
    border: none !important;
    border-radius: 0px !important;
}
*::-webkit-scrollbar-track-piece{
    background-color: transparent !important;
    border: none !important;
}
}
