css:
/* Dark theme for Smartschool by localhost (V 1.0) */

@-moz-document regexp("https://.*smartschool.*") {
/* --login-- */
#authRightPane, #authRightPaneImage.spring {
    background: none;
}
#authLeftPane, #authLeftPane .smscContainer {
    background-image: none;
    background-color: #333333;
}
#authLeftPane #tipContainer, #authLeftPane #statusContainer {
    border: 2px solid #FF520A;
    box-shadow: none;
}
.status div, #schoolplace, #authRightPaneLogin label {
    color: #FFFFFF;
}
#authRightPaneLogin {
    background: none;
    background-color: #212020;
    border: 2px solid #FF520A;
    border-radius: 25px;
    box-shadow: none;
}

#authRightPaneLogin input {
    background-color: #333333;
    color: #FFFFFF;
    border: 1px solid #FF520A;
    border-radius: 0px
}

.smscButton, #authRightPaneOptions {
    border: none;
    background: #333333;
    color: #FFFFFF;
}
.smscButton:hover {
    color: #FF520A;
}

/* --main page-- */

/* header */
.smscTopLogo.spring {
    background-image: url('https://s26.postimg.org/mc9l77p1l/banner_black_grey.png');
}
#smscTopLogoLeftImg {
    display: none;
}
#topnavSpringBalloon {
    content: url('https://s26.postimg.org/teymkhk09/012056-firey-orange-jelly-icon-animals-animal-bi.png');
}
/* top nav */
.smscTopNav {
    background-image: none;
    background-color: #212020;;
    color: #FFFFFF;
    margin-bottom: 20px;
}
.smscTopNavRight .smscTopNavLink, .smscTopNavRight .smscTopNavLink:visited {
    color: #FFFFFF;
}
.smscTopNavRight .smscTopNavLink:hover, .smscTopNavRight .smscTopNavLink:active {
    color: #FF520A;
}
#smscSearch {
    background-image: url('https://s26.postimg.org/jx3gy6npl/search-24.png');
}
#smscNotify.red .smscNotify__bubble {
    background-image: url('https://s26.postimg.org/knw73yq2x/message-2-24.png');
}
#smscTopNavShowHide {
	background-image: url('https://s26.postimg.org/fqimiuo3t/menu-4-24.png');
}
#smscTopNavShowHide.active {
    background-image: url('https://s26.postimg.org/epidtq749/menu-4-16.png');
}
/* body */
.smscborder.centerxy, #leftcontainer, .smscborder.centery.bottomy.smallboxcb, .smscborder.centery.topy.smallboxct, .smscborder.bright.topr, .smscborder.bleft.topl, .smscborder.centerxy, .smscborder.centery.bottomy {
    background-color: #333333;
}

body, button, select, input, textarea {
    color: #FFFFFF;
}
.pns_menu_row:hover, .pns_menu_txt, .pns_menu_txt:hover {
    background-color: #333333;
   
}
.pns_menu_row {
    padding: inherit;
    height: auto;
}
.smscborder, .smscborder.centery.topy, .title.tcenter, .title.tright, .titleIdk, .title.tleft, .smscborder.centerxy {
    background-image: none;
}
.smscborder.bright.bottomr {
    display: none;
}
html, body, .courseButtonLeftSelected, .courseButtonLeft, .courseButtonLeftSelected {
    background-color: #333333;
}
.title.tcenter span center {
    color: #FFFFFF;
    border-left: 2px solid #FF520A;
    border-right: 2px solid #FF520A;
    background-color: #212020;
}
.courseButtonLeft, .courseButtonLeft#button1, .courseButtonLeftSelected, .courseButtonLeftSelected#button1, .courseBlock_folder {
    background-image: url('https://s26.postimg.org/qtxnapk09/folder-24.png');
}
.courseButtonLeft#button3, .courseButtonLeftSelected#button3 {
    background-image: url('https://s26.postimg.org/ql02ks77t/folder-3-24.png');
}

.ssLink, a, .pns_tab, .ssLink:hover, .smsc-column-nav__button, .smsc-column-nav__button:hover, .pns_tab.bgcenter_sel {
    color: #FF520A;
}
.pns_tab {
    height: 0px;
}
a.pns_title_text_news {
    color: #FFFFFF;
}
.smscborder.centery.bottomy.smallboxcb, .smscborder.centery.topy.smallboxct, .courseNav, .smscborder.centerxy, .smscborder.centery.bottomy {
   border: none;
}
/* administration */
.smsc-column-nav__button--mydoc {
    background-image: url('https://s26.postimg.org/kk7fktqx5/upload-24.png');
}
.smsc-column-nav__button--portfolio {
    background-image: url('https://s26.postimg.org/9ynk8tkll/full-folder-24.png');
}
.smsc-column-nav__button--timetable {
    background-image: url('https://s26.postimg.org/hfwro1a4p/time-8-24.png');
}
.smsc-column-nav__button--enquete {
    background-image: url('https://s26.postimg.org/409qyl1mx/bar-chart-5-24.png');
}
.smsc-column-nav__button--photos {
    background-image: url('https://s26.postimg.org/kcjsobfyh/camera-4-24.png');
}
.smsc-column-nav__button--studentcard {
    background-image: url('https://s26.postimg.org/ub4raspe1/user-24.png');
}
.smsc-column-nav__button--lvs {
    background-image: url('https://s26.postimg.org/68nxfx8qx/user-5-24.png');
}
.smsc-column-nav__button--wiki {
    background-image: url('https://s26.postimg.org/qhbb1n821/edit-6-24.png');
}
.smsc-column-nav__button--schoolagenda {
    background-image: url('https://s26.postimg.org/szx02bts9/agenda-24.png');
}
.smsc-column-nav__button--publishers {
    background-image: url('https://s26.postimg.org/dsh0hz1xl/baggage-24.png');
}
#bibnetQuery {
    background-color: #333333;
    border: 1px solid #FF520A;
}

/* messages */
.divSubjectI, .msgContentVal, .msgHeaderVal, .msgHeaderOpt, #msglist, #duplicateheaders {
    color: #000000;
}
}
