css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("grand-mine.ru") {
.node35 {
    background: #030303;
}
.footer {
    background: url(http://image.prntscr.com/image/dcaa5ec69d984c829512bbdd21779b91.jpg) no-repeat center 0;
    background-color: transparent;
    background-repeat: no-repeat;
    background-attachment: scroll;
    background-clip: border-box;
    background-origin: padding-box;
    background-size: auto auto;
}
.f1 {
    background: #030303 url(http://image.prntscr.com/image/dcaa5ec69d984c829512bbdd21779b91.jpg) no-repeat center 0;
}
.footer a {
    color: #FFFFFF;
    padding: 10px;
}
.footer a:hover {
    color: red;
}
.footerLegal,
.footerLegal a,
.footerLegal .pairsInline dt,
#copyright {
    color: red;
    text-shadow: none;
    font-weight: 600;
}
#header .pageWidth .pageContent {
    background: url(http://image.prntscr.com/image/8847bc94a6b1452b9a8313d1aaffa218.jpg);
}
#headerMover #headerProxy {
    background: #030303 url(http://image.prntscr.com/image/8847bc94a6b1452b9a8313d1aaffa218.jpg) no-repeat center 0;
    height: 400px;
}
.navTabs {
    background: #030102;
    font-weight: bold;
    font-size: 11px;
    padding-top: 0;
    padding-bottom: 0;
    height: 40px;
    border: 2px solid #FFFFFF;
}
.navTabs .navTab.PopupClosed a.navLink {
    color: red;
}
.navTabs:hover .navTab.PopupClosed:hover a.navLink:hover {
    color: red;
    background: black;
}
.navTabs .navTab.selected a.navLink,
.navTabs ul.visitorTabs > li.navTab.selected a.navLink {
    background: #0D1613;
    padding-bottom: 2px;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    text-shadow: rgba(255, 255, 255, 0.7) 0px 0px 5px;
    box-shadow: inset rgba(0, 0, 0, 0.6) 0px 1px 4px;
    position: relative;
}
.navTabs .navTab.selected .tabLinks {
    border: 2px solid #FFFFFF;
    background: #030303;
}
.navTabs .navTab.selected .tabLinks a:hover {
    color: red;
    background: red;
}
.navTabs .navTab.selected .tabLinks a {
    color: #FFFFFF;
}
.mainContainer {
    float: left;
    margin-right: -260px;
    width: 100%;
    background: #030303;
}
.breadcrumb {
    font-size: 11px;
    background-color: #030102;
    border: 1px solid #FFFFFF;
    border-radius: 3px;
    overflow: hidden;
    max-width: 100%;
    box-shadow: rgba(0, 0, 0, 0.07) 0px 1px 3px, inset rgba(255, 255, 255, 0.75) 0px 1px 0px;
    height: 30px;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: red;
}
.breadcrumb .crust {
    display: block;
    float: left;
    position: relative;
    max-width: 50%;
    background: #030102;
    color: red;
}
.breadcrumb a {
    text-shadow: none;
    color: black;
}
.sidebar .secondaryContent {
    border-radius: 1px;
    background: #FFC615;
}
.avatar img,
.avatar .img,
.avatarCropper {
    background-color: #FFFFFF;
    padding: 2px;
    border: 1px solid black;
}
[dir="auto"] {
    text-align: left;
    color: red;
}
.sidebar .visitorPanel .username {
    font-weight: bold;
    font-size: 20pt;
    color: red;
    font-family: "Viner Hand ITC";
}
.pairsJustified dt {
    float: left;
    max-width: 100%;
    margin-right: 5px;
    color: #FFFFFF;
}
.pairsJustified dd {
    float: right;
    text-align: right;
    max-width: 100%;
    color: red;
}
.secondaryContent a {
    color: red;
}
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    font-weight: bold;
    color: red;
    background: black;
    padding: 8px;
    margin: -10px -10px 10px;
    border-bottom: 1px solid rgb(201, 223, 235);
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    text-shadow: none;
}
.section {
    margin: 10px auto;
    border: 1px solid red;
}
.sidebar .section {
    box-shadow: none;
    color: red;
}
.sidebar .secondaryContent {
    border: 1px solid #FFFFFF;
    background: #082733;
}
h3 {
    color: red;
}
.sidebar .section .secondaryContent .footnote,
.sidebar .section .secondaryContent .minorHeading {
    color: #FFFFFF;
    margin-top: 5px;
    font-weight: bold;
}
hr {
    color: red;
}
.minecraftServer > .info > .prefix.prefixOnline {
    color: black;
    background-color: #FFFFFF;
    width: 45px;
    text-align: center;
    font-weight: bold;
}
.nodeList .categoryStrip {
    font-size: 11px;
    color: rgb(54, 83, 99);
    background: #030102;
    border: 1px solid red;
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    border-bottom: 1px solid rgb(232, 241, 245);
    border-left: 0px solid #fafcfa;
    padding: 0;
    background: #030102;
}
.nodeList .categoryStrip .nodeTitle a {
    color: red;
}
.node .nodeDescription {
    margin-top: 2px;
    color: #FFFFFF;
}
a:link,
a:visited {
    color: #8FB4C6;
    text-decoration: none;
}
.idxStats dd {
    display: block;
    color: #FFFFFF;
    font-size: 18px;
    padding-bottom: 3px;
}
.pairsJustified dt {
    float: left;
    max-width: 100%;
    margin-right: 5px;
    color: #FFFFFF;
    font-weight: bold;
}
.breadcrumb .crust a.crumb > span {
    display: block;
    text-overflow: ellipsis;
    word-wrap: normal;
    white-space: nowrap;
    overflow: hidden;
    max-width: 100%;
    color: black;
}
.nodeList .categoryStrip {
    font-size: 11px;
    color: rgb(54, 83, 99);
    background: #030102;
    border: 2px solid #FFFFFF;
}
.messageUserBlock {
    background-color: #082733;
    border-radius: 6px;
    position: relative;
    border: 2px solid black;
}
.messageUserInfo {
    float: left;
    width: 124px;
    background-color: #082733;
    border: 2px solid black;
}
.messageUserBlock div.avatarHolder {
    background-color: #082733;
    padding: 10px;
    border-radius: 4px;
    position: relative;
    border: 2px solid black;
}
.messageUserBlock .extraUserInfo {
    font-size: 10px;
    background-color: #082733;
    padding: 4px 6px;
    border-radius: 4px;
    border: 2px solid black;
}
.pageWidth {
    padding-right: 5px;
    padding-left: 5px;
    margin: 0 auto;
    _width: 976px;
    max-width: 1200px;
    box-sizing: border-box;
}
#content .pageContent {
    background: #030303;
    padding: 6px 12px 2px;
    box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 6px;
    min-height: 370px;
}
#content {
    background: #030303;
}
#logoBlock {
	background: #030303;
}
#navigation {
	background: #030303;
}
.Menu .menuHeader {
    overflow: hidden;
    zoom: 1;
    background: #030303;
    border: 2px solid #FFFFFF;
}
.Menu .secondaryContent {
    background-color: #030303;
    border: 2px solid #FFFFFF;
}
.Menu .sectionFooter {
    background-color: #030303;
}
.memberListItem .userStats dt, .memberListItem .userStats dd {
    white-space: nowrap;
    color: #FFFFFF;
}
.memberListItem:hover {
    padding: 15px;
}
.memberListItem {
    overflow: hidden;
    zoom: 1;
    background-color: #030303;
}
.subHeading {
	background-color: #030303;
    color: #FFFFFF;
}
.pairsJustified dt {
    float: left;
    max-width: 100%;
    margin-right: 5px;
    color: #8FB4C6;
    font-weight: bold;
}
.pairsJustified dd {
    float: right;
    text-align: right;
    max-width: 100%;
    color: #8FB4C6;
}
    .discussionListItem {
    border-bottom: 1px solid rgb(232,241,245);
    border-left: 0px solid #fafcfa;
    background: #FFFFD8;
}
}
