css:
@-moz-document domain("www.zeit.de") {
html,
body,
.header,
.nav__tags,
.nav__date,
.nav__dropdown-list,
.buzz-box__heading,
.zg-wiegehtesihnen,
.jobbox__container,
.jobbox__kicker,
.jobbox__title,
.jobbox__byline,
.video-stage,
.video-stage-secondary,
.video-stage-footer,
.footer,
.footer-publisher__row {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.main,
.parquet-meta,
.search__input,
.search__button,
.teaser-small__kicker,
.teaser-small__title,
.teaser-small__text,
.teaser-small__byline,
.teaser-small__datetime,
.teaser-large__kicker,
.teaser-large__title,
.teaser-large__text,
.teaser-large__byline,
.teaser-large__datetime,
.teaser-buzz__kicker,
.teaser-buzz__heading,
.teaser-buzz__title,
.teaser-buzz__metadata,
.print-box__heading,
.print-box__kicker,
.print-box__title,
.print-box__text {
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
    border-radius: 0 !important;
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}

@media screen and (min-width: 48em) { 
    .footer-publisher {
    	padding-bottom: 0! important;                                        
    }
}

.footer {
 	margin-top: 0 !important;   
}
}
