css:
@-moz-document domain("www.faz.net") {


html,
body,
.redBackground,
#dpa_body,
.backgroundColorHighlight,
.dpa_content-menu-btn,
.dpa_content-menu-flyout-content,
.stickyWrapper,
#FAZSlimHeaderInner,
#nat-ca,
#nat-border,
.PlusAnnouncementBuehnenModule,
.PlusAnnouncementBuehneTeaserWrapper,
.PlusAnnouncementBuehneTeaserInner,
.BuehnenModul,
.lst-Comments_CommentBox,
.atc-Intro,
.gf-Wrapper,
.gf-Wrapper_Inner {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

#FAZSeite,
.Editors,
.EpaperLinks li,
.HeaderRowShadow,
.HeaderRowTop,
.HeaderRowTop ul,
#EventBox .active,
.RessortNaviOuter,
.RessortNaviOuter ul,
#nav>li.Selected>a,
#nav>li:hover>a,
.LiveTime,
.TickerItem,
.TeaserText,
.LinkMehr,
h2,
.Copy,
.Datum,
.FAZArtikelText p, 
.o-ModuleWrapper,
.tsr-Top1_TeasersWrapper,
.gh-Wrapper,
.gh-MainNav,
.tsr-Comment,
.tsr-Lead_TextWrapperOuter,
div.vwd_we_faz_c004 div.kursticker,
div.vwd_we_faz_c004 div.contentBox {
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.to-remove-classes {
 	display: none !important;
}

a,
.EventHead,
.Headline,
.teaserInner.linkable .LinkMehr {
	color: hsl(0, 0%, 90%) !important;
}

a:hover,
.EventButton a:hover *,
.teaserInner.linkable.hovered .Headline,
.teaserInner.linkable.hovered .Stichwort,
.teaserInner.linkable.hovered a span.Headline,
.Teaser a.TeaserHeadLink:hover *,
.teaserInner.linkable.hovered .LinkMehr {
	color: hsl(0, 0%, 70%) !important;
    text-decoration: none !important;
}

.a.mmoLink:after {
	color: #FFF;
}



}
