css:
@-moz-document domain("www.spiegel.de") {
html,
body,
.hamburger-icon,
.ressort-teaser-box,
.fbtickerboxhpcontent,
#tickerContent,
#splt_navigation,
#matchplanMatchesView,
#matchplanSwitches,
#matchplanMatchesViewTableHead,
#matchplanMatchesViewRowHead,
#matchplanMatchesViewTable {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

#wrapper-content,
.header-top,
.js-main-navigation,
.js-channel-sub-nav {
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

a,
.fbtickerboxhp .fbtickerboxhpcontent table td.ergebnis {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}


.hamburger-icon:before {
 	border-bottom: 4px solid hsl(0, 0%, 50%) !important;
    border-top: 4px solid hsl(0, 0%, 50%) !important;
    
}

.beendet:hover,
.heute:hover {
 	background-color: hsl(0, 0%, 20%) !important;  
}
}
