css:
@-moz-document domain("www.lefigaro.fr") {


html,
body,
.figh-nielsen__list,
.figh__full,
.figh-collapse--with-abo,
.figh-treenode__level,
.figh-footer__list,
.fig-widget,
.fig-wid-inner,
.fig-side-col,
.fig-2cols,
.fig-conversation,
.fig-colsAD,
#auteur,
.fig-wg__wrapper,
.fig-page__col-right,
.fig-page__container,
.fig-wg__head,
.fig-wg__footer,
.figh-abo-container,
.figh-nielsen__scroll-container,
.unlogged,
.flashActuItem,
#fig-footer-gly,
.fig-auteur-row,
.fig-profil-live-bordure,
.fig-community-actions {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.figh-collapse__nav-btn,
.figh-menu-item,
.figh-treenode,
.fig-encemoment-container,
.fig-hp-even-une,
.fig-profils,
.fig-ensemble,
.fig-wid-list-h li,
.fig-profil,
.fig-container,
.fig-profil-chapo,
.fig-secondary-background,
.fig-tabs-nav,
.fig-mod-title,
.fig-breadcrumb,
.fig-main-title,
.fig-list-articles,
.fig-profile,
.fig-wgprofile,
.fig-wg__tabs-item,
.fig-profile__chapo,
.fig-wgprofile__chapo,
#fig-pagination-nav li a,
.fig-metas,
.fig-tabs-nav li a {
    background: none !important;
    background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.to-remove-classes {
 	display: none !important;
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}

.fig-wid-list-h .active,
.fig-wg__tabs-item--active,
#fig-pagination-nav .fig-pagination-current a,
.fig-tabs-nav .current a {
     background: none !important;
	background-color: hsl(0, 0%, 20%) !important;
    color: hsl(0, 0%, 80%) !important;
}

.fig-content,
.fig-comment__msg {
    color: hsl(0, 0%, 80%) !important;
}

}
