css:
@-moz-document domain("www.nytimes.com") {
html,
body,
.masthead,
.navigation,
.secondary-container,
.navigation-modal,
.mini-navigation,
.mini-navigation-menu,
.search-flyout-panel,
.masthead-cap,
.editions,
.weather-button,
.weather-modal,
.markets-button,
.markets-modal,
.date,
.button-text,
#navigation-edge,
.collection-menu,
.navigation-panel,
.navigationModal,
.subnavigation ol,
.button,
.tabs .selected,
.sharetools-menu  {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.portal-posts-list,
.mCSB_container ul li,
.bcColumn,
.cColumn,
.contentNavigation,
.columnGroup,
.ribbon-page-navigation,
.panel-content,
.comments-header-container,
.comment-text {
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}

.collection-menu h2,
.trending-module span {
    color: hsl(0, 0%, 80%) !important;
}

.sharetools-menu {
 	box-shadow: none !important;   
}
}
