css:
@-moz-document domain("xmovies8.ru") {
body, .info_movie, .block_movies.bg_color, .title_cat p, .breadcrumb>.active { background: #2b2b2b !important; color: #bfbfbf !important; }
#bread, .watch_info { color: #ccc !important; background: transparent !important; }
.container { width: 1280px; padding: 0px !important; }
.popover { color: #333 !important; }
.block_profile .nav-tabs>li a:focus, .block_profile .nav-tabs>li a:hover { color: #CD4D45 !important; }
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover { border: 0 !important; }
.block_title, .list_ep { border-color: #383838 !important; margin-top: 10px !important; }
#bar-player, #header { background: #222 !important; }
.ep_link a:hover, .ep_link a.active, .ep_link a:active, .ep_link a:focus { background: #e62117 !important; }
.full.excerpt, .tit.full { margin-left: -10px !important; }
.block_title .title { margin-bottom: 10px !important; }
.watch_info { margin-bottom: 10px !important; }
.info_movie, .info_movie .col-xs-12 { padding-left: 0px !important; padding-right: 0px !important; }
.alert, .footer_detail, .specs_left.full.btn-, .full.block_title.title_cat.has_filter.ads, #block_cmt, .rating.full.mv-rating, .keyword_widget, #footer, #main_menu>li:last-child, #info_bottom_slider, .jw-dock-button.jw-background-color.jw-reset.jw-sharing-dock-btn, .full.block_movies.block_season, .info_movie .desc .share, #showcomment { display: none !important; }
.info_movie .desc { padding-right: 0px !important; border-right: none !important; width: 146% !important; }
.block_movies { padding-top: 0px !important; padding-bottom: 0px !important; }
.block_title .more { top: 0px !important; }
#main_menu { text-align: left !important; }
.full.detail_content, .main-detail { width: 1280px !important; }
.jw-skin-seven .jw-text-duration { color: #fff !important; }
.jw-skin-seven .jw-background-color { background: transparent !important; }
.jw-skin-seven .jw-progress, .jw-skin-seven .jw-icon-tooltip .jw-active-option, .pagi ul li.c-active a, .pagi ul li.c-active span { background: #e62117 !important; }
.jw-skin-seven .jw-controlbar, li.jw-text.jw-option:hover { border-top: none !important; background: rgba(0, 0, 0, 0.3) !important; }
.ovf_watch { background: #000 !important; }
h1, h2, h3, h4, h5, h6, .block_title .title a { color: #fff !important; }
.cluetip-inner .inner .title h1 { color: #e62117 !important; }
.full.pagi { padding-bottom: 15px; }
.info_movie .desc .tit h1 { font-size: 25px !important; margin-bottom: 5px !important; }
}
