css:
/* i really want this to be global */
@-moz-document url-prefix("https://keep.google.com/") {
:root {
	--headerColor: #212121;
    --bodyColor: #424242;
    --noteColor:  #333333;
    --mainText: #ecf0f1;
    --sidebarText: #ecf0f1;
    --sidebarIcons: #ecf0f1;
}

/* Body */
body {
	background-color: var(--bodyColor) !important;
}

/* Header */
.gb_td gb_Qd gb_Rdt {
    background-color: var(--headerColor) !important; 
}

/* Sidebar text + header text at reminders */
.gb_jc, .gb_cc, .Q0hgme-fmcmS-LgbsSe,
.neVct-LS81yb-tJHJj {
	color: var(--sidebarText);
}

.Q0hgme-fmcmS-LgbsSe:not:hover,
.Q0hgme-fmcmS-LgbsSe:not:focus {
	color: var(--sidebarText);
}

/* Sidebar icons*/
.gb_pc svg, .gb_cc svg {
	color: var(--sidebarIcons);
}

/* Pin icon*/
.IZ65Hb-nQ1Faf svg {
	fill: var(--sidebarIcons);
}

/* Take a note icons and note icons */
.h1U9Be-xhiy4 .RmniWd-rymPhb,
.h1U9Be-xhiy4 .RmniWd-HiaYvf-h1U9Be,
.h1U9Be-xhiy4 .RmniWd-nA1mMd-h1U9Be {
	-webkit-filter: invert(100%);
	filter: invert(100%);
}

/* Note text */
.IZ65Hb-WsjYwc-nUpftc .rTEl-SX9D7d-yaNpec, 
.h1U9Be-YPqjbf,
.IZ65Hb-WsjYwc-nUpftc .rTEl-SX9D7d-r9oPif,
.IZ65Hb-WsjYwc-nUpftc .rTEl-SX9D7d-Y5a8lc,
.IZ65Hb-WsjYwc-nUpftc .rTEl-SX9D7d-u2z5K,
.IZ65Hb-YPqjbf.LwH6nd,
.neVct-Ne3sFf-fmcmS {
 	font-size: 14px;
    font-weight: 500;
    color: var(--mainText)
}

/* Note text top padding */
.XKSfm-L9AdLc .h1U9Be-YPqjbf {
	padding-top: 12px;
}

/* Tags and time */
.IZ65Hb-x00ATb .Q0hgme-fmcmS-LgbsSe,
.IZ65Hb-Tswv1b-SmKAyb {
	color: var(--mainText)
}

/* Note Title */
.r4nke-YPqjbf {
	border-bottom: 1px solid #000;
}

/* Note */
.IZ65Hb-TBnied {
	background: var(--noteColor) !important;
}

/* Note footer Icons */
.IZ65Hb-INgbqf .zyxPWd,
.IZ65Hb-INgbqf .euCgFf,
.IZ65Hb-INgbqf .VsHK1d, 
.IZ65Hb-INgbqf .JqEhuc, 
.IZ65Hb-INgbqf .xl07Ob,
.IZ65Hb-INgbqf .Ge5tnd-HiaYvf {
	-webkit-filter: invert(100%);
	filter: invert(100%);
}

/* Hover over buttons */
.Q0hgme-fmcmS-LgbsSe:not(.VIpgJd-LgbsSe-OWB6Me):focus, .Q0hgme-fmcmS-LgbsSe:not(.VIpgJd-LgbsSe-OWB6Me):hover {
    color: var(--mainText);
}

/* Reminder time text */
.IZ65Hb-zyxPWd-bVEB4e {
	color: var(--mainText);
}

/* Credit to dahlskebank for the colors below */

/* COLOR NOTE RED */
.IZ65Hb-TBnied[style^='background-color: rgb(255, 138, 128)'] .IZ65Hb-YPqjbf.r4nke-YPqjbf {
	background: #61140B !important;
	color: var(--mainText);
}

/* COLOR NOTE ORANGE */
.IZ65Hb-TBnied[style^='background-color: rgb(255, 209, 128)'] .IZ65Hb-YPqjbf.r4nke-YPqjbf {
