css:
@-moz-document url-prefix("http://review.") {
/* View Diff CSS */

/* Upper part of review board */
.review-request {
    background-color: #222;
}

/* diff Containers */
.diff-container {
    background-color: #002800;
    margin-bottom: 1em;
    position: relative;
}

/* *** white space header bar  *** */
.sidebyside tbody.whitespace-file td {
    background: #575012;
    padding: 1em;
}
.sidebyside thead th {
  background: #222;
  font-size: 120%;
  font-weight: bold;
  text-align: left;
  text-overflow: ellipsis;
}

/* *** Diff normal back ground color *** */
.sidebyside {
    background: #141414;
    border-collapse: collapse;
    padding: 2px;
    width: 100%;
    margin-bottom: -1px;
}
.sidebyside tbody tr th {
  background: #222;
  padding-left: 1em;
  padding-right: 4px;
  text-align: right;
  vertical-align: top;
  cursor: pointer;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -khtml-user-select: none;
  -moz-user-select: none;
  -moz-user-select: -moz-none;
  -ms-user-select: none;
  user-select: none;
  /* These must be defined as two rules, or Firefox will drop them both. */
}

/* File name header */
.sidebyside thead .filename-row th {
    overflow: hidden;
    padding: 8px;
    background: black;
    color: white;
}

/* *** Insert color *** */
.sidebyside tbody.insert tr th {
  background: #062314;
  border-color: #bdd8b7;
}

.sidebyside tbody.insert tr td {
  background: #062314;
  border-color: #cfeac8;
}

/* *** Replace color *** */
.sidebyside tbody.replace tr td {
  background: #282809;
  border-color: #e3e57f;
}
.sidebyside tbody.replace tr th {
  background: #282809;
  border-color: #d5d69d;
}
.sidebyside tbody.replace .hl {
  background: black;
}

/* *** Delete color *** */
.sidebyside tbody.delete tr td {
  background: #33060d;
  border-color: #edcfd3;
}
.sidebyside tbody.delete tr th {
  background: #33060d;
  border-color: #d6ccce;
}

/* The text color should not be white, it may be too dark */
 body {
  background-color: black;
  color: #C5C8C6;
  font-family: Verdana, Arial, Helvetica, Sans-Serif;
  font-size: 8pt;
  margin: 0;
  padding: 0;
} 

/* Diff unexpanded header */
.sidebyside tbody.diff-header a {
    text-decoration: none;
    color: white;
}

.sidebyside tbody.diff-header td {
  background: #4d3b24;
  font-size: x-small;
  text-align: center;
}
.sidebyside tbody.diff-header th {
  background: #4d3b24;
}

.sidebyside a {
    color: white;
    text-decoration: none;
}

/* Test area rich text */
.review-request .content .field-container > pre {
  white-space: pre-wrap;
  /* CSS 3 */
  white-space: -moz-pre-wrap;
  /* Mozilla, 1999+ */
  white-space: -pre-wrap;
  /* Opera 4-6 */
  white-space: -o-pre-wrap;
  /* Opera 7 */
  word-wrap: break-word;
  /* IE 5.5+ */
  background-color: #1D1F21;
  border: 1px #b8b5a0 solid;
  margin: 5px 0 0 0;
  padding: 10px;
}

/* Review people name */
.review-request #review_request_details a {
    color: #f0f0f0;
    text-decoration: none;
}

/* Table data in the upper section */
#diff_index table td,
.diff-index table td {
  background: #1D1F21;
  padding: 0.6em;
  margin: 0;
  border-top: 1px #DDD solid;
  vertical-align: top;
}

/* Diff link index */
#diff_index table td a, .diff-index table td a {
    color: #f0f0f0;
    text-decoration: none;
}

/* Page number color */
.paginate-link, .paginate-current {
    background: #654d21;
    color: white;
    padding: 2px 4px;
    border: 1px solid #dddddd;
}

/* Expand changes, show extra whitespace */
.review-request .controls a {
  color: white;
  text-decoration: none;
}
.review-request .controls a:hover {
  color: #0000C0;
}
.review-request .controls {
  background:#222;
  list-style: none;
  margin: 0;
  padding: 0 8px 4px 8px;
  text-align: right;
}


/* Main review board backgournd */
.review-request .main {
  background: #222;
  clear: both;
  padding: 0;
}

/* Download, Review, Ship it */
.actions a {
    color: white;
    cursor: pointer;
    display: block;
    text-decoration: none;
    padding: 6px 10px;
    border-left: 1px #957d2c solid;
}
.actions-container {
    background: #222;
    border-bottom: 1px #957d2c solid;
    float: right;
    width: 100%;
    -moz-border-radius: 5px 5px 0 0;
    -webkit-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
}
.actions li.primary {
    background-color: #222;
}

/* File path  */
.sidebyside thead .filename-row th {
  overflow: hidden;
  padding: 8px;
  background-color: #222;
  color: white;
}

/* Comment section */
.comment-dlg .other-comments ul {
    background-color: #222;
    border: 1px #333333 solid;
    list-style: none;
    margin: 6px 0 0 0;
    overflow: auto;
    padding: 0;
    position: relative;
}

/* Open issue */
.issue-state.open {
    background: #6b600d;
    border: 1px solid #cfb800;
}

/* Reviewboard Status */
.review-request #status {
    color: #f0f0f0;
    margin: 5px 0;
}

/* discard and submit */
#discard-banner,
#submitted-banner {
  background: #575012;
  border: 1px #808080 solid;
  -moz-border-radius: 6px;
  -webkit-border-radius: 6px;
  border-radius: 6px;
  -moz-box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.15);
  -webkit-box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.15);
  box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.15);
}

/* Navigation Bar */
#navbar {
    background: #335f8e;
    border-bottom: 1px #3465a4 solid;
    list-style: none;
    margin: 0;
    min-height: 1.5em;
    padding: 0.5em;
    padding-left: 72px;
}
#navbar a {
    color: #f0f0f0;
    font-size: 110%;
    text-decoration: none;
}

/* Comments Windows */
.comment-dlg form {
    background-color: #18270a;
    display: block;
    padding: 6px;
}

/* Side comment bar */
.commentflag .commentflag-inner {
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    border-radius: 4px;
    opacity: 0.95;
    filter: alpha(opacity=95);
    -moz-opacity: 0.95;
    background-color: #2d598b;
    border: 1px solid #204a87;
    bottom: 0.2em;
    cursor: pointer;
    font-size: 90%;
    font-weight: normal;
    left: 0;
    line-height: 1.7em;
    position: absolute;
    right: 0.2em;
    text-align: center;
    top: 0;
    vertical-align: top;
    width: 100%;
    z-index: 20;
}

/* All variable in diff */
.nv, .dec, .var {color: #C5C8C6;} /* Name.Variable */
.nc { color: #C5C8C6 } /* Name.Class */
.nl { color: #C5C8C6 } /* Name.Label */
.nn { color: #C5C8C6 } /* Name.Namespace */
.nf, .fun { color: #C5C8C6 } /* Name.Function */
.o { color: #C5C8C6; } /* Operator */
.c, .com, /* Comment */
.cm, /* Comment.Multiline */
.c1 /* Comment.Single */
{ color: #72a0d4; font-style: italic }
.m, .lit { color: #a24d0a; font-weight: bold; } /* Literal.Number */
.s, .str, .atv { color: #a24d0a; } /* Literal.String */
.nd { color: Blue; font-weight: bold} /* Name.Decorator */
.mf { color: #a24d0a; font-weight: bold } /* Literal.Number.Float */
.mh { color: #a24d0a; font-weight: bold } /* Literal.Number.Hex */
.mi { color: #a24d0a; font-weight: bold } /* Literal.Number.Integer */
.mo { color: #a24d0a; font-weight: bold } /* Literal.Number.Oct */
.na, .atn { color: #8686dc } /* Name.Attribute */
.nt, .tag, .tag {
    color: #8686dc;
    font-weight: bold;
}
.sd { color: #8686dc; } /* Literal.String.Doc */
.c1 /* Comment.Single */
{ color: #8686dc; font-style: italic }
.cp {
    color: #a24d0a;
}


/* First page CSS */

/* Different Review request changed boxes */
.box-main {
    background: #220;
    padding: 5px;
}

/* Change Summary section */
.changedesc .body > pre {
    background-color: #5d540e;
    border: 1px #b8b5a0 solid;
    padding: 10px;
    font-size: 9pt;
    white-space: pre-wrap;
    white-space: -moz-pre-wrap;
    white-space: -pre-wrap;
    white-space: -o-pre-wrap;
    word-wrap: break-word;
}

/* Issue Summary section */
#issue-summary-table .no-issues td {
    background-color: #5d540e;
    border-color: #999;
    border-width: 1px;
}
#issue-summary .header .counters .counter label {
    color: #f0f0f0;
    font-weight: normal;
    margin-right: 3px;
}

/* Testing Done */
.changedesc .body .diffed-text-area .delete .line {
  background: #4c0511;
}
.changedesc .body .diffed-text-area .delete .marker {
  color: #990000;
  background: #4c0511;
}

.changedesc .body .diffed-text-area .replace-old .line {
  background: #4c0511;
}
.changedesc .body .diffed-text-area .replace-old .marker {
  background: #4c0511;
}
.changedesc .body .diffed-text-area .insert .line {
  background: #062314;
}
.changedesc .body .diffed-text-area .insert .marker {
  background: #062314;
}

/* Head of review box */
.review .header a {
  color: white;
  text-decoration: none;
}

.changedesc .body h3.status .value {
  color: white;
  font-weight: normal;
}

.review .comments .filename a, .review .review-comments .filename a {
    color: #f0f0f0;
    font-weight: bold;
}

/* First page review board box */
.review .body {
    background-color: #1D1F21;
    border: 1px #AAAAAA solid;
    clear: both;
    margin: 5px;
    padding: 10px;
}

/* Issue Resolved */
.issue-state.resolved {
    background: #575012;
    border: 1px solid #e1e1e1;
}

.review .shipit {
    background: #2c4217;
    border: 1px #888888 solid;
    font-weight: bold;
    left: -10px;
    margin-bottom: 8px;
    padding: 4px 8px;
    position: relative;
    width: 4.5em;
    top: -8px;
    -moz-border-radius: 6px 0 6px 0;
    -webkit-border-radius: 6px 0 6px 0;
    border-radius: 6px 0 6px 0;
}

.changedesc .body .diffed-text-area {
    background: #222;
    border: 1px #b8b5a0 solid;
    border-collapse: collapse;
    padding: 0;
    width: 100%;
}

.changedesc .body .diffed-text-area .replace-new .line {
  background: #062314;
}
.changedesc .body .diffed-text-area .replace-new .line .hl {
  background: #062314;
}
.changedesc .body .diffed-text-area .replace-new .marker {
  background: #062314;
}

.issue-state.dropped {
    background: #6b600d;
    border: 1px solid #b9b9b9;
}

.review .controls a {
    color: white;
    text-decoration: none;
}

#issue-summary-table .issue.open td {
    background-color: #27250f;
    border-color: #a89c00;
}

#issue-summary-table .issue .summary-anchor {
    color: white;
    display: block;
    overflow: hidden;
    text-decoration: none;
}

.changedesc .body .diffed-text-area .marker {
    background: #222222;
    font-family: monospace;
    font-size: 13px;
    font-weight: bold;
    text-align: center;
}

.changedesc .body .changed td {
    background: black;
}

.changedesc .body .changed a {
    color: #f0f0f0;
    text-decoration: none;
}

.changedesc .body a {
    color: white;
    text-decoration: none;
}

.changedesc .body .changed .new-value .marker {
  background: #062314;
}
.changedesc .body .changed .old-value .marker {
  background: #4c0511;
}

/* Review Board Dash Board */
#dashboard-wrapper {
    background-color: #220;
    position: relative;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
}
#dashboard-navbar li a {
    color: white;
    text-decoration: none;
    padding: 0;
}
#dashboard-navbar li .count {
  position: absolute;
  top: 0.4em;
  right: 0.8em;
  background: #481313;
  border: 1px #d0d0d0 solid;
  font-size: 90%;
  padding: 0px 8px;
  margin-top: 0px;
  -moz-border-radius: 12px;
  -webkit-border-radius: 12px;
  border-radius: 12px;
}
#dashboard-navbar li .count.count-zero {
  color: white;
  background: #481313;
  border-color: #dcdcdc;
}

dashboard-navbar li.has-url:hover .datagrid-sidebar-row, #dashboard-navbar li.active .datagrid-sidebar-row {
    background: #220;
}

.datagrid-title, .datagrid-titlebox {
    background: #22354a !important;
    -moz-border-radius: 6px 6px 0 0;
    -webkit-border-radius: 6px 6px 0 0;
    border-radius: 6px 6px 0 0;
}

.comment-dlg .other-comments ul li.even {
    background-color: #222;
}

/* Table data dashboard display */
.datagrid tr.even {
    background-color: #220;
}
.datagrid tr {
    background-color: black;
}
.datagrid td a {
    color: #C5C8C6;
    text-decoration: none;
}

/****************************************************************************
 * Column-specific classes
 ****************************************************************************/
.datagrid .age1 {
  background-color: #2c6d29;
  border-left: 1px #415b29 solid;
  border-right: 1px #8bbd5c solid;
  white-space: nowrap;
}
.datagrid .age1:hover {
  background-color: #a1cb9f;
}
.datagrid tr.even .age1 {
  background-color: #32992d;
}
.datagrid .age2 {
  background-color: #ddfa8e;
  border-left: 1px #a3e266 solid;
  border-right: 1px #a3e266 solid;
  white-space: nowrap;
}
.datagrid .age2:hover {
  background-color: #5f7523;
}
.datagrid tr.even .age2 {
  background-color: #627a23;
}
.datagrid .age3 {
  background-color: #615a1d;
  border-left: 1px #d8c158 solid;
  border-right: 1px #d8c158 solid;
  white-space: nowrap;
}
.datagrid .age3:hover {
  background-color: #585225;
}
.datagrid tr.even .age3 {
  background-color: #f3e782;
}
.datagrid .age4 {
  background-color: #6f4419;
  border-left: 1px #d49659 solid;
  border-right: 1px #d49659 solid;
  white-space: nowrap;
}
.datagrid .age4:hover {
  background-color: #dab38e;
}
.datagrid tr.even .age4 {
  background-color: #b36418;
}
.datagrid .age5 {
  background-color: #631818;
  border-left: 1px #f56363 solid;
  border-right: 1px #f56363 solid;
  white-space: nowrap;
}
.datagrid .age5:hover {
  background-color: #d69999;
}
.datagrid tr.even .age5 {
  background-color: #2c0606;
}

.admin h1.title, .box-head, .modalbox-title {
    background: #25598e;
    border-bottom: 1px #728eac solid;
    margin: 0;
    padding: 0.4em 0.6em;
    -moz-border-radius: 6px 6px 0 0;
    -webkit-border-radius: 6px 6px 0 0;
    border-radius: 6px 6px 0 0;
}

/* comments outter line*/
.commentflag .commentflag-inner {
  -moz-border-radius: 4px;
  -webkit-border-radius: 4px;
  border-radius: 4px;
  opacity: 0.95;
  filter: alpha(opacity=95);
  -moz-opacity: 0.95;
  background-color: #082749;
  border: 1px solid #204a87;
  bottom: 0.2em;
  cursor: pointer;
  font-size: 90%;
  font-weight: normal;
  left: 0;
  line-height: 1.7em;
  position: absolute;
  right: 0.2em;
  text-align: center;
  top: 0;
  vertical-align: top;
  width: 100%;
  z-index: 20;
}

.selected .commentflag .commentflag-inner {
  background-color: #082749;
}
.selected .commentflag .commentflag-inner,
.commentflag .commentflag-inner:hover {
  background-color: #082749;
}

.image-review-ui .selection {
  border: 1px #082749 solid;
  position: absolute;
}

.image-review-ui .selection-flag {
  background-color: #082749;
  cursor: pointer;
  height: 2em;
  left: 0px;
  line-height: 170%;
  text-align: center;
  top: 0px;
  width: 2em;
}

.comment-dlg .other-comments {
    background-color: #082749;
    border-right: 1px black solid;
    padding: 6px;
}

.tooltip.comments ul li {
  background-color: #082749;
  border-bottom: 1px #5170b3 solid;
  margin: 0;
  padding: 5px;
}

/* Replace old lines color on front page*/
.changedesc .body .diffed-text-area .replace-old .line {
  background: #8b081e;
}
.changedesc .body .diffed-text-area .replace-old .line .hl {
  background: #8b081e;
}
.changedesc .body .diffed-text-area .replace-old .marker {
  background: #8b081e;
}

/****************************************************************************
 * Banners
 ****************************************************************************/
.banner {
  background: #25400b;
  border: 1px #4d9c00 solid;
  margin-bottom: 10px;
  padding: 8px 10px;
  z-index: 90;
}

} /* For scope */
