css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://web.skype.com/") {
  .input.swx-chat-input {
    min-height: auto !important;
  }
  .input.swx-chat-input .form-holder {
    margin-top: -5px !important;
    margin-bottom: 10px !important;
  }
  #textarea-bindings {
    margin-top: 0px !important;
    margin-left: 5px !important;
  }
  #chatInputContainer {
    border: inset 2px -moz-dialog !important;
    min-height: 40px !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
  }
#chatInputAreaWithQuotes[style="height: 30px;"] {
    height: 20px !important;
}
#chatInputContainer[style="height: 39px;"] > #chatInputAreaWithQuotes[style="height: 30px;"] {
    height: 40px !important;
}
  #chatInputContainer[style="height: 49px;"] {
    max-height: 40px !important;
  }
  #chatInputContainer[style="height: 69px;"] {
    max-height: 60px !important;
  }
  #chatInputContainer[style="height: 89px;"] {
    max-height: 80px !important;
  }
  #chatInputContainer[style="height: 109px;"] {
    max-height: 100px !important;
  }
  #chatInputContainer[style="height: 129px;"] {
    max-height: 120px !important;
  }
  #chatInputContainer[style="height: 149px;"] {
    max-height: 140px !important;
  }
  #chatInputContainer[style="height: 169px;"] {
    max-height: 160px !important;
  }
  #chatInputContainer[style="height: 112px;"], #chatInputContainer[style="height: 122px;"] {
    max-height: 103px !important;
  }
  #chatInputContainer[style="height: 132px;"], #chatInputContainer[style="height: 142px;"] {
    max-height: 123px !important;
  }
  #chatInputContainer[style="height: 152px;"], #chatInputContainer[style="height: 162px;"] {
    max-height: 143px !important;
  }
  #chatInputContainer[style="height: 172px;"] {
    max-height: 163px !important;
  }
  #textarea-bindings #quoteContainer {
    margin-bottom: 0px !important;
  }
  #chatInputContainer .quotedText {
    padding-left: 30px !important;
  }

  .warning.notification, .sendContactCard, #menuItem-dialpad, .messageDestinationHolder, #footer, .btn.transparent.circle.actionButton {
    display: none !important;
  }
  .chatInputActionPicker.collapsible .wrapper .menuContainer.inline {
    display: block !important;
  }
  .send-button-holder {
    margin-left: 10px !important;
  }
  BUTTON[class*="btn disabled"] {
    cursor: default !important;
  }
  .icon.iconfont.media {
    cursor: pointer !important;
  }
  .TranslatorSettings {
    background-color: transparent !important;
  }

  .ContactProfile--main .toggler {
    height: 20px !important;
    width: 43px !important;
    border-left: 1px !important;
  }
  .TranslatorSettings .toggler {
    height: 20px !important;
    width: 43px !important;
  }
  SWX-MESSAGE[class*="deliveryFailed"] .content {
    border: 2px solid mediumvioletred !important;
    z-index: 2 !important;
  }
  SWX-MESSAGE .quotedText {
    padding-left: 10px !important;
    padding-right: 10px !important;
    padding-bottom: 10px !important;
  }
  SWX-MESSAGE[class*=" me "]:not([class*="urlPreview"]) .content {
    padding-right: 20px !important;
  }
  .iconfont.edit {
    padding-left: 10px !important;
  }

  .messageHistory {
    padding: 0px !important;
  }
  .DeliveryStatus {
    margin-bottom: 3px !important;
    margin-top: -18px !important;
  }
  .DeliveryStatus .DeliveryStatus-status {
    display: none !important;
  }
  .DeliveryStatus .iconfont {
    z-index: 2 !important;
    margin-top: -3px !important;
    margin-right: 3px !important;
  }
  SWX-MESSAGE SWX-NAME {
    display: none !important;
  }
  SWX-MESSAGE[class*=" first "] {
    margin-top: 5px !important;
  }
  .messageHistory.largeAllowed.visible {
    margin-bottom: -20px !important;
  }
  .typingIndicator {
    margin-bottom: -60px !important;
    margin-left: -25px !important;
  }
  
  .Me-sky {
    display: none !important;
  }
  .Avatar, .Avatar-image {
    border-radius: 0% !important;
  }
  .Me-skyContainer {
    background-color: transparent !important;
  }
  .sideContainer, .swxMe {
    margin-top: -10px !important;
  }
  .Me-skyContainer .Me-displayName, .Me-skyContainer .Me-moodMessage {
    margin-left: -7px !important;
  }
  .Me-large.Me-large--visible .Me-info {
    margin-top: -10px !important;
  }
  .Me-large.Me-large--visible .Me-info .Me-displayName {
    margin-top: -4px !important;
  }
  
  .urlPreview .description, .urlPreview .domainName {
    display: none !important;
  }
  .urlPreview .thumbnail {
    max-height: 60% !important;
    max-width: 60% !important;
  }
  .urlPreview .title {
    height: auto !important;
    white-space: pre-wrap !important;
    word-wrap: break-word !important;
  }
}
