css:
@-moz-document domain("userstyles.org") {
body, #root, #nav-view, #applies_link
{
	background-color: #444 !important;
    color: #ccc !important;
}

#navigation, .linksBox, #Panel { background-color: rgba(255,255,255,0.13); }
.Item.Read { background: none transparent !important; }
.navigation_link img { filter: invert(0.8); }

a, a:hover, a:visited, a:active, a:focus
{
	color: #bdf !important;
	transition: color 0.5s;
    transition: text-shadow 0.5s;
}

a:hover
{
	text-shadow: 0 0 3px white;
}

.DiscussionForm label, .MItem, .Item.Read
{
    color: lightgrey;
}

/*[[profilepicstyle]]*/

.Pager .Highlight {
    color: #fca !important;
}

.ReactQuote {
	filter: invert(0.65);
}

.EmotifyDropdown, .Emoticon
{
    filter: invert(0.25);
}

pre
{
    background-color: rgba(255,255,128,0.5) !important;
    color: white;
    border-color: rgba(255,255,224,0.5);
}
code { background-color: transparent; }

.navigation_title
{ color: lightgrey !important; }

.navigation_link:hover
{
  background-color: #888;
}

.linksBox .navigation_link:last-child { box-shadow: inset 0 0 2px 1px rgba(255, 255, 255, 0.34), 0 0 0 0 rgba(255, 255, 255, 0.34); }

#header, #Head
{
	background-color: #567;
    color: #ddd !important;
}

#footer
{
    position: relative;
    left: 25%;
    width: 50%;
    background-color: rgba(255,255,255,0.65);
}

.Button, .Button:hover, .Button:active, .Button:visited, .Button:focus
{
	border-radius: 1em;
    color: #111 !important;
    border-color: #666 !important;
}

img.bad-rating, img.good-rating, .SpNotifications, .SpGlobe, .SpInbox, .SpEnvelope, .SpBookmarks, .SpStar, .SpDashboard, .SpOptions, .SpCog, .Item .OptionsTitle, a.Bookmark, a.Bookmarked, a.Bookmarking
{
	filter: drop-shadow(0 0 2px white);
}

.errorExplanation
{
    background-color: #cc5050;
}

.style_title, h1, h2, h3, h4, h5, h6, #headline, #infomation_value_left, #information_key_left, .text_style_page, #information_value, #information_key, .DiscussionForm .PostOptions label { color: lightgrey !important; }

#create_new_style, #create_new_style:hover, #create_new_style:focus, #create_new_style:active, #install_style_button, #install_style_button:hover, #install_style_button:focus, #install_style_button:active
{
	background: linear-gradient(to bottom, rgba(154,203,246,1) 0%,rgba(135,191,243,1) 50%,rgba(113,177,245,1) 51%,rgba(97,149,233,1) 100%);
    color: #46a;
}
#create_new_style a
{
	color: white !important;
}
#update_style_button
{
	background: linear-gradient(to bottom, rgba(154,246,175,1) 0%,rgba(135,243,162,1) 50%,rgba(113,245,151,1) 51%,rgba(97,233,149,1) 100%);
    color: #585;
}
#donation_button
{
	background: linear-gradient(to bottom, rgba(246,205,154,1) 0%,rgba(243,196,135,1) 50%,rgba(245,192,113,1) 51%,rgba(233,192,97,1) 100%);
}
#style_installed_button
{
	background: linear-gradient(to bottom, rgba(204,250,225,1) 0%,rgba(193,249,220,1) 50%,rgba(182,250,217,1) 51%,rgba(174,244,217,1) 100%);
}

#archive_style_button, #archive_style_button:hover, #archive_style_button:focus, #archive_style_button:active,
#archive_style_button:visited,
#archive_style_button:focus
{
	background: linear-gradient(to bottom, rgba(246,154,156,1) 0%,rgba(243,135,135,1) 50%,rgba(245,118,113,1) 51%,rgba(233,115,97,1) 100%);
    color: #c22;
}
#update_style_button,
#style_installed_button,
#donation_button,
.button,
#archive_style_button, #archive_style_button:hover, #archive_style_button:focus, #archive_style_button:active,
#archive_style_button:visited,
#archive_style_button:focus,
#create_new_style, #create_new_style:hover, #create_new_style:focus, #create_new_style:active, #install_style_button, #install_style_button:hover, #install_style_button:focus, #install_style_button:active
{
	box-shadow: 0 3px 8px #333;
}

#share_div { color: lightblue !important; }
x
.social-network-logo:hover
{
	filter: drop-shadow(0 0 4px lightgrey);
}

#notes_textarea { background-color: #555; font-size: 15px; color: lightgrey; border-radius: 5px; overflow-y: unset !important; max-height: unset !important; }

#copyrights::after { content: " Dark Userstyles theme Â©2017 Xane Myers."; }
}
