css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("opensubtitles.org") {
  :root{
      /* --var-name: #color; */
      /* usage:  element { color: var(--var-name); } */
      --link: #00ACAF;
      --link-hover: cyan;
      --link-special: orange;
      --link-visited: yellow;
      --text: #AAA;
      --text-label: #AAA;
      --header: #DDD;
      --border: #00ACAF;
      --row-HL: #000C67;
      --bg: #000C17;
      --bg2: #000C37;
      --bg3: #000C57;
      --meter: #000C87;
      --btn-bg: transparent;
      --btn-text: #00ACAF;
      --btn-border: #00ACAF;
      --btn-hover: #000C67;
      --trans: transparent;
      --so1: yellow;
      --so2: orange;
  }
  
  html, body { background: var(--bg) !important; color: var(--text) !important; }
  h1, h2, h3, h4, h5 { color: var(--header) !important; }
  a, a:link { color: var(--link) !important; background: transparent !important; text-decoration: none !important; }
  a:hover { color: var(--link-hover) !important; background: transparent !important; }
  a:visited { color: var(--link-visited) !important; background: transparent !important; }
  hr { background: var(--border) !important; }
  td { background: none !important; }
  
  /* controls ================ */
  input#search_submit { background: transparent !important; border: 1px solid var(--border) !important; color: var(--btn-text) !important; }
  input#search_submit:hover { border-color: var(--link-hover) !important; color: var(--link-hover) !important; }
  input[type="button"], input[type="reset"] { border: 1px solid var(--border) !important; color: var(--btn-text) !important; background: none !important; }
  input[type="button"]:hover, input[type="reset"]:hover { border-color: var(--link-hover) !important; color: var(--link-hover) !important; }
  
  select { background: none !important; color: var(--link) !important; border: 1px solid var(--border) !important; }
  textarea, input[type="text"],
  input[type="password"] { border: 1px solid var(--border) !important; color: var(--text) !important; background: none !important; }
  
  input[type="submit"] { background: none !important; color: var(--btn-text) !important; border: 1px solid var(--border) !important; }
  input[type="submit"]:hover { border-color: var(--link-hover) !important; color: var(--link-hover) !important; }
  
  .white_content { background: var(--bg) !important; border: 1px solid var(--border) !important; }
  div.logo { background: none !important; }
  div.logo a:after {
    content: "opensubtitles.org";
    font-size: 14px !important;
    vertical-align: top !important;
    text-decoration: none !important; 
    color: var(--link) !important; 
  }
  div.logo a:hover:after { text-shadow: 0 0 2px !important; }
  
  .bt-dwl {
    background: transparent !important;
    color: var(--btn-text) !important;
    text-shadow: none !important;
    box-shadow: none !important;
    border-color: var(--border) !important; 
  }
  .bt-dwl:hover { border-color: var(--link-hover) !important; color: var(--link-hover) !important; }
  
  .ui-widget-header { background: var(--bg3) none !important; border-color: transparent !important; }
  .ui-widget-overlay { background: black none !important; }
  
  h3 { color: var(--header) !important; }
  
  /* login window ================== */
  .lightbox_content { background: var(--bg) !important; border: 1px solid var(--border) !important; }
  .lightbox_header { background: var(--bg3) !important; }
  .right_login { background: var(--bg2) !important; color: var(--link) !important; }
  input.login, input[value="Login"] { background: none !important; border: 1px solid var(--border) !important; color: var(--link) !important; }
  input[value="Login"]:hover { border-color: var(--link-hover) !important; color: var(--link-hover) !important; }
  
  
  /* hide ads =============== */
  #ad1-placeholder, #ad2-placeholder, .right_side_fixed, #scrubbuad_style, img[src*="/gfx/icons/watch_online/"], .a_1, .a_2,
  tr[style="text-align:center;margin:0px;padding:0px;"] { display: none !important; }
  
  img[src="//static.opensubtitles.org/gfx/logo.gif"] { width: 0px !important; height: 0px !important; }
  
  div[itemtype="http://schema.org/Movie"] fieldset:nth-child(8) { display: none !important; } /* tricky */
  iframe[src*="ads"] { display: none !important; }
  
  table#search_results + center + fieldset { display: none !important; }
  
  /* header ===================== */
  .header { height: 25px !important; }
  div#network { background: var(--bg3) !important; border-color: var(--border) !important; }
  .top_info, .top_info_right { border-color: var(--border) !important; }
  .bar li { color: var(--text) !important; }
  
  .bar li.active {
    color: var(--so1) !important;
    background: transparent none !important;
    border: 1px solid var(--border) !important;
    border-radius: 5px !important;
    padding-bottom: 5px !important;
  }
  .bar li { border: 1px solid transparent !important; }
  .bar li:hover { border: 1px solid var(--border) !important; padding-bottom: 5px !important; border-radius: 5px !important; }
  .bar li:hover:first-child { border: 1px solid transparent !important; }
  
  .msg { border-color: var(--so2) !important; background-color: var(--bg2) !important; color: var(--text) !important; }
  
  /* search form ==================== */
  .search_field { background: var(--bg3) !important; }
  #search_text {
    color: var(--text) !important; 
    background: transparent none !important;
    border: 1px solid var(--border) !important;
    padding-left: 5px !important; 
  }
  .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default
  { color: var(--link) !important; background: transparent !important; border-color: var(--border) !important; }
  
  .ui-widget-content { background: var(--bg) !important; border-color: var(--border) !important; color: var(--text) !important; }
  .ui-state-hover { background: var(--row-HL) !important; color: var(--link-hover) !important; border-color: var(--link-hover) !important; }
  
  .ui-state-focus { background: var(--row-HL) none !important; border: none !important; }
  
  .ui-menu-item .year { color: var(--so1) !important; }
  .ui-menu-item .count { color: var(--text) !important; }
  
  /* main page ====================== */
  fieldset { border-color: var(--border) !important; }
  legend { color: var(--link) !important; border-color: var(--border) !important; background: var(--bg2) !important; }
  
  table.smalltable { border: 1px !important; border-spacing: 0px !important; border-color: red !important; }
  .head { background-color: var(--bg3) !important; }
  .even, .odd { background-color: var(--bg2) !important; }
  .even:hover, .odd:hover { background-color: var(--row-HL) !important; }
  
  #multiple_slides_visible .agile_carousel {
    background-color: var(--bg) !important;
    box-shadow: none !important;
    border: 1px solid transparent !important;
  }
  #multiple_slides_visible .slide_inner { background-color: transparent !important; border-color: var(--border) !important; }
  #multiple_slides_visible .slide_inner:hover { border-color: var(--so2) !important; }
  
  .control_set_2_inner .group_numbered_buttons_container { background-color: var(--link) !important; }
  #multiple_slides_visible .previous_next_button { background-color: var(--link) !important; }
  
  /* search results ================== */
  table#search_results { border-spacing: 0px !important; border-color: var(--border) !important; }
  table#search_results tr { border: 1px solid var(--border) !important; }
  
  th ul ul { background: var(--bg) !important; }
  #menu a, #menu h3, #menul a, #menul h3, #menuc a, #menuc h3 { border-color: var(--border) !important; }
  #search_results th:hover { background: var(--meter) !important; }
  
  .sb_star_odd, .sb_star_even, .sb_ok, .sb_star_bad, .sb_close { background-color: transparent !important; }
  .bad { background: #300 !important; }
  .bad:hover { background: #500 !important; }
  .bad a { color: red !important; }
  
  div#top_wrapper_banner {
    background: var(--bg3) !important;
    color: var(--so1) !important;
    border: 1px solid var(--so1) !important;
    width: 99.8% !important; 
  }
  div.close { color: var(--text) !important; }
  
  /* expanding search item */
  td[style="background:#ECECD9;"] { background: var(--border) !important; }
  
  /* comments */
  .comment_reply { background: var(--bg3) !important; }
  
  /* pagination ===================== */
  #pager strong { background: none !important; }
  #pager strong, #pager a { border: 1px solid var(--border) !important; }
  #pager a:hover { border-color: var(--link-hover) !important; }
  
  /* footer ================== */
  .upfooter, .footer { background: var(--bg2) !important; }
}
