/*

Name:          Project: BlueGreen

Version:       1.0

Author:        nyssi.deviantart.com

Created:       29 January, 2009

Last Updated:  31 January, 2009

               Changelog:
               -------------
               31st January: Added larger fonts on date for the Pageview Stats page - thanks to `zetab for the suggestion   
                             Added data URI's and styling for official deviantART polls - thanks to `electricjonny
             
               30th January: Added styling for message centre poll results - thanks to ^Ikue 



Information:   Restyled links, pageview stats and polls for profile pages.

*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {



/* Pageview Stats Styling */

#gmi-Gruser #deviant table.full td {
font-size:12px !important;
padding:0 5px 5px 5px !important;
}

#gmi-Gruser #deviant div.result {
background:url(data:image/gif;base64,) right no-repeat !important;
-moz-border-radius-topleft:5px !important;
-moz-border-radius-bottomleft:5px !important;
line-height:24px !important;
}

#gmi-Gruser #deviant div.bar {
background:url(data:image/gif;base64,) right no-repeat !important;
height:24px !important;
-moz-border-radius-topleft:5px !important;
-moz-border-radius-bottomleft:5px !important;
}

#gmi-Gruser #deviant div.result {
border:none !important;
}


/* Poll's Styling */

#gmi-Gruser #deviant div.ctube div.cfill {
background:url(data:image/gif;base64,)right no-repeat !important;}


#gmi-Gruser #deviant div.cfill span {
margin:0 5px 0 0 !important;
}

/* Message Centre Polls */

#bubbleview-messages #messages div.ctube div.cfill {
background:url(data:image/gif;base64,) right no-repeat !important;}


#bubbleview-messages #messages div.cfill span {
margin:0 5px 0 0 !important;
}

/* Footer Polls */

#depths table#footerstuff td#footer_poll div.poll-bar, 
div.result_bars div.poll-bar {
border-color: #A8BF1F !important;
padding:0 2px !important;
height:24px!important;
}

#depths table#footerstuff td#footer_poll div.poll-bar, 
div.result_bars div.poll-bar span {
color:#FFF !important;
margin:5px 0 0 0 !important;
padding:0 !important;
line-height:24px !important;
}

div.bubbleview#polls div.poll-bar {
background:url(data:image/gif;base64,) right no-repeat !important;
}

/* Links Restyling */

#gmi-Gruser #deviant div.pp li.f a,
#gmi-Gruser #deviant div.ppp li.f a,  
#gmi-Gruser #deviant div.pp td.f a.u, 
#gmi-Gruser #deviant div.pp .grf-deviants span a, 
#gmi-Gruser #deviant div.pp .grf-mirror a,
#gmi-Gruser #deviant div.pp dt.f a,
#gmi-Gruser #deviant div.pp .c a.a,
#gmi-Gruser #deviant div.pp .tt-w a.u,
#gmi-Gruser #deviant div.ppp .tt-w a.u,
#gmi-Gruser #deviant div.artzone .tt-w a.u  {
color:#498091 !important;
text-decoration:none !important;
}

#gmi-Gruser #deviant div.pp li.f a:hover,
#gmi-Gruser #deviant div.ppp li.f a:hover, 
#gmi-Gruser #deviant div.pp td.f a:hover.u, 
#gmi-Gruser #deviant div.pp .grf-deviants span a:hover, 
#gmi-Gruser #deviant div.pp .grf-mirror a:hover,
#gmi-Gruser #deviant div.pp dt.f a:hover,
#gmi-Gruser #deviant div.pp .c a.a:hover,
#gmi-Gruser #deviant div.pp .tt-w a:hover.u,
#gmi-Gruser #deviant div.ppp .tt-w a:hover.u,
#gmi-Gruser #deviant div.artzone .tt-w a:hover.u {
text-decoration:underline !important;
}

#gmi-Gruser #deviant div.pp .tt-w a.t, 
#gmi-Gruser #deviant div.ppp .tt-w a.t,
#gmi-Gruser #deviant div.artzone .tt-w a.t {
color:#50706d !important;
}

}
