css:
@-moz-document domain("www.ft.com") {
html,
body,
.o-header__row,
.o-header__nav,
.o-header__nav--desktop,
.fast-ft,
.o-header__drawer-inner,
.o-header__drawer-menu-item,
.o-header__mega {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.o-teaser__content,
.o-teaser__image-container:after,
.o-teaser__standfirst,
.o-header__drawer-menu-toggle,
.card {
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}

}
