css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Feuille de style faite par Flobesst pour Tom's Guide France (Forum & Solutions)
Un petit tour sur www.bazar-info.net en guise de remerciement, ça fera plaisir :)

Version: 5.0 (27/01/2012) */

/* ***************** */
/* *** SOLUTIONS *** */
/* ***************** */

@-moz-document domain(tomsguide.fr) {

/* Elargissement de la page */
#mainSection { width: auto !important;
margin: 0 30px 0 30px !important; }
.leftCol { width: auto !important;
margin-right: 320px !important;
background-image: none !important; }
.tcsPostContent { float: right !important;
width: auto !important;
left: auto !important;
right: 0 !important;
margin-left: 150px !important;
margin-top: -175px !important; }
.tcsSolution .tcsPostContent { float: none !important; margin: 0 0 0 150px !important; }


/* Augmente la taille du texte et des liens dans le menu ouvrant la liste des catégories. Supprime aussi la couleur violette de ceux visités */
.navMenu #navMenu .meta-name { font-size: 16px !important; }
.navMenu #navMenu ul.cats a { font-size: 13px !important; }
.navMenu #navMenu ul.cats a:visited { color: #004488 !important; }


/* Masque les cases de la colonne de droite */
.rightCol .bomAd, .rightCol .questions, .rightCol .relRes, .rightCol .bTB { display: none !important; }
.rightCol { background-color: transparent !important;
border: none !important; }


/* Colle le suivi de discussions en bas à droite */
.rightCol { float: none !important;
position: fixed !important;
top: auto !important;
left: auto !important;
right: 30px !important;
bottom: 9px !important; }


/* Enlève le fond gris d'un sujet lu */
.tcsListing .bgdoneRead, .tcsListing .bgdoneParticipate, .tcsListing .bgtoRead { background-color: transparent !important; }


/* Masque le bloc "Autres pages sur:" en-dessous du texte du 1er message d'un sujet */
.responseText .line .size3of5 { display: none !important; }


/* Masque le bloc "Ces contenus peuvent également vous intéresser" sous le bloc du premier message d'un sujet */
.relativeContents, .greyDash.rtDash  { display: none !important; }


/* Remonte légèrement le bloc du score d'une réponse */
.responseText .tcsScoreVote { top: -22px !important; }


/* Supprime le contour des avatars */
.tcsPostLeft .usrProfile .usrAvatar { border: none !important; }


/* Augmente la taille du statut (nouveau, addict, modérateur, admin suprême...) */
.tcsPostLeft .usrProfile .rankingStatus { font-size: 12px !important; }


/* Colore en blanc le fond de la réponse rapide */
.qaBox { background-color: transparent !important; }


/* Masque la pub sous la réponse rapide */
.google_ads_bom_block { display: none !important; }


/* Supprime le contour rouge d'un message supprimé */
.deletedAnswer { border: none !important; }

}

/* ************* */
/* *** FORUM *** */
/* ************* */

@-moz-document domain(infos-du-net.com) {

/* Elargissement de la page */
#mainSection { width: auto !important;
margin: 0 30px 0 30px !important; }
.leftCol { width: auto !important;
margin-right: 320px !important;
background-image: none !important; }
.tguContent .responseText { padding-right: 25px !important; }
.responseContent .responseText { margin-right: -4% !important; }



/* Augmente la taille du texte et des liens dans le menu ouvrant la liste des catégories. Supprime aussi la couleur violette de ceux visités */
.navMenu #navMenu .meta-name { font-size: 16px !important; }
.navMenu #navMenu ul.cats a { font-size: 13px !important; }
.navMenu #navMenu ul.cats a:visited { color: #004488 !important; }


/* Masque les cases de la colonne de droite */
.rightCol .bomAd, .rightCol .questions, .rightCol .relRes, .rightCol .bTB { display: none !important; }
.rightCol { background-color: transparent !important;
border: none !important; }


/* Colle le suivi de discussions en bas à droite */
.rightCol { float: none !important;
position: fixed !important;
top: auto !important;
left: auto !important;
right: 30px !important;
bottom: 9px !important; }


/* Corrige le bug des numéros de pages qui ne se cliquent pas malgré le changement de curseur */
/*.pager li ul li { padding: 0 !important; }*/
.pager li ul li a { padding-top: 5px !important;
padding-bottom: 4px !important; }


/* Masquer le bloc "Autres pages sur:" sous le texte du premier message d'un sujet */
.responseText .chevron { display: none !important; }


/* Colore toutes les réponses en fond blanc */
.msgl1 .responseContent, .msgl1 .frmPostContent { background-color: #FFF !important; }


/* Supprime le contour des avatars */
.responseContent .usrProfile .usrAvatar { border: none !important; }


/* Augmente la taille du statut (nouveau, addict, modérateur, admin suprême...) */
.tcsPostLeft .usrProfile .rankingStatus { font-size: 12px !important; }


/* Masque la barre de couleur représentant le badge d'un membre (bronze, argent ou or) */
.responseContent .usrProfile .rankBanner { display: none !important; }


/* Affiche les signatures sur 1 ligne au lieu de plusieurs */
.responseContent .signature { width: auto !important; }


/* Masque la barre de partage (Google, Twitter, G+...) en fin de sujet */
#shareBarProviderListLastTopic { display: none !important; }


/* Masquer les "Contenus similaires" sous la réponse rapide */
.relativeContents { display: none !important; }


/* Modifie l'apparence de la liste de sujets */
.topicListing td { border: none !important; }
.topicListing td a.topicTitle:visited { color: #004488 !important; }
.topicListing .bgdoneRead, .topicListing .bgdoneParticipate, .topicListing .bgtoRead, .topicListing .bgtoParticipate { background-color: transparent !important; }
.topicListing td.updates { text-align: center !important; }
.topicListing tr.firstNonSticky td { padding-top: 20px !important; }


}
