css:
@-moz-document domain("netflix.com") {
#netflix-player .player-control-bar{ background: transparent; -webkit-box-shadow: none; -moz-box-shadow: none; box-shadow: none; }
#mdx-controls-wrapper .player-control-button:before, #netflix-player.ua-webkit- .player-control-bar .player-control-button:before { color: #eee!important; }
#netflix-player .player-control-bar .player-report-problem .border { background: #eee; }
#netflix-player .player-control-bar .player-report-problem { display: none; }
#netflix-player .player-control-bar .player-control-button, #netflix-player .player-status, #netflix-player .player-control-bar .player-control-divider { border: none; }
#netflix-player .player-status { padding-right: 9vh; }
#netflix-player .player-status span { color: #eee; }
#netflix-player .player-controls-wrapper { width: 100%; left: 0; bottom: 0; -moz-transition: opacity .25s cubic-bezier(0.0, 0.0, 0.2, 1); -webkit-transition: opacity .25s cubic-bezier(0.0, 0.0, 0.2, 1); transition: opacity .25s cubic-bezier(0.0, 0.0, 0.2, 1); background: -moz-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, .3) 50%, rgba(0, 0, 0, .7) 100%); background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, .3) 50%, rgba(0, 0, 0, .7) 100%); background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, .3) 50%, rgba(0, 0, 0, .7) 100%); }
#netflix-player .player-controls-wrapper>section { width: calc(100% - 48px); }
#netflix-player .player-slider { padding-left: 24px; margin-top: 2vh; top: 0!important; opacity: 1!important; transform: none!important; -o-transform: none!important; }
#netflix-player .player-slider>div .player-scrubber-progress { height: 5px; -webkit-border-radius: 0px; -moz-border-radius: 0px; border-radius: 0px; margin-top: 2px; -webkit-box-shadow: none; -moz-box-shadow: none; box-shadow: none; background: rgba(255, 255, 255, .4); }
#netflix-player .player-slider>div .player-scrubber-progress-completed, div.player-scrubber.horizontal .player-scrubber-progress-buffered { -webkit-border-top-right-radius: 0px; -moz-border-radius-topright: 0px; border-top-right-radius: 0px; -webkit-border-bottom-right-radius: 0px; -moz-border-radius-bottomright: 0px; border-bottom-right-radius: 0px; -webkit-border-top-left-radius: 0px; -moz-border-radius-topleft: 0px; border-top-left-radius: 0px; -webkit-border-bottom-left-radius: 0px; -moz-border-radius-bottomleft: 0px; border-bottom-left-radius: 0px; }
#netflix-player .player-slider>div .player-scrubber-progress-completed { background: #f12b24; }
div.player-scrubber .player-scrubber-target>.player-scrubber-handle { background: #f12b24; -webkit-box-shadow: none; -moz-box-shadow: none; box-shadow: none; display: none; }
div.player-scrubber.horizontal .player-scrubber-progress-buffered { background: rgba(255, 255, 255, .5); }
#netflix-player .player-scrubber:hover .player-scrubber-progress { height: 7px; margin-top: 1px; }
#netflix-player .player-slider label { position: absolute; bottom: -4vh; right: 35.5vh; }
.container-icon-player-back-to-browse:before { content: ""; background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_chevron_left_white_48px.svg); background-size: 7vh 7vh; width: 7vh; height: 7vh; display: inline-block; -webkit-filter: drop-shadow(0px 0px 1.5vh rgba(0, 0, 0, .6)); filter: drop-shadow(0px 0px 1.5vh rgba(0, 0, 0, .6)); }

.PlayerControls--button-control-row{ margin:0!important;background: transparent; -webkit-box-shadow: none; -moz-box-shadow: none; box-shadow: none;height:6vh;}
.PlayerControls--button-control-row .button-nfplayerPause, .PlayerControls--button-control-row .button-nfplayerPlay,
.PlayerControls--button-control-row .button-volumeLow, .PlayerControls--button-control-row .button-volumeMax, .PlayerControls--button-control-row .button-volumeMedium, .PlayerControls--button-control-row .button-volumeMuted,
.PlayerControls--button-control-row .video-title.text-control,
.ReportAProblemPopupContainer+.nfp-popup-control .nfp-button-control
{border:none;}
.PlayerControls--bottom-controls{margin-top:-40px;width: 100%; left: 0; bottom: 0; -moz-transition: opacity .25s cubic-bezier(0.0, 0.0, 0.2, 1); -webkit-transition: opacity .25s cubic-bezier(0.0, 0.0, 0.2, 1); transition: opacity .25s cubic-bezier(0.0, 0.0, 0.2, 1); background: -moz-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, .3) 50%, rgba(0, 0, 0, .7) 100%); background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, .3) 50%, rgba(0, 0, 0, .7) 100%); background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, .3) 50%, rgba(0, 0, 0, .7) 100%); }
.PlayerControls--progress-control-row{padding:0;margin:0 0 -0.5vh}
.PlayerControls--progress-control-row-standard, .PlayerControls--button-control-row{transition:none;-moz-transition:none;-webkit-transition:none;width: calc(100% - 48px);padding-left: 24px; margin-top: 2vh; top: 0!important; opacity: 1!important; transform: none!important; -o-transform: none!important;position:relative; }
.nfp .button-reportAProblem{display:none;}
.nfp .legacy-controls-styles .scrubber-container .track { height: 5px; -webkit-border-radius: 0px; -moz-border-radius: 0px; border-radius: 0px; margin-top: 2px; -webkit-box-shadow: none; -moz-box-shadow: none; box-shadow: none; background: rgba(255, 255, 255, .4); }
.nfp .legacy-controls-styles .scrubber-container .current-progress { background: #f12b24; }
.nfp .legacy-controls-styles .scrubber-container .buffered { background: rgba(255, 255, 255, .5); }
.nfp .legacy-controls-styles .scrubber-container { -webkit-border-top-right-radius: 0px; -moz-border-radius-topright: 0px; border-top-right-radius: 0px; -webkit-border-bottom-right-radius: 0px; -moz-border-radius-bottomright: 0px; border-bottom-right-radius: 0px; -webkit-border-top-left-radius: 0px; -moz-border-radius-topleft: 0px; border-top-left-radius: 0px; -webkit-border-bottom-left-radius: 0px; -moz-border-radius-bottomleft: 0px; border-bottom-left-radius: 0px; }
.nfp .legacy-controls-styles .scrubber-container .buffered,.nfp .legacy-controls-styles .scrubber-container .current-progress{ -webkit-border-top-right-radius: 0px; -moz-border-radius-topright: 0px; border-top-right-radius: 0px; -webkit-border-bottom-right-radius: 0px; -moz-border-radius-bottomright: 0px; border-bottom-right-radius: 0px; -webkit-border-top-left-radius: 0px; -moz-border-radius-topleft: 0px; border-top-left-radius: 0px; -webkit-border-bottom-left-radius: 0px; -moz-border-radius-bottomleft: 0px; border-bottom-left-radius: 0px; }
.nfp .legacy-controls-styles .scrubber-container .scrubber-head { background: #f12b24; -webkit-box-shadow: none; -moz-box-shadow: none; box-shadow: none; display: none; }
.nfp .legacy-controls-styles .scrubber-container:hover .track { height: 7px; margin-top: 1px; }
.nfp .legacy-controls-styles .time-remaining{position:absolute;bottom:-3.86vh;left:calc(16em + 24px);width:9.6em;text-align:center;}
.nfp .legacy-controls-styles .time-remaining .time{font-size:2.2em;font-weight:400!important;text-shadow:none;}
.top-left-controls{position:relative;}
.top-left-controls:before { content: ""; background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_chevron_left_white_48px.svg); background-size: 7vh 7vh; width: 7vh; height: 7vh; display: inline-block; -webkit-filter: drop-shadow(0px 0px 1.5vh rgba(0, 0, 0, .6)); filter: drop-shadow(0px 0px 1.5vh rgba(0, 0, 0, .6)) brightness(93%); position:absolute;top:2.8em;left:2.8em;}
.button-bvuiExit use{display:none!important;}
.svg-icon,.nfp-button-control:hover .svg-icon{transform:none!important;-moz-transform:none!important;-o-transform:none!important;-webkit-transform:none!important;}
.svg-icon use{color:#eee!important;fill:#eee!important;}
body{color:#eee!important;}
.PlayerControls--button-control-row .video-title.text-control span{color:#eee;font-size:2.2em;}
.video-title{padding-top:0.1vh!important;padding-left:12vh!important;}
.button-nfplayerPlay,.button-nfplayerPause{margin:0 1vh;}
.button-bvuiFullScreenOn svg{display:none;}
.button-bvuiFullScreenOff svg{display:none;}
.button-nfplayerSubtitles svg{display:none;}
.button-bvuiFullScreenOn:before{ content: ""; filter: brightness(93%);background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_fullscreen_white_48px.svg); background-size:4.5vh 4.5vh;width:4.5vh;height:4.5vh; display: inline-block; position:absolute;top:0.8em;left:1em;}
.button-bvuiFullScreenOff:before{ content: ""; filter: brightness(93%);background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_fullscreen_exit_white_48px.svg); background-size:4.5vh 4.5vh;width:4.5vh;height:4.5vh; display: inline-block; position:absolute;top:0.8em;left:1em;}
.button-nfplayerSubtitles:before{ content: ""; filter: brightness(93%);background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_mode_comment_white_48px.svg); background-size:3.4vh 3.4vh;width:3.4vh;height:3.4vh; display: inline-block; position:absolute;top:1.3em;left:1.8em;}
}
