css:
@-moz-document domain(habitica.com) {
#app-header,#login-incentives,#new-stuff,#app>div.container-fluid>div.row>footer,#app>div.notifications,.modal-backdrop,.reward,.drawer-container,.user-dropdown>li,.item-with-icon,iframe,#nav_collapse>div.item-with-icon,.tags-popover-title, div[id^="tags-icon-"],.column-background,.resting-banner,.create-task-area .d-none,.resting-banner,.g1g1-banner {
    display:none!important;
    visibility:hidden!important;
}

.d-none {
    display:block!important;
}

.restingInn .navbar {
    top:0!important;
}

div.user-tasks-page .modal-backdrop,
div.item-with-icon.item-notifications.dropdown,
a.dropdown.item-with-icon.item-user {
    display:block!important;
    visibility:visible!important;
}

div.tasks-navigation>div {
    z-index:1045!important;
    top:-63px!important;
}

div.filter-panel {
    left:0!important;
}

input.input-search {
    width:250px;
}

div.tasks-columns {
    margin-top:-65px;
}

div.create-task-area {
    position:relative!important;
    right:90px!important;
}

#create-task-btn {
    top:-3px!important;
    position:relative;
}

#create-dropdown>div.btn-group {
    right:120px;
    z-index:1031;
}

/*
div.input-group {
    z-index:1031;
}
*/

.user-dropdown {
    overflow:auto!important;
}

.container-fluid {
    padding-top:55px;
    height:100%;
}

.tasks-list {
    height:auto!important;
    padding-bottom:8px!important;
}

.task-content {
    width:300px;
}

#app {
    height:100%!important;
}

a.nav-link {
    color: #432874 !important;
}

a.nav-link:hover {
    color: #fff !important;
}

.tag-label {
    background-color:#ff9c7930 !important;
    padding:5px;
    border-radius:3px;
}

a {
    word-break:break-all;
}

.tags.svg-icon, .tags-popover-title {
    width:0!important;
}

@media (min-width: 992px) {
    .col-lg-3 {
        flex:0 0 33%;
        max-width:33%;
    }
}

.modal-open {
    overflow-y:scroll!important;
    padding-right:0!important;
}
}
