css:
@-moz-document domain("tool-cloud.ealps.shinshu-u.ac.jp"), domain("moodle-cloud.ealps.shinshu-u.ac.jp") {
*
{
    background-color: rgb(49, 49, 49);
}

body
{
    color: #eaeaea;
    background-color: rgb(49, 49, 49);
}

a
{
    color: #53a5ff;
    background-color: rgba(0, 0, 0, 0);
}

a:hover
{
    color: #70b5ff;
}

a:focus
{
    color: #ff6448;
}

span,
img,
p,
h1,
h2,
h3,
h4,
label
{
    background-color: inherit;
}

select
{
    background-color: #717171;
    color: white;
}

i
{
    background-color: rgba(0, 0, 0, 0);
}

tr
{
    vertical-align: inherit;
    border-color: inherit;
    background-color: inherit;
}

.path-mod-assign td.submissionstatussubmitted,
.path-mod-assign div.submissionstatussubmitted,
.path-mod-assign a:link.submissionstatussubmitted
{
    color: inherit;
}

.boxaligncenter
{
    background-color: inherit;
}

.glyphicon
{
    background-color: rgba(0, 0, 0, 0);
}

.block
{
    background-color: rgba(0, 0, 0, 0);
}

.breadcrumb
{
    background-color: rgba(0, 0, 0, 0);
    border: 1px solid #e3e3e3;
    border-radius: 4px;
}

.navbar-inverse .navbar-inner
{
    background-image: linear-gradient(to bottom, rgba(45, 45, 45, 255), rgba(45, 45, 45, 255));
}

.navbar-fixed-top .navbar-inner,
.navbar-static-top .navbar-inner
{
    box-shadow: 0 1px 10px rgba(0, 0, 0, 0.36);
}

.moodle-actionmenu[data-enhanced].show .menu a
{
    display: block;
    color: #f5f5f5;
    background-color: rgba(0, 0, 0, 0);
    padding: 2px 1em 2px 28px;
}

.menu-action-text
{
    background-color: rgba(0, 0, 0, 0);
}

.dropdown-menu>li>a
{
    color: white;
}

.panel-success
{
    border-color: #c3c3c3;
}

.btn>span
{
    background-color: rgba(0, 0, 0, 0);
}

.form-control
{
    color: #ffffff;
    background-color: #5f5f5f;
}

.panel-success>.panel-heading
{
    color: #ffffff;
    background-color: rgb(66, 66, 66);
    background-color: rgb(28, 60, 28);
}

.panel-heading.lead>span
{
    background-color: rgba(45, 45, 45, 0);
}

.panel>.list-group .list-group-item,
.panel>.panel-collapse>.list-group .list-group-item
{
    background-color: #2d2d2d;
}

.panel-danger>.panel-heading
{
    color: #ffcbca;
    background-color: #6f0000;
    border-color: #ebccd1;
}

.panel>.list-group .list-group-item,
.panel>.panel-collapse>.list-group .list-group-item
{
    border-width: 1px 0;
    border-radius: 0;
    background-color: rgb(49, 49, 49);
}

.alert-danger
{
    background-color: #6f0000;
    border-color: #ebccd1;
    color: #ffcbca;
}

.submissionstatustable tr:hover:nth-child(even)
{
    color: black;
    background-color: inherit;
}

.no-overflow p
{
    background-color: rgba(0, 0, 0, 0);
}

div.event div
{
    background-color: rgba(0, 0, 0, 0);
}

.block_calendar_upcoming div.event div.date
{
    background-color: rgba(255, 0, 0, 0.24);
}

div.event p
{
    color: black;
}

div.event span.dimmed_text
{
    color: white;
}

div.event .description span
{
    background-color: inherit;
}

.eventtitle
{
    color: gray;
}

div.event span.date
{
    background-color: rgba(255, 0, 0, 0.24);
    padding: 0px 10px 0px 10px;
}

div.event span.date a.dimmed
{
    color: #f7ff00;
}

div.event b
{
    background-color: inherit;
}

.courses .coursebox *
{
    background-color: inherit;
}

.courses .coursebox.odd:hover,
.course_category_tree .courses>.paging.paging-morelink:hover
{
    background-color: #2b2b2b;
}

.courses .coursebox.even:hover
{
    background-color: #313131;
}

.courses .coursebox.even
{
    background-color: #2b2b2b;
}

#fgroup_id_buttonar
{
    padding: 19px 20px 20px;
    margin-top: 20px;
    margin-bottom: 20px;
    background-color: rgb(49, 49, 49);
    border-top: 1px solid #e5e5e5;
    padding-left: 0;
}

.list-group-item
{
    background-color: #313131;
}

.well
{
    background-color: #313131;
}

table#form td.submit,
.form-buttons,
.path-admin .buttons,
#fitem_id_submitbutton,
.fp-content-center form+div,
div.backup-section+form,
#fgroup_id_buttonar
{
    background-color: #313131;
}

#region-main
{
    background-color: #313131;
}

.popover-title
{
    background-color: #5a5a5a;
}

.yui3-widget-buttons
{
    background-color: rgba(0, 0, 0, 0);
}

.moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd
{
    color: white;
}

input[disabled],
select[disabled],
textarea[disabled],
input[readonly],
select[readonly],
textarea[readonly]
{
    cursor: not-allowed;
    background-color: #313131;
}

.path-calendar .maincalendar .eventlist .event
{
    background-color: #2b2b2b;
}

.path-calendar .maincalendar .eventlist .event div,
.path-calendar .maincalendar .eventlist .event p
{
    color: white;
    background-color: inherit;
}

.path-calendar .maincalendar .eventlist .event .description
{
    background-color: inherit;
}

.calendar_event_course
{
    background-color: #005428;
}

.path-calendar .maincalendar .calendar_event_course
{
    border-color: #009447;
}

.block .minicalendar td.duration_course.duration_finish
{
    background-color: #00500b;
}

.block .minicalendar td.duration_course
{
    border-top: 1px solid #009447;
    border-bottom: 1px solid #009447;
}

.editor_atto_content_wrap
{
    color: #fff;
}

.mform legend a,
.mform legend a:hover
{
    color: #fff;
}

.editor_atto_notification
{
    color: #313131;
}

.yui3-skin-sam .yui3-calendar-content
{
    color: #7d7d7d;
}

.yui3-skin-sam .yui3-calendarnav-prevmonth
{
    border-right-color: #a5a5a5;
}

.yui3-skin-sam .yui3-calendarnav-nextmonth
{
    border-left-color: #a5a5a5;
}

.breadcrumb>li
{
    text-shadow: 0 0 0 rgba(0, 0, 0, 0);
}

input,
textarea,
.uneditable-input
{
    color: white;
}

td[data-core_calendar-title] div
{
    background-color: rgba(0, 0, 0, 0);
    color: #4e4e4e;
}

.moodle-actionmenu[data-enhanced].show .menu
{
    background-color: #313131;
}

.filemanager-toolbar
{
    background: #e0e0e0;
}

.filemanager .fp-content
{
    background-color: white;
}

.fp-toolbar
{
    background-color: rgba(0, 0, 0, 0);
}

.fp-pathbar
{
    background-color: inherit;
}

.filemanager-container *
{
    background-color: rgba(0, 0, 0, 0);
    color: #313131;
}

.foldertree .filemanager a,
.foldertree.filemanager a:hover
{
    color: #dcdcdc;
}

.file-picker a,
.file-picker a:hover
{
    color: #b3b3b3;
    text-decoration: none;
}

.file-picker .fp-list .fp-repo.active
{
    background: #656565;
}

.moodle-dialogue-base .moodle-dialogue-wrap
{
    background-color: #313131;
}

.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd,
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd
{
    background-image: linear-gradient(to bottom, #969696, #313131);
    background-color: rgba(0, 0, 0, 0);
    color: white;
    text-shadow: 0 0 0;
}

.page-context-header .page-header-headings,
.page-context-header .header-button-group
{
    background-color: rgba(0, 0, 0, 0);
}

.generaltable tbody>tr:hover:nth-child(even)>td
{
    color: #545454;
}

table#explaincaps tbody>tr:nth-child(odd)>td,
table#defineroletable tbody>tr:nth-child(odd)>td,
table.grading-report tbody>tr:nth-child(odd)>td,
table#listdirectories tbody>tr:nth-child(odd)>td,
table.rolecaps tbody>tr:nth-child(odd)>td,
table.userenrolment tbody>tr:nth-child(odd)>td,
table#form tbody>tr:nth-child(odd)>td,
form#movecourses table tbody>tr:nth-child(odd)>td,
#page-admin-course-index .editcourse tbody>tr:nth-child(odd)>td,
.forumheaderlist tbody>tr:nth-child(odd)>td,
table.flexible tbody>tr:nth-child(odd)>td,
.generaltable tbody>tr:nth-child(odd)>td,
table#explaincaps tbody>tr:nth-child(odd)>th,
table#defineroletable tbody>tr:nth-child(odd)>th,
table.grading-report tbody>tr:nth-child(odd)>th,
table#listdirectories tbody>tr:nth-child(odd)>th,
table.rolecaps tbody>tr:nth-child(odd)>th,
table.userenrolment tbody>tr:nth-child(odd)>th,
table#form tbody>tr:nth-child(odd)>th,
form#movecourses table tbody>tr:nth-child(odd)>th,
#page-admin-course-index .editcourse tbody>tr:nth-child(odd)>th,
.forumheaderlist tbody>tr:nth-child(odd)>th,
table.flexible tbody>tr:nth-child(odd)>th,
.generaltable tbody>tr:nth-child(odd)>th
{
    background-color: #4e4e4e;
}

.generaltable tbody>tr *,
.generaltable tbody>tr .ygtvtable tbody>tr *
{
    background-color: inherit;
}

.forumpost
{
    background-color: #313131;
}

.forumpost .header
{
    margin-bottom: 3px;
    border-bottom: 1px solid #e3e3e3;
}

.dropdown-menu
{
    background-color: #313131;
}

.course_category_tree .category>.info
{
    background-color: #292929;
}

.panel
{
    background-color: #313131;
}

.course-content .current *
{
    background-color: inherit;
}

.course-content .current
{
    background-color: #272727;
}

span[style*="color: rgb(0, 0, 0);"]
{
    color: white;
}
.que * {
    background-color: inherit;
    color: #dcdcdc;
}
.que .info, .que .formulation, .que .outcome, .que .comment {
    text-shadow: 0 0px 0 rgba(255,255,255,0);
    background-color: #272727;
}
.que .formulation {
    color: #dcdcdc;
}
}
