css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com"), domain("gaming.youtube.com"), domain("www.afreecatv.com"), domain("afreecatv.com"), domain("vod.afreecatv.com") 
{
  
  .page-loaded .yt-base-gutter, .page-loaded #page, .page-loaded #appbar-content    { 
    
    background: #fff url('https://www.designbolts.com/wp-content/uploads/2012/12/Skin-Side-Up-White-Seamless-Texutre-For-Website-Background.jpg') repeat scroll center top !important;
  }
  
  
  .yt-card, .yt-ui-ellipsis-2 { 
    
    background: transparent;
    box-shadow: none;
  }
  

  
  .yt-uix-expander-collapsed .yt-ui-ellipsis { 
    
    background: transparent;
    
  }
  
  .yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a { 
    
    background: transparent;
    
  }

  #guide-container  { 
    
    background: transparent;
    
  }
  
  
  .yt-lockup-tile .yt-lockup-meta, .yt-lockup-tile .yt-lockup-description { 
    
    background: transparent;
    
  }
  
  
  #watch8-sentiment-actions, .resume-playback-progress-bar, .resume-playback-background, .resume-playback-progress-bar
  {
    display: none !important;
  }

  .comment-section-renderer
  {
    display: none !important;
  }

  .ytp-time-separator, .ytp-time-duration
  {
    display: none !important;
  }

  .ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-bar-padding
  {
    display: none !important;
  }

  .ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list, .ytp-touch-mode .ytp-progress-bar-container:not([aria-disabled="true"]) .ytp-progress-list, .ytp-drag .ytp-progress-list
  {
    display: none !important;
  }

  body .addto-watch-later-button, .addto-watch-later-button-sign-in, .addto-watch-later-button-loading, .addto-watch-later-button-error, .addto-watch-later-button-success, .addto-watch-later-button-remove
  {
    display: none !important;
  }

  .video-time
  {
    display: none !important;
  }

  #video-time
  {
    display: none !important;
  }
  ytp-time-display notranslate
  {
    display: none !important;
  }

  yt-lockup-meta-info
  {
    display: none !important;
  }

  #yt-lockup-meta
  {
    display: none !important;
  }

  .yt-lockup-meta
  {
    display: none !important;
  }

  yt-lockup-meta
  {
    display: none !important;
  }

  .ytp-time-display
  {
    display: none !important;
  }
  ytp-play-progress ytp-swatch-background-color
  {
    display: none !important;
  }

  #ytp-play-progress ytp-swatch-background-color
  {
    display: none !important;
  }

  .ytp-play-progress ytp-swatch-background-color
  {
    display: none !important;
  }

  ytp-progress-bar-padding
  {
    display: none !important;
  }

  #ytp-progress-bar-padding
  {
    display: none !important;
  }
  .ytp-progress-bar-padding
  {
    display: none !important;
  }

  .ytp-progress-list
  {
    display: none !important;
  }

  .ytp-scrubber-container
  {
    display: none !important;
  }

  .ytp-progress-bar
  {
    display: none !important;
  }

.ytg-watch-footer-0 .actions.ytg-watch-footer {
display:none !important;
}

.ytg-gaming-video-renderer-1 #bottom-thumbnail-overlay-badges.ytg-gaming-video-renderer > .ytg-gaming-video-renderer {
display:none !important;
}

.ytg-gaming-video-renderer-0 #bottom-thumbnail-overlay-badges.ytg-gaming-video-renderer > .ytg-gaming-video-renderer {
display:none !important;
}

.ytg-inline-playback-renderer-0 #bottom-thumbnail-overlay-badges.ytg-inline-playback-renderer > .ytg-inline-playback-renderer {
display:none !important;
}


.ytg-gaming-video-renderer-2 #bottom-thumbnail-overlay-badges.ytg-gaming-video-renderer > .ytg-gaming-video-renderer {
display:none !important;
}

#like-bar.ytd-sentiment-bar-renderer {
