css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.resilio.com") {

#ipsLayout_contentWrapper, body, .ipsMenu_item a:hover,
.ipsButton, .ipsButton_light, .ipsButton_verySmall,
.ipsSideMenu_list li a:hover
{
background-color:#111111 !important;
}
.cForumRow, .ipsWidget, .ipsDataItem,
.ipsComment_author, .ipsComment_content,
.ipsColumn, .ipsAreaBackground, .ipsQuote, 
.ipsMenu, .ipsPad
{
background-color: #222222 !important;
}
.ipsNavBar_active a, .ipsWidget_title,
.ipsNavBar_secondary, .ipsButtonBar,
.ipsAreaBackground_light, .ipsTabs_activeItem,
.ipsTabs_panel, .ipsQuote_citation, .ipsMenu_title
{
background-color: #333333 !important;
}
input, #elInput_topic_title,
.cke_wysiwyg_div, #elInput_topic_tags_wrapper
{
background-color: #000000 !important;
}
.ipsDataItem 
{
border-bottom:3px #111111 solid !important;
}
.ipsComment, .ipsComment.ipsBox
{
border:3px #111111 solid !important;
}
#elInput_topic_tags_wrapper, #elInput_topic_title
{
border:1px #333333 solid !important;
}
.ipsQuote
{
border:6px #333333 solid !important;
}
a, .ipsWidget_title, .ipsType_pageTitle, 
label, strong
{
color: #FFFFFF !important;
}
p, a:hover, .ipsType_richText, .ipsFieldRow_desc,
.ipsFieldRow_required, input, #elInput_topic_title,
.ipsQuote_contents div, .ipsQuote_citation,
.ipsResponsive_hidePhone, .ipsMenu_title,
.ipsDataItem_main
{
color: #CCCCCC !important;
}
}
