css:
@-moz-document domain("osu.ppy.sh") {
  
  
  /* Filter & bg shit */
  
  .osu-layout--body::before{
  filter: brightness(20%) !important;
  }
  .osu-layout--body-333::before{
  background: url("https://osu.ppy.sh/images/backgrounds/page-light.png") !important;
  }
  /*.logo__main,#download-button img{
  filter: hue-rotate(240deg) !important;
  }*/
  
  
  /* Colors only */
  
  .beatmap-score-top__achieved,.livestream-item__text, .livestream-item__text--name,.forum-post__content--edit-body,.forum-post__info-row,pre,.forum-posts-load-link i{
  color: #EEE !important;
  }
  .page-mode-link--is-disabled{
  color: #CCC !important;
  }
  .beatmapset-row__info,.page-extra__title,.detail-row__text-score--pp,.changelog__text--date,.changelog-stream,h1:not(.osu-page-header__title),h2:not(.osu-page-header__title),h3,h4,.wiki-content__header,.beatmapsets-search-filter__item:not(.beatmapsets-search-filter__item--active),
  .beatmap-score-top__stat-value,.beatmapset-score__stat{
  color: #2ea0d9 !important;
  }
  .t-forum-category-beatmaps .u-forum--link{
  color: #86e !important;
  }
  .t-forum-category-other .u-forum--link{
  color: #88b300 !important;
  }
  .page-tabs__tab:hover,.fa-search::before,.store-header__float-link:hover,.changelog__text--date:hover,.changelog-stream:hover{
  color: #6cf !important;
  }
  .changelog-change__right--major{
  color: lightpink !important;
  }
  
  
  /* Background only */
  
  .beatmapset-panel__panel,.profile-extra-user-page-editor,.beatmapset-info__box--success-rate,.beatmap-score-top--with-outline > :nth-child(2),.forum-post__content--edit-body{
  background: #222 !important;
  }
  .forum-post__content--signature{
  background: #252525 !important;
  }
  .forum-post__info-panel-extra{
  background: #303033 !important;
  }
  .beatmapset-scoreboard{
  background-color: #333 !important;
  }
  .detail-row::before,.beatmapset-score:hover,.beatmap-score-top:hover,pre{
  background: #555 !important;
  }
  .osu-layout__section--extra{
  background: rgba(0,0,0,.5) !important;
  }
  
  
  /* Mixes and other shit */
  
  .page-extra,.post-editor__actions,.post-editor__footer,.post-editor__footer--profile-page,.osu-layout__row--page-compact,.changelog-stream,#download-steps,.osu-layout__row--page,.beatmapsets,.beatmapsets-sorting > *,.beatmapset-scoreboard > *:not(a),.osu-page,.osu-layout__sub-row--lg1,
  .store-header__float-link,.forum-post__content--edit-bar,.forum-post__body,.forums,.left,.right{
  background: #333 !important;
  color: #EEE !important;
  }
  .fancy-search__input:focus{
  background: #444 !important;
  color: #6cf !important;
  border-color: #6cf !important;
  box-shadow: 0 0 10px #2ea0d9 !important;
  }
  .fancy-search__input{
  box-shadow: none !important;
  background: #444 !important;
  color: #6cf !important;
  border-color: #6cf !important;
  }
  .forum-topic-entry:hover,.forum-topic-entry__bg{
  background:#555 !important;
  opacity: none !important;
  }
  .detail-row{
  border-bottom: 1px solid #777 !important;
  }
  .changelog-change + .changelog-change{
  border-top: 1px solid #777 !important;
  }
  .page-tabs__tab--active,.bbcode-spoilerbox__link{
  border-bottom-color: #2ea0d9 !important;
  color: #2ea0d9 !important;
  }
  .page-tabs__tab--active:hover,.bbcode-spoilerbox__link:hover{
  border-bottom-color: #6cf !important;
  color: #6cf !important;
  }
  /**[class="bg__gradient-overlay u-section--gradient-down"]{
  background-image: linear-gradient(rgba(187,17,119,.9),#b17 75%,#b17) !important;
  filter: hue-rotate(240deg) !important;
  }*/
}
