css:
@-moz-document regexp("http://127.0.0.1:8200/ngax/.*"), regexp("http://localhost:8200/ngax/.*") {

body {
    background-color: #1a1a1a !important;
}

.footer {
    background-color: #333333 !important;
}

.header {
    background-color: #333333 !important;
}

.state {
    background-color: #1a1a1a !important;
}

form.styled .buttons input, form.styled .buttons a {
    background: #4a5879;  
}

form.styled .buttons input:hover, form.styled .buttons a:hover {
    background: #6089b5;
}
.button {
    background: #4a5879;
}
.button:hover {
    background: #6089b5;
}
body .container .body .mainmenu>ul>li>a.active {
    color: black;
}

body .container .body .content div.add .steps .step, body .container .body .content div.restore .steps .step {
    color: #2780b3;
}

.step3 source-folder-picker, #folder_path_picker, #restore_file_picker {
    background-color: #ffffff;
}
form.styled input, form.styled textarea, form.styled select {
    color: #000000;
}
}
