css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("biblioteca.unemat.br") {
  body {
    background-image: none !important;
    background-color: #f5f5f5 !important;
    font-family: 'Raleway';
    font-size: 14px;
  }

  div.mFormBody {
    border: none!important;
    margin: 60px auto !important;
    border-radius: 5px !important;
  }

  div#upperUserLogin {
    margin: -95px 0 10px 400px !important;
    position: absolute !important;
    display: inline-block !important;
    color: green !important;
    width: 600px !important;
  }

  div#searchTop,
  div#secondLineDiv,
  a#about {
    display: none !important;
  }

  #searchListPanel {
    margin-bottom: 10px;
  }

  .mTextFieldSimpleSearch {
    background: none !important;
    width: 140px;
  }

  #btnLoginUpper,
  #btnLoginUpper:hover {
    width: 90px;
    line-height: 14px !important;
    display: inline-block !important;
    vertical-align: top !important;
    font-size: 12px !important;
    border: 1px solid gray !important;
    margin-left: 10px;
    background-image: none !important;
    padding: 0;
  }

  #btnLoginUpper:hover {
    box-shadow: 0px 0px 5px green !important;
    color: white;
    padding: 0;
    background-color: #005A00;
  }

  .mButton:hover {
    box-shadow: 0px 0px 5px green !important;
    color: white;
    background-color: #005A00;
    border: none;
  }

  .newItem {
    background-color: #eee;
    border: 1px solid #ddd;
    height: 30px;
    margin-bottom: 4px;
    padding: 2px 10px 2px 10px;
    color: #333;
    border-radius: 10px;
  }

  #searchFooterContent div {
    text-align: right !important;
    font-size: 13px!important;
    margin-top: 8px!important;
    color: #666!important;
  }

  .search .mTextField {
    background: none !important;
  }

  select {
    width: 250px !important;
  }

  .gUserMenuButton {
    margin-bottom: 10px;
    border: none!important;
    background: none !important;
    border-radius: 50px;
  }

  .gUserMenuButton:hover {
    box-shadow: 0 0 5px #006402 !important;
  }

  .mBox {
    border-radius: 10px;
  }

  .mBoxTitle {
    border-radius: 10px;
    background-color: #006402 !important;
  }
}
