css:
@-moz-document domain("hitomi.la") {
.hitomi-horizontal {
    display: none;
}
.hitomi-vertical {
    display: none;
}

body {
    background-color: #000000;
}

.navbar {
    max-width: 1500px;
    background-color: #000000;
    background-image: none;
    height: 40px;
    margin: auto;
}

input#query-input {
	background-color: black;
	color: white;
}

#search-suggestions li {
	background-color: black;
}

#search-suggestions li a {
	background-color: black;
	color: #999999;
}
.top-content {
    background-color: #000000;
    padding: 10px 10px 0 10px;
}
#lang-list li {
    color: #ffffff;
}
.list-title {
    height: 35px;
    text-transform: capitalize;
    background-color: #000000;
    background-image: none;
    border: 2px solid #313131;
}
.container {
    width: 1337px;
    max-width: 1337px;
    border-left: none;
    border-right: none;
    border-bottom: none;
    -moz-box-shadow: 0 0 10px #DDD;
    -webkit-box-shadow: 0 0 10px #000;
    box-shadow: 0 0 10px #000;
    background-color: #000;
    overflow: hidden;
    margin-top: 0;
    margin-bottom: 0;
    margin-left: auto;
    margin-right: auto;
}
.gallery-content {
    clear: left;
    background-color: #000;
    text-transform: capitalize;
    overflow: hidden;
    padding: 10px 160px 30px 160px;
}
.dj {
    border: 2px solid #313131;
    color: #ffffff;
    background-color: black;
}
.dj .artist-list {
    color: #ffffff;
    background-color: #313131;
}
.dj h1 {
    text-shadow: none;
    background-color: #000000;
}
.page-container {
    background-color: #000000;
}
.page-container li {
    font: bold 23px Arial,Helvetica,sans-serif;
    color: #ffffff;
}
.content {
    background-color: #000000;
    padding-left: 0px;
    padding-right: 0px;
    padding-top: 10px;
    padding-bottom: 10px;
    overflow: hidden;
    text-transform: capitalize;
}
.dj-gallery h1 {
    text-shadow: none;
    background-color: #000000;
}
.dj-gallery h2 {
    color: #ffffff;
    background-color: #313131;
}
.dj-gallery {
    color: #ffffff;
}
.thumbnail-list {
    outline: 2px solid #313131;
    background-color: #000000;
}
.thumbnail-container {
    background-color: #000000;
    max-height: 100%;
    width: 100%;
/*    max-height: 253px;
    width: 181px; */
    display: block;
    margin: 1px 1px 1px 1px;
}
.thumbnail-container img {
    border: 1px solid black;
    max-height: 100%;
    max-width: 100%;
}
select {
    background-color: #000;
    border: 1px solid #fff;
    color: white;
}
.navbar-inverse .navbar-inner {
    background-image: linear-gradient(to bottom,#000,#000);
    background-repeat: repeat-x;
    border-color: #000000;
}
.cover img {
    max-height: 320px;
    max-width: 300px;
    margin-bottom: 0px;
    border: none;
}
.acg {
    border: 3px solid #99cccc;
    color: #ffffff;
    background-color: black;
}
.acg-date {
    white-space: nowrap;
    padding-bottom: 4px;
}
.acg h1 {
    text-shadow: none;
    background-color: #000000;
    color: white;
}
.acg .artist-list {
    color: #ffffff;
    background-color: #313131;
}
.tags li a, .relatedtags li a {
    padding: 4px;
    background-color: #000000;
    border: 1px solid #313131;
}
h3 {
    position: relative;
    float: left;
    top: -15px;
    margin: 0 0 30px 0;
    padding-left: 525px;
    color: #ffffff;
    font: bold 35px Arial,Helvetica,sans-serif;
    text-shadow: none;
    white-space: nowrap;
    overflow-x: hidden;
}
.manga h1 {
    text-shadow: none;
    background-color: #000000;
}
.manga .artist-list {
    color: #ffffff;
    background-color: #313131;
}
.manga {
    border: 3px solid #cc99cc;
    color: #ffffff;
    background-color: black;

}
.dj-date {
    color: #666666; 
    margin: 40px 0 5px 0;
}
.acg-date {
	color: #666666;
    margin: 50px 0 5px 0;
}
.manga-date {
    color: #666666;
	margin: 40px 0 5px 0;
}
.cover-column h1 {
    margin: 0px;
    margin-top: 4px;
    padding: 0px;
    color: #ffffff;
    font: bold 18px Arial,Helvetica,sans-serif;
    text-align: center;
    text-shadow: none;
    background-color: #000000;
    border: 2px solid #313131;
}
.page-content {
    background-color: #000;
    padding: 15px 30px;
}
a {
    text-decoration: none;
    color: #ffffff;
    font: 1em Arial,Helvetica,sans-serif;
}
.page-content a {
    font-weight: bold;
    color: #ffffff;
}
ul.posts li {
    width: 330px;
    color: white;
}
.donate {
    background-color: #000000;
    background-image: none;
    font: bold 16px Arial,Helvetica,sans-serif;
    color: #fff;
}
.dj:hover {border: 2px solid #ffffff;}
.acg:hover {border: 3px solid #287373;}
.manga:hover {border: 3px solid #a900a9;}
.anime:hover {border: 3px solid #8BC34A;}
.cg:hover {border: 3px solid #9999FF;}

.dj-img1{
    border: 1px solid #ffffff;
    background-color: #000000;
    height: 250px;
    max-width: 179px;
    min-width: 179px;
}
.dj-img2{
    border: 1px solid #ffffff;
    background-color: #000000;
    height: 230px;
    max-width: 166px;
    min-width: 166px;
    z-index: 3;
    position: absolute;
    left: 68px;
}
.dj-img-back {
    background-color: #000000;
    border: 1px solid #ffffff;
}
.dj-img1 {
    -moz-transform: rotateY(00deg);
    -ms-transform: rotateY(00deg);
    -o-transform: rotateY(00deg);
    -webkit-transform: rotateY(00deg);
    transform: rotateY(00deg);
    top: -20px;
}
.dj-img2 {
    -moz-transform: rotateY(00deg);
    -ms-transform: rotateY(00deg);
    -o-transform: rotateY(00deg);
    -webkit-transform: rotateY(00deg);
    transform: rotateY(00deg);
    top: -15px;
}
.dj-img-back {
    display:none
    }
.dj-img-cont {
    z-index: 999;
    position: absolute;
    height: 220px;
    width: 320px;
    top: 20px;
    left: -40px;
}
nav > ul > li:hover {
    background-color: #313131;
    color:white
}
#lang-drop {
    background-color: #000000;
}
.tags li a:hover, .relatedtags li a:hover {
    background-color: #313131;
    color: inherit;
}
.gallery-content a:hover {
    color: #a7a7a7; 
}
ul.simplePagerNav li.currentPage a {
    color: #ffffff;
}
ul.simplePagerNav li a {
    color: #fff;
    text-decoration: none;
}
#nozomi-link {
    display: none;
}
