    left: -40px;
}
.video_backdrop {
    margin: 14px;
    padding: 0px;
    outline: 1px solid #999999;
    background-color: #000000;
}
.anime-gallery {
    color: #ffffff;
}
.cg-gallery {
    color: #ffffff;
}
.manga-gallery {
    color: #ffffff;
}
.anime-gallery h1 {
    text-shadow: none;
    background-color: #000000;
}
.anime-gallery h2 {
    color: #ffffff;
    background-color: #313131;
}
.cg-gallery h1 {
    text-shadow: none;
    background-color: #000000;
}
.cg-gallery h2 {
    color: #ffffff;
    background-color: #313131;
}
.manga-gallery h1 {
    text-shadow: none;
    background-color: #000000;
}
.manga-gallery h2 {
    color: #ffffff;
    background-color: #313131;
}

}
