css:
@-moz-document domain(heise.de) {
#mitte_links {
    width: 96% !important;
    margin-bottom: -5px !important;
    min-height: inherit;
}

figure.aufmacherbild, figure.aufmacherbild img, figure.video {
    width: 100%!important;
    float: left;
    margin: 0 10px 10px 0;
}

.bild_rechts {
    margin-right: 20px;
}

#mitte {
    min-height: unset !important;
}

a-collapse, #content_foren, .printversion, .akwa-ad-container, .buehnenteaser, .bcadv_oben, .us_ad, .teaser_adliste, .shariff, .permalink, .themen_foren, #comments_container, #themen_aktuell, #navi_bottom, #teaser_sockel, #bannerzone, #mitte_rechts, .heisetopnavi, .news_navi, .themenseiten, #sitemap, footer, aside, .dossier, .widget-werbung, .adbottom, .ad-container, .bottom_up,.article-footer__content,a[name="meldung.newsticker.bottom.zurstartseite"],.sidebar,.a-pvgs,.dev-nav-socialmedia,#main_navigation,.breadcrumb,.main-header {
  display: none !important;
}

#container_content {
  top: 0 !important ;
  box-shadow: 0 5px 8px rgba(0,0,0,.3);
  margin-bottom: 15em; 
  padding: 0 0.5rem;
}

#mitte {
  overflow: hidden;
}

#container {
  padding-bottom: 10em;
}

body, html {
  background: #fff;
}

header,.akwa-article__content,.tp_content>main {
    width:100%!important;
    max-width:100%!important;
    margin-right:0!important;
}

.tp_content {
    margin-bottom:30%;
}

.header-main {
    padding:0;
}

.article-layout__content-container {
    display:block;
}

.article-layout__content {
    max-width: unset;
}
}
