css:
@-moz-document domain("goodreads.com") {
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

.siteHeader__logo--doodle {
    display: inline-block !important;
    background: url("https://www.goodreads.com/assets/layout/header/nav_g_icon_doodle.png") no-repeat !important;
    background-position: center 4.5px !important;
    background-size: 30px 41px !important;
    width: 30px !important;
}

.socialMedia,
#myQuizStats,
a[href^="/list/liked/"],
a[href^="/poll/user_votes/"],
body > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.rightContainer > div:nth-child(3),
body > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.rightContainer > div:nth-child(4),
body > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.rightContainer > div:nth-child(6),
body > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.rightContainer > div:nth-child(7),
body > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.rightContainer > div:nth-child(8),
body > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.rightContainer > div:nth-child(9),
body > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.rightContainer > div:nth-child(5) > div.h2Container.gradientHeaderContainer {
	display: none !important;
}

body, p, ol, ul, td, th, a, 
.readable,
.bookTitle,
.authorName,
.actionLink,
.elementList,
.quoteText,
.updateAction,
.textBox,
.groupName,
.content620 a, 
.content300 a,
.u-defaultType,
.authorQAPrompt,
.actionLinkLite,
.gr-buttonAsLink,
.gr-book__author,
.comment .reviewText,
.gr-book__description,
.addBookTipDescription,
.bigBox .h2Container h2,
h1, .h2Container H2, .pageHeader h2,
.communityAnswer .communityAnswerBody,
input[type=text], input[type=number], input[type=search], 
input[type=email], input[type=number], input[type=password], textarea,
.uitext, .uitext th, .uitext td, .uitext ul, .uitext ol, .uitext li,
.moreReaderQA a {
	font-family: "Lato", IRANSans, Vazir, "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}

.communityQuestionSubTitle .bookLink,
.content620 .authorName, .content620 .bookTitle, .content620 .by, .serif, a.header14 {
	font-family: "Merriweather", IRANSans, Vazir, Georgia, "Times New Roman", serif !important
}

.comment .reviewText,
.readable, body, p, ol, ul, td, th {
	line-height: 25px !important;
}

.h2Container,
h2.brownBackground {
	background-image: none !important;
}

.gr-book__title {
	font-weight: normal !important;
}

.h2Container {
	height: 30px !important;
    border-bottom: 1px solid #ddd !important;
}

.gradientHeaderContainer {
	padding-bottom: 0 !important;
}

img {
    border-radius: 4px;
}

.nav img[alt="my profile"] {
	width: 30px;
    height: 30px;
    border-radius: 15px;
    margin-top: 3px;
}

.mainContentFloat {
	font-size: 14px !important;
}

.bigBoxBody,
.miniBookContent .authorLinks {
	margin-top: 15px !important;
}

}
