@-moz-document domain("1000novel.com") {
@keyframes rubberBand {
    from {
        -webkit-transform: scale3d(1, 1, 1);
        transform: scale3d(1, 1, 1);
    }
    30% {
        -webkit-transform: scale3d(1.25, 0.75, 1);
        transform: scale3d(1.25, 0.75, 1);
    }
    40% {
        -webkit-transform: scale3d(0.75, 1.25, 1);
        transform: scale3d(0.75, 1.25, 1);
    }
    50% {
        -webkit-transform: scale3d(1.15, 0.85, 1);
        transform: scale3d(1.15, 0.85, 1);
    }
    65% {
        -webkit-transform: scale3d(.95, 1.05, 1);
        transform: scale3d(.95, 1.05, 1);
    }
    75% {
        -webkit-transform: scale3d(1.05, .95, 1);
        transform: scale3d(1.05, .95, 1);
    }
    to {
        -webkit-transform: scale3d(1, 1, 1);
        transform: scale3d(1, 1, 1);
    }
}
* {
    transition: all .3s;
    box-sizing: border-box;
}
body,
h1,
h2,
h2 a,
h2 a:visited,
h3,
h4,
h5,
h6,
p,
select,
textarea,
.menu-primary a {
    font-family: "sans-serif" !important;
}
body {
    background: none;
}
span.fn a,
span.fn a:visited {
    color: #000;
}
a:focus {
    outline: auto !important;
}

#wrap {
    border-top: none;
}
#header {
    background: #2c333e;
}
#title-area {
    overflow: hidden;
    width: 100%;
    padding: 25px 0;
    text-align: center;
}
#title a {
    font-family: 'Courgette' !important;
    text-shadow: none;
    animation-name: rubberBand;
    animation-duration: 1s;
    animation-fill-mode: both;
    animation-iteration-count: infinite;
}
#title:hover {
    animation-name: rubberBand;
    animation-duration: 1s;
}


.menu-primary li,
.menu-secondary li,
#header .menu li {
    display: inline-block;
    width: 14%;
}
@media only screen and (max-width: 600px) {
    .menu-primary li {
        margin: 0;
        width: 100%;
    }
}
.menu-primary .current-menu-item a {
    background: #646c7d;
}
li,
ol,
ul {
    text-align: center;
    list-style: none;
}
ul#menu-main-menu:after {
    content: ".";
    clear: both;
    display: block;
    height: 0;
}
#nav {
    background-color: #2c333e;
    border-top: none;
    overflow: visible;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, .3);
}
.menu-primary li a {
    z-index: 0;
    float: left;
    padding: 1rem;
    width: 100%;
}
.menu-primary li a:hover {
    background-color: rgb(76, 86, 103);
}
.wrap:after {
    content: "";
    display: block;
    clear: both;
}
#inner {
    background: #ECF0F1;
    padding: 1rem;
}
#inner .wrap {
    width: 100%;
    max-width: 50rem;
}
.breadcrumb {
    border-radius: 2px;
    border: none;
    background: #fff;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .2);
    padding: 10px 25px;
}
.breadcrumb a {
    color: #000;
}
#recent-posts-4,
#nav_menu-2,
#nav_menu-3,
#tag_cloud-2,
#content .post {
    padding: 2rem;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .2);
    background: #fff;
}
#content .post {
    margin: 0 0 2rem;
    background: #fff;
}
.widget-area h4 {
    border-bottom: 1px solid #dcdcdc;
    font-size: 2rem;
    padding: 1rem 0;
}
span.categories a,
span.fn a,
p.pages a,
span.tags a,
div.widget-wrap ul li {
    text-align: left;
    text-indent: 2em;
    color: #000;
}
a,
a:hover,
a:visited {
    text-decoration: none;
}
h2 a:hover {
    color: #6a82ff;
}
#content {
    width: 100%;
}
.post-info {
    border: none;
}
.post-meta {
    border-top: none;
}
#content-sidebar-wrap {
    width: auto;
}
div.tagcloud a,
div.widget-wrap ul li a {
    color: #000;
}
#sidebar {
    display: inherit;
    width: 100%;
    margin-top: 10px;
}
.sidebar-content-sidebar #content-sidebar-wrap,
.sidebar-sidebar-content #content-sidebar-wrap,
#content,
#sidebar-alt,
.sidebar {
    float: none;
}


.post-meta a {
    color: #000;
}


p {
    font-size: 18px;
}


div.navigation {
    font-size: 16px;
    overflow: visible;
    padding: 0;
    width: auto;
}
div.navigation ul li a {
    color: #000;
    padding: 10px;
    background: #d3d4d8;
    border-radius: 2px;
    border: none;
}
div.navigation li.active a,
div.navigation li a:hover {
    background-color: #585858;
    border: none;
    color: #fff;
    padding: 10px;
}
@media only screen and (max-width: 600px) {
    div.navigation li,
    div.navigation li a {
        display: block;
    }
    div.navigation li a {
        margin: 1rem;
        padding: 1rem;
    }
}

#footer {
    background-color: #2c333e;
    border-top: 1px solid #313337;
}

#footer p {
    color: #fff;
}

#footer p a {
    color: #fff !important;
}

/*display.none*/
[class="widget-area header-widget-area"],
[id="description"],
div[id^="custom_html"],
#menu-item-602,
#menu-item-2481,
#menu-item-3292,
div[style],
div#atstbx {
    display: none !important;
}
}
