css:
@-moz-document domain("kickstarter.com") {
/* Created by Marc Stocker */
/*=======================================*/
/*             Colors Used               */
/*======================================= 

			== Background Colors ==
Lighter Grey	- Used for Header			#323232
Dark Grey		- Used for Body				#3b2b2b
Darker Grey		- Used for accents, cards	#1e1e1e
Lighter BKGD	- Used for a down Arrow		#777
Input Field 								#ccc
Darker Kickstarter Green					#2d9353

			  == Text Colors ==
Standard White Grey	- Used for most text	#ccc
Standard Whiter Grey- A bit Whiter			#ddd
Darker text			- More Bold				#
Dark Text			- All or Nothing link	#070707
navy-700			- Light Grey			#777
navy-900			- Lighter Grey			#999



*/
}

@-moz-document domain("kickstarter.com") {
:root {
    
  
    
    --bg-color-background	: #2e2e2e;		/* Main Background */
    --bg-color-first		: #3e3e3e;		/* Grey highlight */
    --bg-color-light-grey	: #6b6b6b;		/* Light Grey Login Header*/
    --bg-color-hover		: #3b3b4b;		/* */

    --border-color-top		: #2c2c36;		/* */
    --border-color-right	: #2c2c36;		/* */
    --border-color-bottom	: #2c2c36;		/* */
    --border-color-left		: #2c2c36;		/* */

    --text-color-first		: #ffffff;		/* */
    --text-color-second		: #eeeeee;		/* */
    --text-color-third		: #b2b2b2;		/* */
    --text-color-forth		: #5c5c5c;		/* */
    --text-color-green		: #257727;		/* */
    
    --text-color-links		: #2d9cbd;      /* Standard link color 	- Default(Light Blue/Cyan) */
    --text-color-hover		: #10c4fb;		/* Link hover color		- Default(slightly ligher than link color) */
    --text-color-visited	: #9a6eff; 		/* Visted link color 	- Default(Purple) */
    --image-opacity			: 0.7;  		/* 1 is brightest (completly opaque) .0 is see through */
    --display-none			: none;
}


/* Transparent Background */
button, .bg-white, .hover-bg-white:hover
{
 	background-color: transparent;   
}

/* Main Background?? */
.bg-grey-light
{
 	background-color: #212121 !important;   
}

/* Header Section */
/* Regular Grey background and white text */
div.site-nav__right, div.site-nav__middle, div.site-nav__left,span.site-nav__item-label, svg.svg-icon__search,
div.featured-project__content,
div.promo-container, #js-do-locked-header,
div.js-add-fixed-here.z2.t0.w100p.bg-grey-100,
div.table-cell.col-8.bg-blue-light.p3.p10-sm.mobile-center,
div.h100p.flex.flex-column.justify-center.items-center,
div.activity_card, div.NS-activity--activity,
a#js-user_nav_select, 
.bg-grey-400
{
    background-color: #323232 !important;
    color: #ccc !important;
}

/* 
Dark Grey Background Coloring 
	        and
	     White Text
*/
section.NS_projects__hero_funding.bg-grey-100, div.site-nav-frame.js-site-nav-container,
div.site-nav-container.site-nav-container--main.active-nav--primary, 
span.navy-700.ml2.ml0-md,
div.py3.py10-sm.bg-white,
section#advanced_container,
div#main_content,
section.staff-picks.pt3.pt10-sm.bg-white,
section.featured-project.pt3.pb3.bg-white,
div.py3.py7-sm.bg-violet-light.border-top,
div.NS_projects__project_nav.js-project-nav,
div.community-section__locations.js-community-locations,
div.community-section__new_vs_existing,
div.community-section__founding_backers,
div.NS_projects__community_section,
section.tools.js-tools.clip.hide.block-md.pt12,
div.container-flex.relative.px2.z_overlap_slider.clearfix,
div.featured-project.pt2.pb2.bg-white,
/* Modal */
div.modal_dialog_body, 
div.bg-blue-light
{
    background: #2b2b2b !important;
    background-color: #2b2b2b !important;
    color: #ccc;
}

/* Dark Grey Background only */
div.js-faq-answer, li.js-faq, .bg-grey-100, .text-strikethru .text
{
 	background-color: #2b2b2b !important;   
    box-shadow: none !important;
}
/* Darker Grey Background only */
.tout-banner__message,
.js-mini-footer,
div.in-page-alert,
div.timeline__divider.timeline__divider--successful,
div.timeline__divider_content,
div.featured-project__content,
div.community-block-content.clearfix,
div.founding-backer.community-block-content,
header.bg-color-.bg-grey-light.category-hero.hero-.js-category-hero.with_background,
body.users_edit,
div.js-add-padding-here, div#js-do-content-start,
/* Must be signed in to comment */
div#new_comment,
/* Creator Comments */
li.NS_comments__comment.collaborator.comment.item.mb3.py3,
/* Create Project FAQ */
li.faq>div, li.faq.expanded>div, li.faq>a,
/* Login Page */
div#login, div.rounded-content-frame__footer, div.rounded-content-frame__content, .bg-navy-300,
body#users_comments #activity li
{
    background-color: #1e1e1e !important;
}
div.user-menu-footer {background: #1e1e1e !important; color:#ccc !important;}
/*Profile page, Header Card Background*/
body.profile #running-board-wrap
{
    background-color: #05566b;
}

/* Lighter Background */
div.down-arrow.js-hero-scroll, div.friends-tout__stat
{ background-color: #777 !important; }

/* Other Background Colors */
.bg-green { background-color: #2c8e50 !important }
.table_light { background-color: #777 !important }
th.sortable, th.desc, table.table_light>thead>tr>th { background-color: #666 !important }
div.js-campaign-state.js-campaign-state__canceled div div { background-color: #380400 !important }

/* Featured Project name background */
section.section_potd-block span, select[name="lang"]
{
    background-color: rgba(18, 18, 18, 0.72);
}

/* Category Homepage title background */
select[name="currency"], #index-container section.section_category-page-header.border-bottom.border-dark-grey-500.mb6.mb0-md div.mt8-md.mt6.relative span
{
    background-color: rgba(18, 18, 18, 0.72);
}


/* Faded Modal transparent background */
div.modal_dialog, .bg-grey-100_95, .simple-modal .modal_dialog_outer
{
 	background-color: rgba(7,27,27,0.9);   
}

.simple-modal .modal_dialog_outer
{
 	background-color: rgba(4, 14, 14, .66);   
}

/* Transparent background - no background */
.NS_users__profile #category_circle #half_circle
{
    background: transparent;   
}

#small_circle>canvas
{
    opacity: .6   
}

/* ----------------- Text -------------- */
/* White Text */
div.medium, span.navy-700,
data, div.project-card-content>p,
div.js-faq-answer>div>p,
a.grey-dark,
div.subtitle, div.right>div,
div.statline>div>div,
div.statline>div>div>div>div>div>form>button,
a.comments,
h1.title.white>a,
h1.white,
span.sentence-open.js-sentence-open,
/* Comments */
div.main.clearfix.pl3.ml3>p,
/* Modal */
div.NS_backings__info>div>a, div.text, div.backings-info__creator,
div.backing-complete__checkbox, a.note-add, .link-soft-black, body, .display-none.block-sm, .soft-black
{
    color: #ddd !important;
}
a.navy-600 { color: #b7f7b4 !important; }
span.bold.navy-600, span.bold, div.promo-title.green-dark.mb3 { color: #ccc !important }


/* Darker Text */
a.green-dark.text-underline { color: #070707 !important }
a.navy-700 { color: #777 !important; }
a.navy-900, .rounded-content-frame__footer, body#users_comments #activity li p { color: #999 !important; }
li.cycle-pager-active>a.navy-900 {color: #fff !important }
a.navy-700:hover { color: #999 !important; }
div.description { color: #999 !important }
div.founding-backer.community-block-content>div { color: #999 !important }
li>div, .pledge__reward-description, .NS_users__profile .profile_bio a { color: #bbb !important }

/* Other Text Colors */
/*div.left>div>a { color: #f14c28 !important }*/
div.left>div>a { color: #7f9bff !important }
.bg-grey-light { color: #333 !important }
strong { color: #222 !important }

/* New Kickstarter Blue */
.section_homepage-categories-block  div.grid-row li.mb-1_6 .soft-black
{
 	color: #056d7d !important;
    font-weight: bold;
}

/* Dark Orange */




div.featured-project__blurb.ratio-16-9, div.project-stats-label.js-text,
div.featured-project__blurb.ratio-16-9:after
{
    color: #fff;
    background-color: transparent !important;
    background: transparent !important;
}

div.featured-project__title.ratio-16-9>a, h6.project-title>a
{
    color: #4dc16e !important;
}


/* Hover Colors */
.hover-bg-grey-200:hover { background-color: #363636 !important;    }
div.bg-white, section.bg-white, html { background-color: #212121; }

ul.nav.f5.list>li>a, a.green-dark, a.black, h4.project-title.ellipsis>a,
h2.f3.mb0.normal>a, h3.type-18.medium.lh4.mb2>a, .hover-dark-grey-500:hover
{
    color: #eee;
}

div.promo-container:hover { background-color: rgba(50,50,50,0.3) !important;}



div.NS_watches__watch.js-watch, a.bttn.bttn-medium.bttn-shadow-light.w100p,
div.basis50p.block-sm.bttn.bttn-medium.bttn-shadow-light.grow-1.hide.ml2.w100p
{
    background-color: #010101;
    color: #eee;
    box-shadow: none;
}

div.NS_projects__project_nav.js-project-nav.skrollable.skrollable-between,
div.NS_projects__project_nav.js-project-nav.skrollable.skrollable-after,
a.tabbed-nav__link.type-14
{
    background-color: transparent;
    color: #999;
}

a.is-active.js-load-project-content
{
    background-color: #3b3b3b;
    color: #eee;
}

a.nowrap, a.medium
{
    color: #eee;
}

figure>img, span.ss-icon.ss-delete
{
    background-color: transparent !important;
}


/* Large full width display for single project */



/* Report Button - And other Buttons*/
a.bttn.bttn-medium.bttn-white.bttn-shadow,
a.btn.btn--lighter-gray
{
    background-color: #424242 !important;
    color: #ccc !important;
}

.project-card, .navigation li a:hover
{
    background: #222 !important;
}

/* Like Button */
span.ksr-icon__heart {
    color: red !important;
}
button.btn--like {
    background-color: #111 !important;                                           
    color: red !important;                                           
}
/* See all button */ 
a.bttn,
/* Show More Button */
/* Follow Button */
button.bttn,
/* ALL THE BTTN's !!!! */
div.bttn, .btn--border-gray,
div.load_more>a
{
    color: #ccc !important;
    background-color: #191919 !important;
}
/* Button Hover Attribute */
/* See all button */ 
a.bttn:hover,
/* Show More Button */
/* Follow Button */
button.bttn:hover,
/* ALL THE BTTN's !!!! */
div.bttn:hover, .btn--border-gray:hover,
div.load_more>a:hover
{
    color: #999 !important;
}


/* Remind me Button */
div.NS_watches__watch.js-watch{ background-color: transparent !important; }

/* Projects we like "button" */
/*
div.projects-we-love-badge.projects-we-love-badge--overlay,
div.projects-we-love-badge.projects-we-love-badge--overlay>div.icon,
div.projects-we-love-badge.projects-we-love-badge--overlay>div.icon>span.ksr-icon__heart
{ 
    background-color: #2d9353 !important; 
	color: #e9e9e9 !important;
}
*/
div.js-track-project-card > div > div > div.relative.self-start > a.type-12.black.hover-black.bg-white.hover-border-soft-black.transition-all.inline-block.ml3.absolute.px2.py2_3.b-2.border.border-grey-500.shadow-hover
{ 
    background-color: #2d9353 !important; 
	color: #e9e9e9 !important;
}

/* Pop-ups */
/* Remind me Notification */
div.jGrowl-notification,
div.jGrowl-message
{
    background-color: #1c1c1c !important;
    color: #eee !important;
}

/* Video/GIF Loading Dots */
div.loader-dots
{
 	background: #777 !important;
}





div>input.new-form__input--numbers.form-control.js-no-reward-form-input.js-form-input.pledge__no-reward__input.text
{
    background-color: #555;
}

li.list-disc
{
    background-color: #424242;
    background: 0px 0px;
    position: relative;
}

div#wiki-document-head>h1
{
    color: #57b3db;
}

/* All Links */
a[href^="https://www.kickstarter.com/"] {
    color: #e0e0e0 !important;
}


/*----- links ------*/

html.site_index.fontface.js body#site_index.frame.site.not-mobile.not-phone.not-mobile-safari.not-chrome-ios.not-forces-video-controls.not-tablet.not-ipad.not-iphone.not-ios.not-android.not-touchable.not-native-app-request.not-apple-pay-capable.not-android-pay-capable.responsive.skip_autologin div.user-menu div#js-user_menu_dropdown.NS_layouts__user_dropdown.column-span-2.relative div.user-dropdown.js-user-dropdown.bg-white.absolute.r0.t0.z9000.scroll-y.backer.fixed.selected div#menu-me-children.user-options div#menu-me-account.menu-me-account.dropdown-column ul.js-dropdown_group.dropdown-menu li a, html.site_index.fontface.js body#site_index.frame.site.not-mobile.not-phone.not-mobile-safari.not-chrome-ios.not-forces-video-controls.not-tablet.not-ipad.not-iphone.not-ios.not-android.not-touchable.not-native-app-request.not-apple-pay-capable.not-android-pay-capable.responsive.skip_autologin div.user-menu div#js-user_menu_dropdown.NS_layouts__user_dropdown.column-span-2.relative div.user-dropdown.js-user-dropdown.bg-white.absolute.r0.t0.z9000.scroll-y.backer.fixed.selected div#menu-me-children.user-options div.NS_layouts__user_dropdown_backer_creator_columns div#menu-me-backed.menu-me-backed.dropdown-column ul.js-dropdown_group.dropdown-menu li.project-title a, div p a {
	color: var(--text-color-links) !important; }
html.site_index.fontface.js body#site_index.frame.site.not-mobile.not-phone.not-mobile-safari.not-chrome-ios.not-forces-video-controls.not-tablet.not-ipad.not-iphone.not-ios.not-android.not-touchable.not-native-app-request.not-apple-pay-capable.not-android-pay-capable.responsive.skip_autologin div.user-menu div#js-user_menu_dropdown.NS_layouts__user_dropdown.column-span-2.relative div.user-dropdown.js-user-dropdown.bg-white.absolute.r0.t0.z9000.scroll-y.backer.fixed.selected div#menu-me-children.user-options div#menu-me-account.menu-me-account.dropdown-column ul.js-dropdown_group.dropdown-menu li a:hover, html.site_index.fontface.js body#site_index.frame.site.not-mobile.not-phone.not-mobile-safari.not-chrome-ios.not-forces-video-controls.not-tablet.not-ipad.not-iphone.not-ios.not-android.not-touchable.not-native-app-request.not-apple-pay-capable.not-android-pay-capable.responsive.skip_autologin div.user-menu div#js-user_menu_dropdown.NS_layouts__user_dropdown.column-span-2.relative div.user-dropdown.js-user-dropdown.bg-white.absolute.r0.t0.z9000.scroll-y.backer.fixed.selected div#menu-me-children.user-options div.NS_layouts__user_dropdown_backer_creator_columns div#menu-me-backed.menu-me-backed.dropdown-column ul.js-dropdown_group.dropdown-menu li.project-title a:hover, html.activity_index.no-mega.fontface.js body#activity_index.frame.activity.not-mobile.not-phone.not-mobile-safari.not-chrome-ios.not-forces-video-controls.not-tablet.not-ipad.not-iphone.not-ios.not-android.not-touchable.not-native-app-request.not-apple-pay-capable.not-android-pay-capable.fixed-width.skip_autologin main div#main_content div#content.bg-grey-light section.dashboard_activities div.mobius_page ul.mobius li.page div.activity div.NS-activity--activity div.activity_card div.container-flex div.row div.project_info.col.col-10.col-pre-1.mt3.mb3.js-read_more_context div.bg-white.border-grey-light.rounded.py3.row div.flag-body.pl2.col.col-10 div.row div.col.col-12 h3.mb2 a.link-green-dark:hover,
a:hover, button.soft-black:hover, .display-none.block-sm:hover, .NS_users__profile .profile_bio a:hover, .nav--subnav__item__link.nav--subnav__item__link--gray.js-backed-link.current,.nav--subnav__item__link--gray.current {
 	color: var(--text-color-hover) !important; }
asdfasdf {
	color: var(--text-color-visited) !important }
.btn.btn-primary.has-icon-right.fa-caret-down {
 	color: #000 !important; }

/* ------------------ Borders --------------- */
.border-soft-black
{
 	border-color: #056d7d !important; 
}
/* Border Hover */
.hover-border-soft-black:hover
{
 	border-color: #666 !important;   
}


/* Invert Colors */
section.hero.bg-cover.bg-center.center.pt8.pb8.pt12-md.pb0-md,
img[src="https://static.kickstarter.com/assets/projects/learn/map-bdc6a70ced4a588e2ec21d605b20fc73ac37e37d24f53f677662e956c539e6c6.jpg"],
svg.svg-icon__close
{
    -webkit-filter: invert(1);
   filter: invert(1);
}
img[alt="tci swirl logo"]
{
 	mix-blend-mode: multiply;   
}
/* Create a project header */
h1.normal_weight.type-60-md.mb3.mb7-sm, h4.grey-dark.mb3 { color: #222 !important }



/* Remove Box Shadows */
.shadow-button, .bttn-shadow-dark
{
 	box-shadow: none !important;   
}

span[style*="box-shadow: white 12px 0px 0px, white -12px 0px 0px"], .section_potd-block span
{
    background-color: rgba(18, 18, 18, 0.72);
    box-shadow: rgba(18, 18, 18, 0.72) 12px 0px 0px, rgba(18, 18, 18, 0.72) -12px 0px 0px !important;
}
}

@-moz-document domain("kickstarter.com") {
/* Header Stuff */
/* Icons */ 
svg.mr4
{
    fill: #b4b4b4 !important;
}
svg.svg-icon__search
{
    fill: #eee !important;
    background-color: #323232;
}

/* Search Bar input field */
input.text, input
{
 	background-color: #3b3e3e !important;   
    color: #c4c4c4;
}
input.text, input.input-text, input.password, input[type="text"], textarea, textarea.input-textarea, iframe.editor, select
{
    color: #c4c4c4;
}
/* Search Bar Exit Button */
.fill-soft-black
{
 	fill: #cecece;   
}
.hover-fill-soft-black:hover
{
 	fill: #088599;   
}
/*************/
/* Front page slide show pane 1 */

div[data-theme="light-theme"],
div[data-theme="light-theme"]>div>div>div>h1
{
    -webkit-filter: invert(1);
   filter: invert(1);
}

/* Slide 3 */
div.type-16.type-18-sm.navy-600
{
 	color: #fff !important;   
}
}

@-moz-document domain("kickstarter.com") {
/* Project Cards for displaying all projects */
/* Remove white gradiant on card footer*/
div.project-card-footer
{
    background: linear-gradient(to bottom, rgba(34,34,34,0) 0%, #181818 50%) !important;
}

/* About the Creator */
div.modal_dialog_head>h4,
span.ss-icon.ss-delete
{
 	background-color: #1e1e1e;
    color: #eee;
}
/* Individual Project Page */
/* Pledges     that's it?*/
div.project.projects.not-mobile.not-phone.not-mobile-safari.not-chrome-ios.not-forces-video-controls.not-tablet.not-ipad.not-iphone.not-ios.not-android.not-touchable.not-native-app-request.not-apple-pay-capable.not-android-pay-capable.responsive, div.pledge__info, div.pledge__hover,
li.hover-group.js-reward-available.pledge--available.pledge-selectable-sidebar,
li.pledge--no-reward
{
    background-color: #212121;
}
div.pledge__hover.hover-zoomout { background-color: rgba(43,222,115,0.23) !important; }
h2.pledge__amount { color: #25CB68 !important; }
/* Pledges, All Gone */
li.hover-group.pledge--all-gone.pledge-selectable-sidebar {background-color: #331f1f !important}
li.hover-group.pledge--all-gone.pledge-selectable-sidebar>div.pledge__hover.hover-zoomout {background-color: rgba(51, 31, 31, 0.8) !important}

/* More ghost white gradients */
div.pledge__reward-description-toggle.more:before 
{
 	background: transparent !important;
}
.type-16.clamp-3::after, .job-listing ul li.clamp-3::after
{
    background: linear-gradient(to right, rgba(255,255,255,0), #212121 80%);
}

/*********/
/*  Backed Project Page 
       Backed Pledge    */
.pledge--backed.pledge-selectable-sidebar, .pledge--backed.pledge-selectable-sidebar, .pledge__info
{
 	background: #212121 !important;   
}
.pledge--backed, span.pledge--backed__label, div.modal_dialog_content
{
    background: #2d9353 !important;
 	border: 1px solid #2d9353;
}
/* Unbacked Pledges, on backed project page */
li.pledge-selectable-sidebar
{
 	background: #212121;   
}
/* Prototype Popup */
div.supporting-material-callout__overlay-block
{
 	background-color: #212121;   
}
p.blue-dark
{
 	color: #ddd !important;   
}

/* Hover Color */
a.js-view_your_pledge
{
	background-color: rgba(43,222,115,0.23) !important;
}

/* Converted Currancy Icon */
svg.svg-icon__conversion
{
 	background: #d4d4d4 !important;
    filter: invert(1);
}



span.location-name, div.project-stats-value, .project-stats-label, ul.project-stats>li, ul.project-stats>li>div, ul.project-stats>li>span,
a.inline-block.type-34.mb2.navy-700.hover-green-700.transition-all.js-do-link,
a.inline-block.type-34.mb2.navy-700.hover-green-700.js-do-link,
ul[role="tablist"]>li>a
{
    color: #eee;
}


/************************************/

/* Updates Page */

form.new_comment, form#new_comment
{
 	background: #222;   
}

textarea#comment_body
{
 	background-color: #444;   
}

div.timeline__divider--successful
{
    border-color: #111;
}

/************************************/
}

@-moz-document domain("kickstarter.com") {
/* Common Background Colors, headers, text colors */

.bg-grey-light { background-color: #444;    }

/* Normal Text Styles, common headers */
p.medium, .num-backers, .grey-dark,
h1.normal, h2.normal, h3.normal, h4.normal, h5.normal, 
