css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("nyaa.si") {
	.alert-info {
		display: none;
	}
	.table-bordered {
		border: 0;
	}
	table.table thead th {		
		border: 0;
		border-bottom: 1px solid #000;
	}
	table.torrent-list thead th.sorting::after,
	table.torrent-list thead th.sorting_asc::after,
	table.torrent-list thead th.sorting_desc::after {
		top: 10px;
		right: 6px;
	}
	.table-striped > tbody > tr td,
	.table-striped > tbody > tr:nth-of-type(2n+1) td {
		background-color: #BAE1FF;
		border:0;
		border-bottom:0;
		color: #111111;
	}
	.table-hover > tbody > tr:hover td {
		background-color: #A9D0EE;
	}
	.table-striped > tbody > tr.danger td,
	.table-striped > tbody > tr.danger:nth-of-type(2n+1) td {
		background-color: #FFB3BA;
		color: #111111;
	}
	.table-hover > tbody > tr.danger:hover td {
		background-color: #EEA2A9;
	}
	.table-striped > tbody > tr.success td,
	.table-striped > tbody > tr.success:nth-of-type(2n+1) td {
		background-color: #BAFFC9;
		color: #111111;
	}
	.table-hover > tbody > tr.success:hover td {
		background-color: #A9EEB8;
	}  
	.panel-success > .panel-heading {
		background-color: #BAFFC9;
	}
	.panel-danger > .panel-heading {
		background-color: #FFB3BA;
	}
	#torrent-description p {
		overflow: hidden;
	}
}
