@-moz-document domain("buglog.mysprod.com") {
body {
    background-color: #2e3338;
    color: white;
}
#header-right {
    color: white;
}
.rickshaw_graph {
    background-color: #bac0c6;
}
.rickshaw_graph x_axis_d3 {
    color: white;
    width: 584px;
}
#chartcontainer {
    color: white;
}
#content {
    background-color: black;
}
#header-left h1 span {
    color: white;
}
td,
th {
    background-color: #484b4f;
}
.table-striped tbody tr:nth-child(odd) td,
.table-striped tbody tr:nth-child(odd) th {
    background-color: #ccc;
}
a,
a:link {
    color: #08e;
}
a:hover,
a:active,
a:visited {
    color: #08e;
}
.cell_message_content {
    color: #ccc;
}
td.cell_datetime {
    color: #ccc;
}
td.cell_count {
    color: #ccc;
}
#extensions-side {
    color: #6a707a;
}
em {
    color: #ccc;
}
.well {
    color: #aaa;
}
