css:
@-moz-document domain("osu.gatari.pw") {
body {
    background-image: url(http://i.imgur.com/3bMPEk0.png) !important;
    color: #ffffff !important;
}

body {
font-family: "Exo 2";
font-size: 16px;
font-style: oblique;
line-height: 1.42857143;
color: #ffffff;
background-color: #fff;
}

a {
color: #ffffff !important;
}

.col-lg-12 {
    background: #36393e !important;
}

.alert.alert-warning.margin {
    background: #1e2124 !important;
}

.alert.alert-warning.margin a {
    background: #1e2124 !important;
    color: #3495c7 !important;
}

.navbar-inverse {
    background: url(http://i.imgur.com/hvD86QR.png) !important;
}

.panel-heading {
    background-color: #343434 !important;
}

.alert-warning {
    background-color: #2e3136 !important;
    color: #838790 !important;
}


.list-group-item {
    position: relative;
    display: block;
    padding: 10px 10px;
    margin-bottom: 0px !important;
    background-color: #282b30;
    box-shadow: 0 1px 2px rgba(4, 4, 4, 0.25);
    color: #848891 !important;
    text-shadow: none !important;
}

a, button {
    color: #232323 !important;
    text-shadow: none;
    text-decoration: none !important;
    transition: color 200ms linear, background-color 150ms linear;
    outline: 0 !important;
}

.profileGameModes .active {
    background: #3495c7;
    color: #fff !important;
}

.profileGameModes a {
    background: #1e2124;
}

a, button {
    color: #ffffff !important;
}

h4 {
    color: #838790 !important;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    background-color: #282b30 !important;
    color: #ffffff !important;
}

.panel-heading {
    background-color: #282b30 !important;
}

.table>tbody>tr.warning>td, .table>tbody>tr.warning>th, .table>tbody>tr>td.warning, .table>tbody>tr>th.warning, .table>tfoot>tr.warning>td, .table>tfoot>tr.warning>th, .table>tfoot>tr>td.warning, .table>tfoot>tr>th.warning, .table>thead>tr.warning>td, .table>thead>tr.warning>th, .table>thead>tr>td.warning, .table>thead>tr>th.warning {
background-color: #2e3136 !important;
    color: #838790 !important;
}

td#stats-name {
    color: #838790 !important;
}

.progress-bar {
    background-color: #3495c7 !important;
}

b {
    color: #ffffff !important;
}

h1 {
    color: #ffffff !important;
}

td {
    color: #ffffff !important;
}

.btn {
    color: #ffffff !important;
    background-color: #1e2124 !important;
}


.alert.alert-up {
    background: #36393e !important;
    border: none !important;
    color: #b9babc !important;
    box-shadow: none !important;
}

.avatar {
    width: 136px !important;
    height: 136px !important;
    border-radius: 50% !important;
    background: #2e3136 center no-repeat;
    background-size: cover;
}

.avatar {
    background-image: none !important;
    border: none !important;
}

.avatar img {
    width: 136px !important;
    height: 136px !important;
    border-radius: 50% !important;
}

.nav>li>a>img {
    width: 22px !important;
    height: 22px !important;
    border-radius: 50% !important;
}

.form-control {
    border: none !important;
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    border-radius: 0px !important;
    line-height: 0;
    color: #ffffff;
    background-color: #2e3136;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    box-shadow: 0 1px 3px rgba(0,0,0,.25);
}

.alert.alert-up.badge {
    color: #838790 !important;
    background: #282b30 !important;
    box-shadow: 0 1px 2px rgba(4, 4, 4, 0.25);
}

.dropdown-menu {
    background-color:#2e3136 !important;
}

.dropdown-menu>li>a {
    color: #838790 !important;
}

hi1 {
    color: #838790 !important;
}

p {
    color: #838790 !important;
}

label {
    color: #838790 !important;
}

.beatmap-favs {
    background-color: #282b30 !important;
    color: #838790 !important;
}

.beatmap-info {
    background-color: #282b30 !important;
}

a.beatmapTab {
    background-color: #282b30 !important;
}

a.beatmapTab.selected {
    background-color: #3495c7 !important;
}

.panel-body {
    background-image: none !important;
    background: #2e3136 !important;
}

.card.hovercard {
    background-color: #282b30 !important;
}

.card.hovercard .info .title {
    color: #ffffff !important;
}

.card.hovercard .info .desc {
    color: #ffffff !important;
}

.input-group-addon {
    color: #838790 !important;
    background-color: #282b30 !important;
}

button.btn.btn-success {
    background-color: #3495c7 !important;
}

tr.warning.selected b {
    background-color: #2e3136 !important;
}

.table>tbody>tr.warning.selected>td {
    background-image: url(http://i.imgur.com/NOhj8Ku.png) !important;
}

.iconpicker-popover.popover .popover-title {
    border-bottom: 1px solid #2e3136;
    background-color: #36393e;
}

.iconpicker .iconpicker-items {
    background: #36393e;
}

div.sceditor-toolbar {
    background: #2e3136;
    border-bottom: 1px solid #2e3136;
}

.sceditor-container {
    background: #2e3136;
    border: none !important;
}

b {
    color: #3495c7 !important;
}
}
