@-moz-document url-prefix("https://ps.pcti.tec.nj.us/guardian") {
html,
body {
    background-color: #4f4b4b;
    color: white;
    height: 0%;
    font-family: Arial, sans-serif;
}
#content-main font {
    display: none;
}
