    color: white;
}
#sessiontimeoutwarningBox h2,
button {
    color: black !important;
}
ul.tabs li a {
    background-color: #3d3d3d;
    color: white;
}
ul.tabs li a:hover {
    background-color: #686868;
}
.box-round {
    border: 2px solid #ccc;
    border-radius: 5px;
}
p {
    color: white;
}
p > b {
    color: white;
}
#legend strong {
    color: white;
}
#legend p {
    color: white;
}
.psparent #nav-main a {
    color: white;
}
#nav-main li.selected,
#nav-main-frame li.selected a {
    background-color: #787779;
}
