@namespace url(http://www.w3.org/1999/xhtml);

@-webkit-keyframes fadeIn { 
  0% { opacity: 0.3; }
  20% { opacity: 0.5; }
  40% { opacity: 0.7; }
  60% { opacity: 0.8; }
  80% { opacity: 0.9; }
  100% { opacity: 1; }
}
@keyframes fadeIn {
  0% { opacity: 0.3; }
  20% { opacity: 0.5; }
  40% { opacity: 0.7; }
  60% { opacity: 0.8; }
  80% { opacity: 0.9; }
  100% { opacity: 1; }
}
@-webkit-keyframes fadeOut { 
  100% { opacity: 0.3; }
  80% { opacity: 0.5; }
  60% { opacity: 0.7; }
  40% { opacity: 0.8; }
  20% { opacity: 0.9; }
  0% { opacity: 1; }
}
@keyframes fadeOut {
  100% { opacity: 0.3; }
  80% { opacity: 0.5; }
  60% { opacity: 0.7; }
  40% { opacity: 0.8; }
  20% { opacity: 0.9; }
  0% { opacity: 1; }
}

@-moz-document domain("mastodon.social") {
   @media (min-width: 1000px) {
     
    .drawer { width: 300px; }
    
    .column { width: 50%; flex: auto;}

    .optionally-scrollable{ width: 0px; }
    .getting-started__wrapper{ display: none; }
    div#Getting-started{ display: none; }
     
     
     .status__avatar{ -webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}   
     .status__avatar:hover{ -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}

     
     .drawer:hover { width: 300px }
     
     .compose-form {-webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}
     
     .compose-form:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
     .drawer__header {-webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}
     
     .drawer__header:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
     .search {-webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}
     
     .search:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
     .drawer__pager {-webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}
     
     .drawer__pager:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
     .drawer__inner {-webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}
     
     .drawer__inner:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s;opacity: 1.0;}
     
     .navigation-bar {-webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}
     
     .navigation-bar:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
     .column-header { -webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;} 
     .column-header:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
     .column-back-button { -webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3; }
     .column-back-button:hover { -webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
    .scrollable{-webkit-animation-name: fadeOut;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeOut;
  animation-duration: 0.3s; opacity: 0.3;}
     
    .scrollable:hover{-webkit-animation-name: fadeIn;
  -webkit-animation-duration: 0.3s;
  animation-name: fadeIn;
  animation-duration: 0.3s; opacity: 1.0;}
     
      .column-icon { display: none; }
     .collapsable-collapsed { display: none; }
     
   }
    
