css:
@-moz-document domain("referencesource.microsoft.com") {
frameset#splitter {
    border-color: #2c3622;
    background-color: #1A1118;
}
frameset#splitter frame {
    border-color: #1A2A0A;
    background-color: #1A1118;
}
frameset#splitter frame#s {
    border-color: #1A2A0A;
    background-color: #1e1c1d;
}
body>div.rA {
    background-color: #2a2a34 !important;
}
body>div.rG>div.rF>div.rN {
    background-color: #1A1118 !important;
}
body>div.rG>div.rF>a.rL:hover {
    background-color: #352a41 !important;
}
body,
button,
select {
    color: #dcdcdc;
}
span.activeTab {
    background-color: #A7A !important;
    color: black;
}
body a *:not(img),
body a:link *:not(img) {
    background-color: rgba(60, 60, 60, 0) !important;
    color: #3AE;
}
body a:hover *:not(img),
body a:active *:not(img) {
    background-color: rgba(60, 60, 60, 0) !important;
    color: #4BF;
}
body a:visited *:not(img),
a#logoText {
    background-color: rgba(60, 60, 60, 0) !important;
    color: #9182ff;
}
div.note a.blueLink {
    color: blue;
}
body input,
body textarea {
    color: #f0f0f0 !important;
    background: #222 !important;
    border-color: #333 !important;
}
*::selection {
    background: #B0B0B0;
}
.resultGroupHeader {
    background-color: #232A15;
}
.resultGroupHeader:hover {
    background-color: #2C381C;
}
div.note {
    background-color: #BBAA77;
    color: #444;
}
span.k {
    color: #3A8;
}
p a,
p a:link,
ul a,
ul a:link,
ol a,
ol a:link,
.blueLink,
.blueLink:link {
    color: #3AE;
}
p a:hover,
p a:focus,
p a:active,
ul a:hover,
ul a:focus,
ul a:active,
ol a:hover,
ol a:focus,
ol a:active,
.blueLink:hover,
.blueLink:focus,
.blueLink:active {
    color: #4BF;
}
table thead tr td {
    background-color: #45444A !important;
}
pre#code {
    color: #dcdcdc;
}
pre#code span.c {
    color: #5d8b4e;
}
pre#code b {
    color: #569cd6;
}
pre#code .r {
    color: #b8d7a3;
    border-color: #252525;
}
pre#code a {
    color: #d0dadd;
}
pre#code a:focus {
    background-color: darkgreen;
}
pre#code a:hover {
    color: #bccddf;
}
pre#code a.t,
pre#code a.t:hover {
    color: #4ec9b0;
}
pre#code .s {
    color: #d69d85;
}
div.dH {
    background-color: #999592 !important;
    color: #0e0e0e;
    opacity: 0.84;
}
div.dH a.blueLink {
    color: blue;
}
.documentOutlineButton,
.projectExplorerButton,
.namespaceExplorerButton {
    background-color: #ebeceb;
    opacity: 0.87;
}
.documentOutlineButton:hover,
.projectExplorerButton:hover,
.namespaceExplorerButton:hover {
    opacity: 0.9;
}
.namespaceExplorerButton:hover {
    border-color: #bcf;
    background-color: #ecf1ff;
}
::-webkit-scrollbar {
    width: 11px;
    height: 11px;
}
::-webkit-scrollbar-track {
    background-color: transparent;
    border-radius: 5px;
    -webkit-box-shadow: inset 0 0 3px rgba(100, 150, 100, 0.33);
}
::-webkit-scrollbar-corner {
    background-color: rgba(100, 150, 100, 0.1);
    border-radius: 5px;
    -webkit-box-shadow: inset 0 0 3px rgba(100, 150, 100, 0.33);
}
::-webkit-scrollbar-thumb {
    background-color: #3A3A3A;
    border-radius: 5px;
}
::-webkit-scrollbar-thumb:hover {
    background: #3e403e;
    -webkit-box-shadow: inset 0 0 3px rgba(50, 100, 50, 0.25);
}
::-webkit-scrollbar-thumb:active {
    background: #3a3a3b;
}
}
