css:
@-moz-document domain("trackmytime.com") {
body {
  overflow-X: hidden;
}

.navbar-default .main {
  background: #58626f none;
}
.userinfo {
  background-color: #454c54 !important;
}
#headerNav a {
  background: #393f47 none;
}

#headerNav img,
.pStatus .btnrow:last-child,
.punchForm select,
.punchForm span:first-of-type,
.punchForm br {
	display: none;
}

.account {
  background: white none !important;
  border-radius: 0 0 15px 15px;
  top: -7px;
}
#messageFlag {
  position: relative;
  top: 0 !important;
}

html,
body,
#rightWrapper,
.quad {
  background-color: #cdd2d8;
}

.quad {
  border-color: #a0a8b2;
}

.tblborder.abs {
  max-height: 200px;
}
.tblHeader {
  background-color: #58626f !important;
}

.btnrow {
  margin-top: 0;
}
.btnrow a.btn,
.pStatus a.btn {
  background-color: #58626f !important;
  background-image: none;
  padding: .5em 1.5em;
  user-select: none;
}
.pStatus a.btn:not(.disabled):hover,
.pStatus a.btn:not(.disabled):focus,
.pStatus a.btn:not(.disabled):active
.btnrow a.btn:not(.disabled):hover,
.btnrow a.btn:not(.disabled):focus,
.btnrow a.btn:not(.disabled):active {
  background-color: #373e47 !important;
}

.quad.btmMargin {
  margin-bottom: 20px !important;
}

.linkbutton,
[title="Download"] {
  color: #d67a11 !important;
}

.footer {
  padding-bottom: 1em;
}
}

@-moz-document url-prefix("https://v2.trackmytime.com/Timecards/"), url-prefix("http://v2.trackmytime.com/home/Home_emp.aspx") {
#timecards {
  background-color: white !important;
  border-radius: 5px 5px 0 0;
  color: #393F47 !important;
  font-weight: bold;
  text-decoration: underline;
  text-shadow: none;
}

.tableHeader {
  background: #58626f none;
}
.tableCellTodayrepeat {
  background-image: linear-gradient(to bottom, white, #eaf1f9, #dce3ea);
}
.tableCellToday {
  background-image: linear-gradient(to bottom,  #dce3ea, #eaf1f9, white);
}

.tableAltrepeat {
  background: #eee none;
}
}

@-moz-document url-prefix("https://v2.trackmytime.com/home/Home_emp.aspx"), url-prefix("http://v2.trackmytime.com/home/Home_emp.aspx") {
#punch {
  background-color: #CDD2D8 !important;
  border-radius: 5px 5px 0 0;
  color: #393F47 !important;
  font-weight: bold;
  text-decoration: underline;
  text-shadow: none;
}
}

@-moz-document url("https://v2.trackmytime.com/home/Employee_services.aspx"), url("http://v2.trackmytime.com/home/Employee_services.aspx") {
#employeeServices {
  background-color: #CDD2D8 !important;
  border-radius: 5px 5px 0 0;
  color: #393F47 !important;
  font-weight: bold;
  text-decoration: underline;
  text-shadow: none;
}

.quad {
  border-bottom: 0;
}
.quad.thrdq,
.quad.lastq {
  display: none;
}
}

@-moz-document url("https://v2.trackmytime.com/goodlife"), url("http://v2.trackmytime.com/goodlife") {
.login-box {
  background-color: #58626f;
  border: 0;
}

.login-bottom-link a {
  color: white;
  text-decoration: none;
}
.login-bottom-link a::before {
  font-size: 16px;
  font-weight: bold;
  text-decoration: underline;
}
.login-bottom-link a:first-of-type::before {
  content: "Sign up";
  margin-right: 2em;
}
.login-bottom-link a:last-of-type::before {
  content: "Find my login page";
}

.login-bottom-link img {
  display: none;
}
}
